css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rockhell.ru") 
{   
  /*фон*/
  div#ipbwrapper
  {
    background: none;
    background-image: url("http://s019.radikal.ru/i605/1411/bb/6ca2f9f27616.png");
  }
  
  /*лого*/
  img[src*="http://www.rockhell.ru/public/style_images/1_main.gif"]
  {
    background-image: url("http://s017.radikal.ru/i426/1411/06/6e90d6b1f8b3.gif");
    width: 450px;    
    display: inline-block;
    padding: 90px 0 0 0;
    height: 0px;
  }
  
  /*Убрать гомоградиент*/
  div#header_bar
  {
    background-image: none !important;
    background-color: black;
  }
  
  /*фон под лого*/
  div#branding
  {
    /*background: none !important;*/
    background-color: black;
  }
  
  /*фон поиска*/
  #search_wrap 
  {
    background: none repeat scroll 0% 0% #E6E6E6;
  }
  
  /*обводка кнопки поиска*/
  #search .submit_input 
  {
    border: 1px solid #E6E6E6;
  }
  
  /*Спрятать то что должно быть хотлистом*/
  div#index_stats
  {
    display: none;
  }
  
  /*убрать блоки пользователей*/
  #community_app_menu > li[id = nav_app_customSidebarBlocks]
  {
    display: none;
  }
  
  /*Растянуть список форумов*/
  .ipsLayout_largeright.ipsLayout_withright 
  {
    padding-right: 0px;
  }
  
  /*выравнивание подфорумов*/
  .ipsBox
  {
    padding: 0px;
  }
  
  /*центральная часть контента*/
  div#content
  {
    background-color: transparent;
    box-shadow: none;
  }
  
  /*вфделенный таб*/
  #community_app_menu > li.active > a 
  {
    background: none;
    background-image: url("http://s019.radikal.ru/i605/1411/bb/6ca2f9f27616.png");
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
  }
  
  #community_app_menu > li > a:hover
  {
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
  }
  
  /*#nav_app_members>a
  {
    font-size: 0 ! important;
    color: transparent ! important;
  }
  
  #nav_app_members>a:after
  {
    content: "Говнари" ! important;
    font: 12px helvetica,arial,sans-serif ! important;
    color: #ccc ! important;
  }*/
  
  #forum_table
  {
    background: none repeat scroll 0% 0% #e6e6e6;
  }
  
  /*бг к таблицам форумов*/
  .ipsBox_container 
  {
    border: 0px solid #000;
    background: none repeat scroll 0% 0% #e6e6e6;
  }
  
  /*боковое меню в недавний сообщениях*/
  .ipsLayout_left .ipsBox_container 
  {
    border: 1px solid #000000;
    border-radius: 4px;
    background: none repeat scroll 0% 0% #C9C9C9;
  }
  
  .ipsLayout_left .ipsBox
  {
    border-radius: 4px;
    margin-top: 32px;
    background-color: #888;
  }
  
  .ipsSideMenu ul li 
  {
    border-bottom: 1px solid #000000;
    background-color: #C9C9C9;
    color: #adadad;
  }
  
  .ipsSideMenu ul li.active a 
  {
    background: url("http://www.rockhell.ru/public/style_images/master/icon_check_white.png") no-repeat scroll 6px 8px #999999;
    /*background: url("http://www.rockhell.ru/public/style_images/master/icon_check_white.png") no-repeat scroll 6px 8px, repeating-linear-gradient(45deg, #000000, #000000 10px, #090909 10px, #090909 20px);*/
    color: #000000;
    font-weight: bold;
  }
  
  .ipsSideMenu h4
  {
    color: #adadad;
  }
  
  div.ipsBox_container.ipsSideMenu
  {
    /*background: repeating-linear-gradient(45deg, #222222, #222222 10px, #333333 10px, #333333 20px);*/
    background: repeating-linear-gradient(45deg, #000000, #000000 10px, #090909 10px, #090909 20px);
    color: #adadad;
  }
  
  .ipsSideMenu ul
  {
    border-top: 1px solid #000000;
  }
  /*--------------------------------------------*/
  
  /*иконка в списке форумов*/
  .ipsUserPhoto 
  {
    padding: 1px;
    border: 1px solid #404040;
    background: none repeat scroll 0% 0% #aaaaaa;
  }
  
  /*непрочитанный топик или форум*/
  .unread 
  {
    background-color: #C9C9C9;
  }
  
  /*непрочитанный топик или форум иконка*/
  .unread .altrow, .unread.altrow 
  {
    background-color: #C9C9C9;
  }
  
  /*отрисовка нижней границы в ячейках таблиц*/
  table.ipb_table td 
  {
    border-bottom: 1px solid #000;
  }
  
  /*отрисовка боковых линий таблиц*/
  table.ipb_table
  {
    border-left: 1px solid #000;
    border-right: 1px solid #000;
  }
 
  /*текущий топик*/
  .ipsType_pagetitle, .ipsType_subtitle 
  {
    color: #101010;
  }
  
  /*пагинатор активные*/
  .pagination .pages li.active 
  {
    background: none repeat scroll 0% 0% #ADADAD;
    color: #000;
    border: 1px solid #000000;
    border-radius: 4px;
    padding: 0px 5px;
  } 

  /*пагинатор выделенные*/
  .pagination .pages a:hover 
  {
    background: none repeat scroll 0% 0% #c0c0c0;
    border-radius: 4px;
    padding: 0px 4px;
  }
  
  /*вперед назад*/
  .pagination .back a, .pagination .forward a 
  {
    background: none repeat scroll 0% 0% #ADADAD;
    border: 1px solid #000000;
    border-radius: 4px;
    color: #000;
  }
  
  /*вперед назад выделенные*/
  .pagination .back a:hover, .pagination .forward a:hover 
  {
    background: none repeat scroll 0% 0% #c0c0c0;
    border: 1px solid #000000;
    color: #000;
  }
  
  ul.mini_pagination li a 
  {
    background: none repeat scroll 0% 0% #ADADAD;
    border: 1px solid #606060;
    color: #333;
  }
  
  .maintitle 
  {
    background: repeating-linear-gradient(45deg, #000000, #000000 10px, #090909 10px, #090909 20px);
    color: #ADADAD;
  }
  
  /*количество сообщений*/
  .maintitle.clear.clearfix:not(.ipsFilterbar)
  {
    border-radius: 4px;
    margin-bottom: 10px;
    background: repeating-linear-gradient(45deg, #000000, #000000 10px, #090909 10px, #090909 20px);
    color: #ADADAD;
  }
  
  /*сам пост*/
  .post_block 
  {
    background: none repeat scroll 0% 0% #ADADAD;
    border: 1px solid #000000;
    /*border-top: 0px solid #000000;
    padding-top: 1px;*/
  }
  
  .post_body .post 
  {
    color: #000000;
  }
  
  /*шапка поста*/
  .post_block:first-child h3
  {
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    /*background: none repeat scroll 0% 0% #000000;*/
    background: repeating-linear-gradient(45deg, #000000, #000000 10px, #090909 10px, #090909 20px);
    border-width: 1px;
    border-style: solid;
    border-color: #000000;
    margin-top: -5px !important;
    margin-left: -1px !important;
    margin-right: -1px !important;
  }
  
  .post_block h3 
  {
    /*background: none repeat scroll 0% 0% #000000;*/
    background: repeating-linear-gradient(45deg, #000000, #000000 10px, #090909 10px, #090909 20px);
    border-width: 1px;
    border-top-width: 0px;
    border-style: solid;
    border-color: #000000;
  }
  
  /*имя запостившего*/
  .post_block h3 a
  {
    color: #ADADAD;
  }
  
  .desc.blend_links a
  {
    color: #ADADAD !important;
  }
  
  #forum_table .desc.blend_links a
  {
    color: #000000 !important;
  }
  
  /*заголовок цитаты*/
  p.citation 
  {
    border-radius: 0px;
    margin-left: 20px;
    margin-right: 20px;
    padding-top: 3px;
    padding-bottom: 0px;
    border: 1px solid #000000;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    background: none;
    /*background: repeating-linear-gradient(45deg, #ADADAD, #ADADAD 10px, #B5B5B5 10px, #B5B5B5 20px);*/
    background: repeating-linear-gradient(45deg, #222222, #222222 10px, #333333 10px, #333333 20px);
    /*background-color: #585858;*/
    color: #ADADAD;
    /*color: #000000;*/
  }
  
  /*тело цитаты*/
  blockquote.ipsBlockquote 
  {    
    border-width: 1px;
    border-style: solid;
    border-color: #000000;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    margin-left: 20px;
    margin-right: 20px;
    margin-bottom: 20px;
    background: none repeat scroll 0% 0% #c0c0c0;
  }
  
  div#fast_reply_wrapper .ipsBox_container
  {
    border: 1px solid #000000;
    background-color: #c0c0c0;
  }
  
  /*убрать иконку юзера из формы быстрого ответа*/
  div#fast_reply_wrapper .ipsUserPhotoLink
  {
    display: none;
  }
  
  /*растянуть поле ответа*/
  div#fast_reply_wrapper .ipsBox_withphoto
  {
    margin-left: 0px;
  }
  
  /*шапка редактора  в форме быстрого ответа*/
  div#fast_reply_wrapper .cke_top 
  {
    background: none !important;
    background-color: #c0c0c0 !important;
  }
  
  /*тело редактора*/
  div#fast_reply_wrapper .cke_skin_ips .cke_contents iframe 
  {
    background-color: #ffffff;
    border-width: 1px;
    border-style: solid;
    border-color: #000000;
  }  
  
  div#fast_reply_wrapper .cke_skin_ips .cke_wrapper 
  {
    border: 0px solid #000000 !important;
    background-color: #c0c0c0 !important;
    background-image: none !important;
  }
  
  /*содержимое в редакторе*/
  .CSS1Compat body 
  {
    color: #222;
    background-color: #E6E6E6;
  }
  
  /*цитата в редакторе*/
  cite.ipb 
  {
    margin-top: -20px;
    margin-left: -11px;
    margin-right: 10px;
    margin-bottom: 10px;
    padding-top: 2px;
    padding-left: 10px;
    padding-right: 10px;
    padding-bottom: 2px;
    border: 1px solid #000000;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    background: repeating-linear-gradient(45deg, #222222, #222222 10px, #333333 10px, #333333 20px);
    /*background: none;
    background-color: #585858 !important;*/
    color: #ADADAD;
  }
  
  .cke_show_borders .ipsBlockquote 
  {
    padding-top: 0px;
    padding-top: 5px;
    margin-left: 20px;
    margin-right: 20px;
    border-width: 1px;
    border-style: solid;
    border-color: #000000;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    background: none repeat scroll 0% 0% #d0d0d0;
  }
  
  /*Спрятать кнопку жалобы*/
  .report
  {
    display: none;
  }
  
  /*.topic_buttons li a
  {
    color: #adadad;
  }
  
  .topic_buttons li a:hover
  {
    color: #ffffff;
  }*/
  
  /*убрать количество подписавшихся*/
  span.ipsButton_extra.right
  {
    display: none;
  }
  
  .topic_buttons li a, .topic_buttons li, .ipsButton_secondary, ul.post_controls li.post_edit a, ul.post_controls li.post_del a, .input_submit, .input_submit.alt
  {
    background: none !important;
    background-color: #202020 !important;    
    border-style: solid !important;
    border-color: #212121 !important;
    border-image: none !important;
    border-width: 1px 1px 0px !important;
    border-radius: 3px !important;    
    box-shadow: 0px 1px 0px 0px #5C5C5C inset, 0px 2px 3px rgba(0, 0, 0, 0.2) !important;
    color: #ffffff !important;
    text-shadow: 0px -1px 0px #191919 !important;    
    font-weight: normal !important;
    font-family: Helvetica,Arial,sans-serif !important;
    font-style: normal !important;
    font-variant: normal !important;
    font-stretch: normal !important;
    height: 100% !important;
    min-height: 25px !important;
    /*padding-top: 5px !important;*/
    text-align: center !important;
    opacity: 0.6 !important;
    min-width: 100px !important;
    display: inline-block !important;
    cursor: pointer;
    line-height: 22px !important;
    transition: all 0.2s ease-in-out 0s !important;
  }
  
  .topic_buttons li a
  {
    min-width: 102px !important;
  }
  
  ul.post_controls li.post_del a
  {
    color: #EE1111 !important;
  }
  
  ul.post_controls li.post_edit a
  {
    color: #44AE44 !important;
  }
  
  .topic_buttons li a:hover, .ipsButton_secondary:hover, ul.post_controls li.post_edit a:hover, ul.post_controls li.post_del a:hover, .input_submit:hover
  {
    opacity: 1 !important;
  } 
  
  /*Подпсь*/
  .signature 
  {
    color: #505050;
    border-top: 1px solid #000000;
  } 
  
  /*всплывающий юзерпрофиль*/
  .popupInner h3 
  {
    background: none;
    background-color: #000000;
    border-style: solid;
    border-color: #000000;
    box-shadow: none;
  }
  
  .message 
  {
    background: none;
    background-color: #C9C9C9;
    padding: 10px;
    border: 1px solid #606060;
    color: #000000;
    line-height: 1.6;
    font-size: 12px;
  }
  
  .userpopup 
  {
    background-color: #E6E6E6;
  }
  
  .userpopup .side 
  {
    margin-top: 0px !important;
  }
  
  .userpopup .user_controls 
  {
    text-align: right;
  }
  
  .ipsBadge_green 
  {
    background: none;
    background-color: #ADADAD;
    border: 1px solid #606060;
    color: #323232;
  }
  
  .desc.lighter, .desc.lighter.blend_links a, li.group_title, li.group_title > span
  {
    color: #101010 !important;
  }
  
  .breadcrumb, .breadcrumb a, .ipsType_pagedesc, .pagination .pages a, .pagejump, .desc, .desc.blend_links a, p.posted_info 
  {
    color: #000000;
  }
  
  #footer_utilities .ipsList_inline > li > a 
  {
    margin-right: 0px;
    padding: 0px;
  }
  
  #board_stats .value 
  {
    background: none repeat scroll 0% 0% #C9C9C9;
    color: #4A4A4A;
    padding: 2px 6px;
    padding-top: 4px;    
    border: 1px solid #606060;
  }
  
  .statistics 
  {
    border-top: 1px solid #000000;
  }
  
  a 
  {
    color: #000;
    text-decoration: none;
  }
  
  span.name > a > span > span
  {
    color: #000000 !important;
  }
  
  #copyright
  {
    display: none;
  }
  
  a#toggle_sidebar
  {
    display: none;
  }
}
