css:
@namespace url(http://www.w3.org/1999/xhtml);

/* OK Wave [2011/04] */
@-moz-document domain("okwave.jp") {

  * { font-family: meiryo; font-size: 12px; }
  #ok_wrapper { margin-top: 2em !important; }

   #ok_header
  ,#ok_shoulder_navi
  ,.bread
  ,#ovt_top
  ,#ok_area_r
  ,#gads_top ,#gads_btm
  ,div.ok_area_l-inner+*+div
  ,#ok_footer_search ,#ok_footerArea
  { display: none; }

}

/* Hatena::Question */
@-moz-document domain("q.hatena.ne.jp") {

   .google_afc
  ,.question-information
  ,#sidebar
  { display: none !important; }

}

/* 教えて！goo [2012/07] */
@-moz-document domain("oshiete.goo.ne.jp") {

  * { font-family: meiryo; font-size: 13px !important; }
  #os_inmain { margin: 0 auto !important; }
  #os_incontents #oshieteMain { max-width: 800px !important; }
  .a-article {
    border: none !important;
    box-shadow:  0 1px 3px #ddd;
    padding: 0 0 1em !important;
    margin: 0 0 30px !important;
    }
  .q_article p.q_text,
  .a-article p.a_text {
    padding: 16px 2.5em 10px !important;
    }
  .a-note, .a-thanks { background: none !important; }

  /* 非表示 */
  #globalheaderBox
 ,.new-question
 ,.useful-button
 ,#ipsxSBanner
 ,#header
 ,#header>ul+div+script+script+center
 ,#google_ads1
 ,.ok_vote
 ,.ok_usertool_sbm
 ,#ok-sub, .complain
 ,.pr-unit, .q_footer
 ,#os_rside, .q_only_item, .q_only_close, #no_a_area
 ,#connection_area
 ,#footer {
    display: none !important;
  }
}

/* msn相談箱 */
@-moz-document domain("questionbox.jp.msn.com") {
 #ok_sub {display: none;}
 #ok_main {width: auto !important;}
}
