@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tieba.baidu.com") {
  
  /*===========贴吧首页=============*/
  /* 顶栏推荐 */
  div[class="top-sec clearfix"]{display:none! important;}
  
  /* 右栏，顶部推荐 */
  div[class="r-top-sec"]{display:none! important;}
  /* 右栏，右侧推荐 */
  div[class="r-right-sec"]{display:none! important;}
  
  /* 右栏，左侧推荐 */
  #spage_liveshow_slide{display:none! important;}
  #plat_act_wrapper{display:none! important;}
  
  /* 左栏，我在贴吧 */
  #balv_mod{display:none! important;}
  
  /* 左栏，一键签到 */
  div[class="link onekey_sign_wrap"]{display:none! important;}
  
  /*===========帖子内容=============*/
  /* 贴吧banner */
  div[class="card_banner"]{}
  
  /* 签到 */
  #sign_mod{display:none !important;}
  
  /* 吧特权包 */
  div[class="fMember_cnt"]{display:none !important;}
  
  /* T豆商场神马的 */
  #encourage_entry{display:none !important;}
  
  /* 浮动栏 */
  ul[class="tbui_aside_float_bar"]{display:none !important;}
  
  /* 签名栏 */
  div[class="d_sign_split"]{display:none !important;}
  img[class="j_user_sign"]{display:none !important;}
  
  /* 右上导航栏 */
  li[class="u_wallet"]{display:none !important;}
  li[class="u_tbmall"]{display:none !important;}
  li[class="u_app"]{display:none !important;}
  li[class="u_baiduPrivilege"]{display:none !important;}
  li[class="u_tshow"]{display:none !important;}
  li[class="u_joinvip"]{display:none !important;}
  li[class="u_baiduPrivilege"]{display:none !important;}
  .userbar li[class="split"]{display:none !important;}
  
  /* 随动导航栏 */
  ul[class="core_title_btns"] li[class^="reward"]{display:none !important;}
  
  /* 编辑器 */
  div[class="edui-toolbar"] div[class^="edui-btn edui-btn-medal"]{display:none !important;}
  div[class="poster_head clearfix"]{display:none !important;}
  
  /* 用户资料卡 */
  #user_visit_card{display:none !important;}
  
  /* 分享到 */
  div[class="p_share_ding pb_bright_share"]{display:none !important;}
  div[class="share_btn_wrapper"]{display:none !important;}  
 
  /* 顶部群组活动 */
  #threadListGroupCnt{display:none! important;}
}
