css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.amazon.' ) , url-prefix('https://www.amazon.' ) , url-prefix('http://amazon.' ) , url-prefix('https://amazon.' )

{

/*
Singularity Utopia Style
11 Feb 2016 
*/

.auth-workflow
    
    {
        padding: 20px 25% !important;        
    }
    
.auth-workflow .a-box 
    
    {   border: 1px solid #AAD7E6;
        box-shadow: lightgrey 0px 6px 14px !important;  
    }
    
.huc-v2-page-container
    
    {
        width: 98% !important;
        min-width: 98% !important;
        max-width: 98% !important;
    }

#cbcc_banner_container
    
    {
        display: none;
    }
  
    
#landingImage
    
    {
        margin-right: 70px !important;
    }
    
#actionPanelWrapper.burj
    
    {
        background: none !important;
    }
    
#actionPanel.a-section.a-spacing-none.ap-onetime
    
    {
        border-left: 1px solid lightgrey !important; 
    }
    
#actionPanel.a-section.a-spacing-none.ap-onetime, #actionPanelContainer  

 {
width: 400px !important;
     min-width: 350px !important;
     max-width: 350px !important;
     margin-top: 50px;
     
     }    
   
#amsDetailRight_feature_div, #burjPpdDivider, .burj #burjPpdDividerShadow 
    
    {
        display: none !important;
        visibility: hidden !important;
    }
    
    
.shoveler-heading-title
    
    {
        padding-left: 15px !important;
    }
    
#hud-dashboard .profile-image
    
    {
        display: none !important;
    }
    
.a-section .a-spacing-none
    
    {
        line-height: 19px !important;
    }
    
#hud-dashboard
    
    {
        height: auto !important;
        padding-bottom: 0px !important;
        display: none !important;        
    }

#hud-dashboard .customer-name
    
    {
        font-size: 14pt;
    }

.ap_popover.ap_popover_sprited
    
    {
        width: 90% !important;
    }

#ap_container > iframe
    
    {
        width:98% !important;
        left:0px !important;
    }
    
.ap_popover_sprited

    {
        left: 10% !important;
        right:300px !important;
        float:left !important;
        margin: 0% !important;
    }    

    
 .main-image-widget, #main-image-content, .productImageGrid    
    
    {
        float: left !important;
        max-width: 400px !important;
    } 
     
table.buyBoxFixedLayout, table.buyingDetailsGrid, table.buyBox          

{
    float: right !important;
    width: 215px !important;
    margin-right: 5px;
}
   
.kicsGradientBox

    {
        width: 98%;
    }

#ap_email, #ap_password
    
    {
        padding: 4px 5px 0px 5px !important;   
    }    

.asinItem
    
    {
        overflow: hidden !important;
    }
    
#center #slots DIV#centerSlots.amabot_center
    
    {
        min-width: 78% !important;
        max-width: 78% !important;
    }
    
table, p, .unified_widget.pageBanner
    
    {
        width: 96% !important;
    }
       
.amabot_center
    
    {
        padding: 5px 0px 5px 10px !important;
    }
         
#rhf_table    

    {
        min-width: 97% !important;
        max-width: 97% !important;
    }  
    
html, body
    
    {
        min-width: 99.6% !important;
        max-width: 99.6% !important;
    }  

#kds-popover-mask, #kds-popover
    
    {
        display: none !important;
    }
    
#prime-dp-two-choice-container, #navbar, .a-section, .cs-help-container, .a-container, #dp .a-container, #a-page, .a-row.shiptrackPageMinWidth, .shiptrackPageMinWidth, .gbox-row-1-0
        
    {
        min-width: 100% !important;
        max-width: 100% !important;
    }

#ys 

    {
        min-width: 100% !important;
        max-width: 100% !important;
        padding-left: 0px !important;
        padding-right: 0px !important;
    }
   
}    

/* 

The Singularity is Near

*/
