css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zhihu.com") {
*{font-family:  "Microsoft Yahei" !important;}
body.zhi{background:#222E3E !important;}
.zhi .zu-main{
    background:#FFF !important;
    width:1000px !important;
    padding:25px 20px 50px!important;
    margin-bottom:-1px !important;
    }
    #js-global-notify{
        
    margin-bottom:-2px !important;
    }
.zu-top{
     position: fixed;
     left: 0px;
     top: 0px;
     z-index: 20;
     width: 100%;
     height: 45px;
    background: -moz-linear-gradient(center top , #3F546D 0%, #2D3C4E 100%) !important;
    border-bottom: 1px solid #3F546D;

}
    .zu-top-nav-userinfo {
            background: -moz-linear-gradient(center top , #3F546D 0%, #2D3C4E 100%) !important;
    }
.zu-top-search-input {
    background: none repeat scroll 0% 0% #E1EAF2;
    line-height: 16px;
    border: 1px solid #3F546D;
    border-radius: 0px;
    color: #49525C;
  box-shadow:none !important;
}

.zu-top-add-question,.zu-top-search-button{
    background: -moz-linear-gradient(center top , #617FA2 0%, #546E8D 100%) !important;
    color: #F5F6F7;
    font-size: 15px;
    border: 1px solid #3F546D !important;
    border-radius: 0px !important;;
}
    .zu-top-add-question{
        display: none;
    }
.zu-top-nav-li.current {
    background-color: #2B384E !important;
    background-image: none;
    background-repeat: repeat-x;
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.12) inset;
}
  
.zu-top-nav-userinfo.selected,
html.no-touch .top-nav-profile:hover .zu-top-nav-userinfo,
.zu-top-nav-userinfo:hover {
    background: #2B384E!important;
}
.top-nav-profile li a {
    border-color:#394460 !important;
    background: #2B384E!important;
}
.top-nav-profile li:hover a {
    background: #3F546D!important;
}
#zh-top-search .ac-renderer > div {
    border-radius: 0px !important;;
}
.Avatar{border-radius:60px !important;border:1px solid #197ad3;}
    
}
