css:
@-moz-document url("chrome://global/content/commonDialog.xul"),
regexp("about:(buildconfig|BUILDCONFIG|config|CONFIG|crashes|CRASHES|memory|MEMORY|plugins|PLUGINS|serviceworkers|SERVICEWORKERS|support|SUPPORT).*$") {      
    
/*___________________________________________
 |                                           | 
 |  BACKGROUND IMAGE                         | 
 |  see the end for a backup data image copy |
 |___________________________________________|*/    
    
html,
html > body,
HTML > BODY,
#configTree #configTreeBody,
html > body[onload="populateReportList()"],
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) {   
    background: url("http://abload.de/img/b1fgs11.png") repeat fixed, #212121 !important; } 
  
/*_______________
 |               | 
 |  ABOUT:MEMORY | 
 |_______________|*/
    
.section,     
.ancillary .opsRow  {
    border-radius: 0px !important;
    border: 1px solid rgba(250,250,250,.1) !important;
    background-color: rgba(250,250,250,.1) !important; } 
    
.ancillary button { margin: 0 2px !important; }      
  
 .section :-moz-any(h1,h3) { 
     color: rgb(180,180,180) !important;}  
    
.section .upDownArrow  {
    margin-left: 10px !important; }    
    
.section .hasKids:hover {
    text-decoration: none !important;
    background-color: rgba(150,150,150,.4) !important; }  
    
.section .mrValue {
    color: rgb(200,80,0) !important;}    

.section .hasKids .mrValue {
    color: rgb(240,180,100) !important;} 
    
.ancillary .opsRow .opsRowLabel {
    color: rgb(180,180,180) !important;}
    
body .hasKids .mrSep {
    color: rgb(250,200,0) !important; }  
    
body .hasKids .mrSep:hover {
    color: rgb(0,250,0) !important; } 
    
body .mrNote {
    color: rgb(220,100,0) !important;}    
    
.section h2 {
    background-color: transparent !important;
    color: rgb(180,180,180) !important; } 
    
.ancillary:last-child .legend,    
body :-moz-any(.mrValue, .mrPerc, .mrSep, .mrName) {    
    font: normal 13px verdana !important;} 
    
body .mrName {
    color: rgb(0,0,0) !important;}    

body .mrPerc{
  font: bold 12px tahoma !important; 
  color: rgb(5,5,5) !important;}  
    
/*_____________________
 |                     | 
 |  ABOUT:BUILDCONFIG  | 
 |_____________________|*/    

html .aboutPageWideContainer {
    border: 1px solid rgba(250,250,250,.26) !important;}
    
html .aboutPageWideContainer h2 {
    text-align: left !important; } 
    
html .aboutPageWideContainer > P  { 
    color: rgb(160,160,160) !important;
    font: normal 13px verdana !important;} 
    
HTML > BODY > TABLE:nth-child(7) > TBODY > TR,
HTML > BODY > TABLE:nth-child(5) > TBODY > TR:first-child > TH,
HTML > BODY > TABLE:nth-child(5) > TBODY > TR:last-child > TD    {
    background: rgba(250,250,250,.1) !important; }
    
/*_________________
 |                 |
 |  ABOUT:CRASHES  | 
 |_________________|*/     
    
html > body,
html > body > h1{ 
    background-color: transparent !important; 
    border-color: transparent !important; } 
    
#reportList > table {
    border: 1px solid #111 !important;
    background-color: rgba(250,250,250,.1) !important; }
    
#reportList #tbody > tr > td:first-child:hover a {
    text-decoration: none !important;
    color: rgb(0,220,0) !important; }
    
#reportList #tbody > tr:hover > td:nth-child(n+2) {
    color: rgb(220,220,220) !important;}   
    
#reportList > table tr:hover td {
    background: rgba(250,250,250,.2) !important; } 
    
#clear-reports {
    -moz-margin-end: 4.9% !important; }
    
html > body[onload="populateReportList()"] h1 {
    font-weight: bold !important;
    padding-bottom: 20px !important;
    margin-top: -13px !important;
    -moz-margin-start: 5% !important; }       
    
/*_________________
 |                 |
 |  ABOUT:PLUGINS  | 
 |_________________|*/    
    
#outside > TABLE {
    outline:  none !important;
    border-color: rgba(250,250,250,.15) !important;
    background-color: rgba(80,80,80,.4) !important; }    

#outside > HR { 
    opacity: 0 !important;}  
    
#outside DL > DD {  
    color: rgb(160,160,160) !important;    
    font: normal 11px verdana !important;} 
    
#findpluginupdates {  
    text-align: center !important;
    color: rgb(180,180,180) !important;    
    font: normal 12px verdana !important;}    
    
#outside DL > DD > SPAN {  
    color: rgb(180,150,100) !important;    
    font: normal 12px verdana !important;} 
    
#outside > TABLE > THEAD > TR > TH {
    color: rgb(160,160,160) !important;
    background-color: rgba(20,20,20,.5) !important; }    
  
#outside > H1    {
    font-size: 250% !important;
    margin-bottom: 4px !important;
    color: rgb(160,160,160) !important; } 
    
/*_________________
 |                 |
 |  ABOUT:SUPPORT  | 
 |_________________|*/       
  
#crashes-allReports.hidden a,
#contents > table:nth-child(15) > thead > th,
#contents > table:nth-child(12) > thead > th  {
    border: 0 !important;
    outline: 0 !important; }
    
html[hasBrowserHandlers="true"] body {
    background: transparent !important; }    

    #action-box,     
#contents > table {
    outline:  none !important;
    border: none !important;
    background-color: rgba(80,80,80,.4) !important; }
    

#reset-box,    
#safe-mode-box { 
        background-color: transparent !important;
        border: none !important;}    
    
#contents .prefs-table {
    outline: 1px solid rgba(250,250,250,.3) !important;}    
  
#reset-box {  
    border-color: rgba(250,250,250,.1) !important; } 

tr:hover td {
    background: rgba(250,250,250,.2) !important; } 
    
#reset-box p {
    text-align: center !important;
    color: rgb(150,150,150) !important;
    font: normal 12px verdana !important;}
    
#reset-box h3,
#safe-mode-box > h3    {
    text-align: left !important;
    color: rgb(170,170,170) !important;
    font: bold 15px verdana !important;}      
    
#contents th {
    color: rgb(160,160,160) !important;
    background-color: rgba(20,20,20,.5) !important; }    
  
html > body > h1 {
    font-size: 250% !important;
    margin-bottom: 4px !important;
    color: rgb(160,160,160) !important; }  
  
html h2 {
    text-align: center !important;
    font-size: 180% !important;
    margin-bottom: 4px !important;
    color: rgb(130,130,130) !important; }
    
html h2:first-child {
    text-align: left !important;}     
  
.prefs-table { 
    border: none !important;}  
  
html > body > div:nth-child(3),
#prefs-user-js-section > p {
    color: rgb(150,150,150) !important; 
    font: normal 100% verdana !important;}
  
html > body a {
    font: normal 100% verdana !important;
    color: #0083DB !important; }
 
html > body a:hover {  
    color: #00AAFF !important; }
  
tr > td, tr > th  { 
    font: normal 100% verdana !important;
    border: none !important;
    outline:1px inset rgba(0,0,0,.15) !important; }
    
tbody tr > td:nth-child(1) {
    font: bold 12px tahoma !important; }
    
    tbody tr > td { color: black}    
    
#contents > table:nth-child(2) > tbody tr > td {
    outline: 1px inset rgba(0,0,0,.15) !important;}
  
html > body tbody { 
    outline: 1px solid rgba(0,0,0,.4) !important; }   

#graphics-info-properties,    
#graphics-tbody {
    outline-color: rgb(60,60,60) !important; }     
 
/*__________
 |          |
 |  BUTTONS | 
 |__________|*/     
    
html > body button { 
    font-size: 13px !important;
    -moz-appearance: none !important;
    color: rgb(160,160,160) !important;
    padding: 1px 2px 2px 2px !important;
    border: 1px solid rgb(35,35,35) !important;
    background: -moz-linear-gradient(rgb(125,125,125) 1%, rgb(65,65,65) 12%, rgb(25,25,25) 90%, rgb(10,10,10) 98%) !important; }
html > body button:hover { 
    color: rgb(220,220,220) !important;
    border: 1px solid rgb(25,25,25) !important;
    background: -moz-linear-gradient(rgb(150,150,150) 1%, rgb(90,90,90) 12%, rgb(35,35,35) 90%, rgb(20,20,20) 98%) !important; }     
html > body button:hover:active { 
    background: -moz-linear-gradient(rgb(130,115,60) 2%, rgb(80,60,10) 10%, rgb(50,30,0) 90%, rgb(20,20,20) 98%) !important; }

/*________________________
 |                        |
 |  ABOUT:SERVICEWORKERS  | 
 |________________________|*/  
    
#serviceworkers > div  { 
    background: rgba(250,250,250,.1) !important; border: 1px solid #111; }
    
#serviceworkers > div > hr { 
    border: none !important }   
    
#serviceworkers > div > button:nth-child(3) { 
    margin: 4px 4px 0 16px!important }    
    
/*________________
 |                |
 |  ABOUT:CONFIG  | 
 |________________|*/  
    
#configTree #configTreeBody {  
    font-size: 11px !important;
    font-family: verdana, /*corbel,candara*/sans-serif,fantasy!important;
    box-shadow: inset -3px -3px 2px rgba(0,0,0,.5),inset 3px 3px 2px rgba(0,0,0,.35) !important; 
    border: 1px solid !important; border-color: rgba(100,100,100,.5) rgba(140,140,140,.5) rgba(140,140,140,.5) rgba(100,100,100,.5) !important;}   

treechildren::-moz-tree-cell-text { 
    color: rgb(165,165,165) !important; }
                   
#configTreeBody::-moz-tree-cell-text(user) {
    color: rgb(200,200,200) !important; }
  
treechildren::-moz-tree-cell-text(selected),                   
#configTreeBody::-moz-tree-cell-text(selected, user) {
    color: rgb(200,150,0) !important; }  

treechildren::-moz-tree-row(hover):not(selected) {
    background: rgba(70,70,70,.2) !important;
    border: 1px outset rgba(0,0,0,.2) !important; }
    
 treechildren::-moz-tree-row   {
    margin: 0 3px !important;
    border-radius: 8px !important;}
  
treechildren::-moz-tree-row(selected):not(:hover),
treechildren::-moz-tree-row(hover,selected) { 
    background: rgba(110,110,110,.3) !important; 
    border: 1px solid !important;
    border-color: rgba(200,200,200,.2) rgba(0,0,0,.65) rgba(0,0,0,.65) rgba(200,200,200,.2) !important; } 
  
treechildren::-moz-tree-row:not(selected),
treechildren::-moz-tree-row:not(hover):not(selected) {
    outline: 0 !important; }  
  
#configTree treechildren::-moz-tree-cell-text {
    margin-left: 5px ; }   
  
treechildren::-moz-tree-row {
    border: 2px solid transparent!important;
    min-height: 22px!important;
    height: 22px !important;  }

#configTree > treecols :-moz-any(#prefCol,#lockCol,#typeCol,#valueCol,.treecol-image) {
    min-height: 22px !important;    
    margin-bottom: 4px !important;
    box-shadow: none !important;
    font-family: verdana !important; 
    color: rgb(180,150,100) !important; }
    
#configTree > treecols :-moz-any(#prefCol,#lockCol,#typeCol,#valueCol) {
    max-height: 22px !important;} 
    
.treecol-image:hover { 
    margin-top: -5px !important;}
    
.treecol-image:hover .tree-columnpicker-icon {
    margin-bottom: -9px !important; }
    
    #prefCol:hover > image  {
    margin-bottom: -5px !important;}  
    
#configTree > treecols > splitter { 
    outline: 1px solid rgba(250,250,250,.3) !important;  
    margin-bottom: 4px !important; }  

#configTree > treecols,  
#configTree > treecols :-moz-any(#prefCol,#lockCol,#typeCol,#valueCol,.treecol-image) { 
    border: none !important;}     

#filterRow, 
#configDeck, 
#configTree,
#configTree > treecols :-moz-any(#prefCol,#lockCol,#typeCol,#valueCol,.treecol-image) {
    -moz-appearance: none !important;
    background: url("data:image/png;base64,") repeat scroll,
    -moz-linear-gradient(rgb(120,120,120) .5%, rgb(50,50,50) 2%, rgb(30,30,30) 98%, rgb(5,5,5) 99.5%) fixed !important;}
  
#configTree > treecols{ 
    margin: -2px 0 !important; }
    
#configTree { 
    margin: -2px !important; }                
                   
#configTree #configTreeBody {
    margin: -2px 0px 18px 16px ;} 
  
#configTree > treecols :-moz-any(#prefCol,#lockCol,#typeCol,#valueCol) .treecol-text {
    margin-left: 20px !important;}     

#configTree > treecols :-moz-any(#prefCol,#lockCol,#typeCol,#valueCol,.treecol-image):hover {
    background: rgba(80,80,80,1) !important; padding-bottom: 10px !important; }    
  
#filterRow > label {
    color: rgb(160,160,160) !important; 
    margin-left: 24px !important;}
  
#textbox {
    margin-right: 16px !important;
    margin-bottom: 3px !important;
    height: 24px !important;
    -moz-appearance: none !important; 
    color: rgb(180,180,180) !important;
    border-radius: 4px !important;
    font: normal 13px verdana !important;
    border: 1px solid !important;
    background: rgb(55,55,55) !important;
    box-shadow: rgba(0,0,0,.3) 1px 1px 3px 0px inset !important; 
    border-color: rgba(0,0,0,.6) rgba(150,150,150,.45) rgba(150,150,150,.45) rgba(0,0,0,.6) !important;
    }
    
#textbox ::-moz-selection { 
    background: rgba(160,240,240,.3) !important;
    color: rgb(0,0,0) !important; }
    
#textbox:hover,
#textbox[focused] { 
    color: rgb(200,200,200) !important;
    background: rgb(40,40,40) !important;
    box-shadow: rgba(0,0,0,.55) 1px 1px 3px 0px inset !important;} 

#textbox:hover:not([focused]) { 
    background: rgb(50,50,50) !important;}
    
.textbox-search-icon:hover {
    opacity: 1 !important;
    cursor: default }
.textbox-search-icon {
    opacity: .6 !important; 
    -moz-image-region: auto !important; 
    list-style-image: url("data:image/png;base64,") !important;}   
                   
/*______________________________________________________________________________
 |                                                                              |
 |  DIALOGUE POPUP FOR ABOUT:CONFIG                                             |
 |  REPLACE dialog:-moz-any(...) WITH #commonDialog TO MAKE GLOBAL              |
 |  - btw when changing the width, close/reopen the popup for it to take effect |
 |______________________________________________________________________________|*/
  
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) { 
    width: 40em !important;
    -moz-appearance:-moz-win-glass !important; } 
    
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) description[context="contentAreaContextMenu"] { 
    font: normal 13px verdana !important;
    color: rgb(180,150,100) !important; } 
 
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) > grid textbox{
    height: 24px !important;
    -moz-appearance: none !important; 
    color: rgb(190,190,190) !important;
    box-shadow: 1px 1px 0px rgba(20,20,20,.5)inset,-1px -1px 0px rgba(150,150,150,.3)inset !important;
    border: none !important;
    font-size: 13px !important;
    background: rgb(60,60,60) !important;} 
    
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) > grid textbox[focused="true"] ::-moz-selection { 
    background: rgba(160,240,240,.3) !important;
    color: rgb(0,0,0) !important;}
    
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) > grid textbox:not(:hover) ::-moz-selection {     
    background: rgb(60,60,60) !important;
    color: rgb(200,200,200) !important; }
  
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) hbox > button {
    -moz-appearance: none !important;
    padding: 1px 2px 2px 2px !important;
    border: 1px solid rgb(30,30,30) !important;
    background: -moz-linear-gradient(rgb(130,130,130) 1%, rgb(70,70,70) 12%, rgb(30,30,30) 90%, rgb(10,10,10) 98%) !important; }
    
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) hbox > button:hover { 
    color: rgb(180,180,180) !important;
    border: 1px solid rgb(20,20,20) !important;
    background: -moz-linear-gradient(rgb(150,150,150) 1%, rgb(90,90,90) 12%, rgb(35,35,35) 90%, rgb(20,20,20) 98%) !important; }     
    
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) hbox > button:hover:active { 
    background: -moz-linear-gradient(rgb(130,115,60) 2%, rgb(80,60,10) 10%, rgb(50,30,0) 90%, rgb(20,20,20) 98%) !important; }
    
dialog:-moz-any([title="Enter string value"],[title="Enter integer value"],
[title="New string value"],[title="New integer value"],[title="New boolean value"]) hbox > button[default="true"]:not(:hover) {
    color: rgb(180,150,100) !important; }  
    
/*__________________________________________
 |                                          |
 |  RIGHT CLICK MENU FOR ABOUT:CONFIG ONLY  | 
 |__________________________________________|*/
    
menupopup {
    opacity: 1 !important;
    border: none !important; 
    padding: .4em 0 !important;
    background: none !important;
    box-shadow: none !important;
    -moz-appearance: none !important;
    border-radius: 0px !important;
    color: rgb(160,160,160) !important;      
    background: -moz-linear-gradient(rgb(125,125,125)1%, rgb(75,75,75)3%, rgb(50,50,50)97%, rgb(20,20,20)99.5%) !important;
    text-shadow: 0px 1px 0px rgba(0,0,0,.15), 0px -1px 0px rgba(0,0,0,.3), 2px 2px 1px rgba(200,200,200,.2) !important;}
    
menupopup .menu-text,
menupopup .menu-iconic-text {
    font-size: 120% !important; 
    font-family: candara !important; 
    color: rgb(200,200,200) !important; }
    
.menuitem-iconic[disabled="true"] .menu-text,
.menuitem-iconic[disabled="true"] .menu-iconic-text {
    color: rgb(130,130,130) !important; }
    
menuseparator {
    opacity: 0 !important; }
    
menu:hover,
menuitem:hover,    
menuitem.menuitem-iconic:hover,
menuitem.menuitem-non-iconic:hover {
    -moz-appearance: none !important;
    background: rgba(0,0,0,.4) !important;
    box-shadow: none !important; }
    
menu,
menuitem,    
menuitem.menuitem-iconic,
menuitem.menuitem-non-iconic {
    -moz-appearance: none !important;
    background: none !important; }     
     
/*______________
 |              |
 |  SCROLLBARS  | 
 |______________|*//*AGENT_SHEET*/
    
html scrollbar:not(#foo) {
    z-index: 2147483647 !important;
    position: relative !important;
    background: none !important;
    -moz-appearance: none !important; }
    
html>scrollbar[orient="vertical"]:not(#foo)	{
    -moz-margin-start: -16px !important; }
    
html>scrollbar[orient="horizontal"]:not(#foo) {
    margin-top: -14px !important; }    

scrollbar[orient='vertical'] > slider > thumb {
    min-height: 28px !important; }
    
scrollbar[orient='horizontal'] > slider > thumb {
    min-width: 28px !important; }
    
scrollbar  > scrollbarbutton {
    display: none !important; }
    
scrollcorner {
    visibility: collapse !important; }
    
.CodeMirror-scrollbar-filler { 
    display: none !important; }
    
scrollbar > slider > thumb,
scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    border-radius: 0px !important;
    display: -moz-box !important;
    -moz-appearance: none !important; 
    border: 1px outset black !important;    
    background-color:rgb(70,70,70) !important;
	  box-shadow: inset 1px 1px 6px 0px rgb(25,25,25), inset -1px -1px 6px 0px rgb(178,178,178) !important; } 
    
html scrollbar:not(#foo)  {
    background-color: rgb(65,65,65) !important;
    box-shadow: inset 2px 2px 3px 1px rgba(25,25,25,.6), inset -2px -2px 3px 1px rgba(178,178,178,.6) !important; } 
 
/* BRIGHTER ON HOVER */
    
scrollbar > slider > thumb:-moz-any(:hover, :active),
scrollbarbutton:-moz-any(:hover, :active)[sbattr="scrollbar-up-top"],
scrollbarbutton:-moz-any(:hover, :active)[sbattr="scrollbar-down-bottom"] {
    background-color: rgb(160,160,160) !important; }  

/* HIDES HORIZONTAL SCROLLBAR TILL HOVERED */
    
scrollbar[orient="horizontal"]:not(:hover),
html:not(#foo) scrollbar[orient="horizontal"]:not(:hover) {
    opacity: 0 !important;  }  

/* HIDES SCROLLBAR BUTTONS TILL SCROLLBARS HOVERED */
    
scrollbar:not(:hover) scrollbarbutton {
    opacity: 0 !important; }
    
/* KEEPS VERTICAL THUMB VISIBLE */
    
scrollbar[orient="vertical"]:not(:hover),        
html:not(#foo) scrollbar[orient="vertical"]:not(:hover) {
    opacity: 1 !important; }

/* COMMENT THIS OUT TO SHOW BACKGROUND SLIDER */
    
scrollbar,    
scrollbar > slider,
html scrollbar:not(#foo),
#internal-code > hbox > textarea > div > scrollbar {
    -moz-appearance: none !important; 
    background: transparent !important;
    background-color: transparent !important;
    box-shadow: none !important; } 

