css:
@-moz-document domain(d20pfsrd.com) {
[class*="sites-embed"]:not([role*="navigation"]):not([class*="sites-sidebar-nav"]),
[class*="sites-embed"]+[style*="float"],
[class*="sites-tile-name-header"],
[class*="sites-tile-name-content-2"],
[class*="sites-logo"],
[id*="sites-chrome-footer-wrapper"] { display: none; }
[class*="sites-header-cell-buffer-wrapper"] { padding: 7px; }
[id*="sites-chrome-main"],
[id*="sites-chrome-main"]>tbody,
[id*="sites-chrome-main"]>tbody>tr,
[id*="sites-chrome-main"]>tbody>tr>td { display: block; }
[id*="sites-chrome-sidebar-left"] { max-width: 20px; float: left; }
[id*="sites-chrome-sidebar-left"]:hover { max-width: none; }
#sites-canvas-wrapper #sites-page-title-header,
#sites-canvas-wrapper #sites-page-title,
#sites-canvas-wrapper h1,
#sites-canvas-wrapper h2,
#sites-canvas-wrapper h3,
#sites-canvas-wrapper h4,
#sites-canvas-wrapper h5 { font-size: 1.2em !important; }
}
