css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/*titlebar buttons*/

#appmenu-button {display: none;}
#TabsToolbar {margin-left: -5px;}
#menubar-items {margin-left: -5px;}

/* tabs (don't forget to disable browser.tabs.animate in about:config)*/
.tabbrowser-tab[fadein]:not([pinned]) {
	min-width: 10em !important;
	max-width: 10em !important;
	background-image: -moz-linear-gradient(#fbfbfb, #d5d5d5) !important;
 }

.tabbrowser-tab[fadein]:not([pinned]):not([selected]) {
	min-width: 10em !important;
	max-width: 10em !important;
	background-image: -moz-linear-gradient(#d5d5d5, #fbfbfb) !important;

 }
.tabbrowser-tab[fadein][pinned] {
	min-width: 2em !important;
	max-width: 2em !important;
	background-image: -moz-linear-gradient(#fbfbfb, #d5d5d5) !important;
 }

.tabbrowser-tab[fadein][pinned]:not([selected]) {
	min-width: 2em !important;
	max-width: 2em !important;
	background-image: -moz-linear-gradient(#d5d5d5, #fbfbfb) !important;

 }
 
 



/*hide icons on urlbar*/

#identity-box {display: none;}

#notification-popup-box {display: none;}

#page-proxy-favicon {display: none;}

#urlbar-reload-button {display: none !important;}

.urlbar-history-dropmarker {display: none !important;}

#urlbar-icons {display: none;}

/*urlbar-container size*/
 #urlbar-container {
	min-width: 0px !important;
	max-width: 0px !important;
	min-height: 26px !important;
	max-height: 26px !important;
	margin-left: 0px !important;
	padding-left: 0px !important;
}




/*new tab button invicible*/

.tabs-newtab-button {

  opacity: 0 !important;

}

/*tab color*/
.tabbrowser-tab{
margin-right: -1px;
}

/*main windows size*/

#main-window[tabsintitlebar]:not([inFullscreen]) #toolbar-menubar[inactive] ~ #TabsToolbar{
height: 24px !important;
margin-bottom: -2px !important;
margin-top: -3px;
margin-left: -4px;
background-image: -moz-linear-gradient(#f7f7f7, #f0f0f0) !important;

}
/*tab icon position*/

.tab-icon-image{
margin-top: -2px;
box-shadow:  0px 0px 2px 0px #9f9e9e;}

/*URL BAR POSITIONING, these settings are for 1366x768 screens, other screens might need some adjustments*/

#urlbar {
	position: fixed;
   	box-shadow: none !important;
    right: 113.6em;
	top: 3em;
	transition: none;
    width: 29.4em;
	border: 0.1em solid;
	border-color: black !important;
	border-radius: 0em;
 }

#urlbar[focused] {
	position: fixed;
	right: 84.3em;
	top: 3em;
	transition: none;
	opacity: 1 !important;
    width: 29.4em;
 }
 
.textbox-input-box.urlbar-input-box{
width: 29.4em;
}
