css:
@-moz-document url(chrome://browser/content/browser.xul){

/* Bookmarked items */
.autocomplete-richlistitem[type="bookmark"] {font-weight: bold !important;
background:linear-gradient(0deg, rgba(163, 73, 164, 1), rgba(255, 255, 255, 0), rgba(255, 255, 255, 0), rgba(255, 255, 255, 0), rgba(255, 255, 255, 0))  !important;
border-radius: 8px !important;}
  
/* "Swtich to tab" items */
.autocomplete-richlistitem[actiontype="switchtab"] {font-weight: bold !important;
background:linear-gradient(0deg, blue, rgba(255, 255, 255, 0), rgba(255, 255, 255, 0), rgba(255, 255, 255, 0), rgba(255, 255, 255, 0))  !important;
  border-radius: 8px !important;}

.autocomplete-richlistitem[actiontype="switchtab"] .ac-url-text, .autocomplete-richlistitem[type="bookmark"] .ac-url-text {font-weight:normal ! important;}
}
