@namespace url(http://www.w3.org/1999/xhtml);

/* YOU HAVE TO ADJUST THE HEIGHT OF THE READER WIDGET USING THE FOLLOWING LINE IN YOUR CODE
#feedReaderHeadlinesFrame, #feedReaderContentFrame {height:859px !important;} 
This line is already i the code, just before the --- TABS --- line
*/

@-moz-document url-prefix(http://www.netvibes.com/privatepage/) {
/* --- HEADER --- */
/* New organisation of the Header */
#top .left {width: 43px !important; right:0px !important; position:absolute !important; z-index:10; top:0px; direction:rtl !important;}
#top #contentAnalyseContainer {width:0px !important; direction:ltr !important; margin-left:0px !important;}
#top .add-button .text {display: none !important;}
#top .add-button .icon {margin-right: -4px !important;} /* Reduces the Add Button */
#top .right {position:absolute !important; top:0px; right:40px; }
#top #userProfile {padding-right:0px !important; padding-left:0px !important; margin-right:3px !important; margin-left:3px !important;}
#top #userProfile img {margin-left:5px !important; margin-right:5px !important; height:28px !important; width:28px !important;}
#top #userProfile .icondashboard, #top .right #userPages .icondashboard {display:inline !important;}
#top #userProfile .icon, #top #userProfile .text, #top #userPages, #top .separator, #top #userSettings, #top .center {display:none !important;}
#switch-view {display:none !important;} /* Switch view (widget or reader) */

/* Save space on header */
#top {position:absolute !important; width:0px !important; right:0px !important; background-image:none !important;}
#header { background-image:none !important; background-color:#373737 !important;}
#topLinks {height:0 !important; padding-bottom: 2px !important;}
#collapseExpand, #globalSearch {display:none !important;} 

/* Remove annoying red dot */
#savingStatus {display: none !important;}

/* MP3 player */
#playermp3Container {z-index:99 !important; background-color:#373737 !important; position: absolute !important; right:-79px !important;} 
#playermp3Container .controls-times {display:none !important;}
.uwa-audioplayer {width:140px !important;  height:32px !important;}

/* Menu */
.menu .vip, .menu #topBar-help {display:none !important;}
.menu, .menu li:hover {max-width:80px !important; min-width:80px !important; border-top-left-radius:6px !important;}
.menu .signout:hover {border-top-left-radius:0px !important;}
     
/*--- FOOTER ----*/
#footer {display:none !important}
#viewType-1 {padding-bottom:0px! important;}

/*--- WIGDET ---*/
.moduleHeader .uwa-widget-menu-button.uwa-icon.charts {display:none !important;}
.moduleHeader:not(hover) .menus {display:none !important;}
.moduleHeader:hover .menus {display:block !important;}

/* Link already seen */
li.read {opacity:0.4 !important;}
.nv-thumbnailedList .read .description, li.read .feedThumbnail, .sharing {display:none !important;}
li.read .main-link, .nv-thumbnailedList .read h3 {margin-left:0px !important; font-size:8px !important;}

/* Personnalization of the widgets */
.module, .moduleFrame, .moduleContent, c, .moduleHeader {border-radius: 0 8px !important;}
.resizable .module, .resizable .moduleEdit {margin: 0px !important; margin-bottom: 5px !important;}
.moduleContent{padding:5px 5px 5px 5px!important}
.item a.download {display:none !important;}
/* Pager (msg anciens)*/
.uwa-pager.nv-pager {display:none !important;} 

/* Share - To delete if you want to share */
.share {display: none !important;}

/* Gripper */
.gripper {background:none !important;}

/*--- READER ----*/
#feedReader {top: -11px !important; bottom: 0px !important; margin-left:-12px !important; width:100% !important; position: absolute !important;}
#feedReaderFrame {top:0px !important; bottom: 0px !important; position: absolute !important;}
#feedReaderFrame .frame {height: 100% !important;}
.nv-scroller-scrollbar-x, .action-read, .nv-ir-feed-head .title .favicon, .nv-ir-feed .item-actions {display:none !important;}
.nv-scroller-scrollbar:before {opacity:0.3 !important;}
.nv-scroller-scrollbar.visible:before {opacity:0.5 !important;}
.nv-quickshare-bar {width:870px !important; text-align:right !important; margin-top:-20px !important;}
#readerTable .item-actions-wrapper {width:1px !important; right:-85px !important; margin:0px !important; height:20px !important; position:absolute !important;}
#readerTable .item-actions .item-action {margin:0px !important;}
#readerTable .nv-ir-feed {
  max-width:800px !important;
  text-align:justify !important;
  padding-top:0px !important;}
#readerTable .nv-ir-article-text {
  max-width:800px !important;
  line-height:22px !important;
  text-align:justify !important;}
#readerTable .nv-ir-article-text p {padding-bottom:10px !important;}
#readerTable .nv-ir-feed-head {max-width:800px !important; margin-top:5px !important; padding-bottom:10px !important; bottom: 0px !important;}
#readerTable .nv-ir-feed-head .title {line-height:25px !important;}
#readerTable .nv-ir-feed-infos {margin-top:0px !important; padding-bottom:0px !important; bottom: 0px !important;}
#readerTable .feed.nv-item-renderer {margin:0px !important; padding-bottom:0px !important;}
#readerTable .website iframe {top:0px !important; bottom: 0px !important;}
#readerTable .content-website {padding-left:20px !important;}
#readerTable .headlines.tdHeadlines {width:350px !important;}
#readerTable img, #readerTable .entry-enclosures-list {float:left !important; margin: 10px 15px 15px 0px !important;}
#feedReaderHeadlinesFrame .unread {font-weight:bold !important;}
#feedReaderHeadlinesFrame .headline-link {white-space:normal !important; padding:5px !important;}
#feedReaderHeadlinesFrame .headline {padding:0px !important;}
#feedReaderContentFrame .content-website {padding-top:0px !important;}
#feedReaderHeadlinesFrame, #feedReaderContentFrame {height:859px !important;} /* TO BE ADJUSTED */

/*--- TABS ---*/
/* Slimmer tabs */
#topLinks {padding-top: 3px !important;}
#divTabs {margin-top: 1px !important; margin-left:-8px !important;}
#divTabs li {padding: 3px 5px !important;}
#divTabs .actions-tab-options, .tabs .icon {display:none !important;}
.tabs .innerTab {padding-left:4px !important; padding-right:4px !important;}
.tabs .tab {min-width:90px !important;}
    
/* Delete "Tab +"*/
.nv-tabs-left, .nv-tabs-right {display:none !important;}

/* Number of links unread */
.innerTab .unread, .counter.unread {color:rgb(50,50,50) !important; font-weight:bold !important;}

/* Main div: removes the left padding */
#maintable{padding-left:2px!important;}

}
