css:
@-moz-document url-prefix(http://live.francetvinfo.fr/widget_live/) {

.messages-list, .search-messages-list {height:673px !important;}    /* REGLER LA HAUTEUR ICI */

  /* Top */
    .trending_topics dt, .delete_this {display:none !important;}
    .trending_topics dd {margin-left:0px !important; margin-right:10px !important; margin-top:-2px !important;}
    .trending_topics {padding-top:0px !important; padding-bottom:0px !important;}
    .trending_topics .trending-topic {color:#FFF !important;}
    .header:not(hover) {margin-top:-45px !important;}
    .header:hover {margin-top:0px !important; -moz-transition-delay:1s !important;}
    .header .searchForm {margin-top:5px !important; padding-left:15px !important;}
    .header .searchForm .searchMode {right:35px !important;}
    .header .searchForm #q {padding-left:5px !important;}
    .header {background-color:#5E5E5E !important;}
    .searchForm .submit {margin-right:-3px !important; border-style:solid !important; border-width:1px !important;}
  /* Message */
    .publication, .share.sharrre, .reply {display:none !important;}
    .messages-list {padding:0px !important;}
    .message {padding:5px 3px 5px 45px !important; border-color: #9E9E9E !important; border-radius:0px !important; border-bottom:none !important; border-left:none !important; border-right:none !important; margin-bottom:0px !important; background:lightgrey !important; text-align:justify !important; padding-right:10px !important;}
    .message .username, .message .body {margin-left:-10px !important;}
  /* Bottom  */
    .more {background:lightgrey !important; margin-top:0px !important; padding-top:10px !important; padding-bottom:5px !important;} 
    .input_message textarea {height:100px !important;}
    .formComment {margin-bottom:-165px !important; background-color:#5E5E5E !important;}
    .formComment:hover {margin-bottom:0px !important;}
    .video, .formComment .avatar, .formComment h2 {display:none !important;}
    .deco {display:inline !important; border-radius:5px 0 0 0 !important; background:linear-gradient(195deg, transparent, transparent,transparent,transparent,transparent,transparent,transparent,#5E5E5E,#5E5E5E,#5E5E5E,#5E5E5E, #5E5E5E, #5E5E5E) !important;  width:60px !important; margin-left:-20px !important;}}
