css:
@-moz-document domain("youtube.com") {
/* 
 * Name: Youtube - Zelda Majora's Maskprogress bar
 * Title: Youtube - Zelda Majora's Mask progress bar
 * Author:  MMouse
 * Version: 1
 *
 */

.html5-play-progress, .ytp-play-progress {
   background: #9233E6 !important; /* Old browsers */
   background: -moz-linear-gradient(left, #1b1b1b 0%, #9233E6 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#1b1b1b), color-stop(100%,#9233E6)) !important;
   background: -webkit-linear-gradient(left, #1b1b1b 0%,#9233E6 100%) !important;
   background: -o-linear-gradient(left, #1b1b1b 0%,#9233E6 100%) !important;
   background: -ms-linear-gradient(left, #1b1b1b 0%,#9233E6 100%) !important;
   background: linear-gradient(to right, #1b1b1b 0%,#9233E6 100%) !important;
}
/*.html5-load-progress, .ytp-load-progress {
   background: #404040 !important;
}*/
/* actual scrubber button */
.html5-scrubber-button, .ytp-scrubber-button {
   background: #383A3B !important;
   border-color: #1B1B1B !important;
   transition: box-shadow 0.2s, background 0.2s !important;  
}
/* volume control */
.ytp-volume-slider-foreground:before {
   background: #9233E6 !important; /* Old browsers */
   background: -moz-linear-gradient(left, #1b1b1b 0%, #9233E6 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#1b1b1b), color-stop(100%,#9233E6)) !important;
   background: -webkit-linear-gradient(left, #1b1b1b 0%,#9233E6 100%) !important;
   background: -o-linear-gradient(left, #1b1b1b 0%,#9233E6 100%) !important;
   background: -ms-linear-gradient(left, #1b1b1b 0%,#9233E6 100%) !important;
   background: linear-gradient(to right, #1b1b1b 0%,#9233E6 100%) !important;
}

.html5-scrubber-button, .ytp-scrubber-button {
	background: url("http://i.imgur.com/76DrtEd.png") ! important;
	background-repeat: no-repeat ! important;
	width: 42px ! important;
	height: 36px ! important;
	border: none ! important;
	margin-left: -16px ! important;
	margin-top: -19px ! important;
}
}
