css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.ohmydollz.com/forum/") {


.pun .tcl,.pun .tc2, .pun .tc3,.pun .tcr  {border-color:#00501f !important;}
.pun .tcr  {border-right: 1px solid #00501f !important;}

.pun .tcr .byuser {color: #00501f !important; }

#contenu{
background: url("http://image.noelshack.com/fichiers/2014/48/1417309629-fond-2014.png") fixed repeat top center !important;
}

#head_forum {
background-image:url("http://image.noelshack.com/fichiers/2015/08/1424298524-banniere-2014.png") !important;
    position: relative !important;
    right: 0px !important;
    top: 0px !important;
}

#head_links, #head_links a {
left:250px !important;
bottom:31px !important;
color:#bc0000 !important;
font-family: "verdana", arial, sans-serif !important;
font-size : 14px !important;
text-shadow:green 0px 0px 10px !important;
}

#head_links a[href*="../?p=gazette"] {
text-shadow:green 1px 1px 2px !important;
}

#footer_box {
background-image:url("http://image.noelshack.com/fichiers/2014/49/1417393078-brd-about.png")  !important;
opacity : 0.8 !important;
}

#footer_box p, #footer_box a{color:#FFF !important;}


DIV.icon {
background-image:url("http://img269.imageshack.us/img269/9880/nonlu.png") !important;
}
DIV.inew {
background-image:url("http://img10.imageshack.us/img10/5305/43519237.png") !important;
}


#brdwelcome a, h2, td .tclcon, .pun, .pun .tcr .byuser {color: #00501f !important;}


#brdwelcome {
background-image:url("http://image.noelshack.com/fichiers/2014/48/1417121044-brd-visit-2014-v2.png")  !important;
color:#00501f !important;
width: 970px !important;
}

#brdwelcome .conr {
position:relative;
right:10px;
}

#brdmenu {
background-color:rgba(144, 199, 150, 1) !important;
background-image:none !important;
width: 938px !important;
}


#announce h2, #idx1 h2, #idx2 h2, #idx3 h2, #idx4 h2, #idx5 h2, #idx6 h2, #idx7 h2, #idx8 h2, #idx9 h2, #idx10 h2, #vf h2, .blockpost h2, .blockform h2, .pun H2 {
background-image:url("http://image.noelshack.com/fichiers/2014/48/1417123110-main-head.png")  !important;
background-color:transparent !important;
}


.blockpost H2 {
background-image:url("http://image.noelshack.com/fichiers/2014/48/1417124439-main-head-message.png")  !important;
}

DIV.box, .pun BLOCKQUOTE, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven {
background-color: transparent !important;
}

thead .tcl, thead .tc2, thead .tc3, thead .tcr {
background-image:url("http://img137.imageshack.us/img137/4552/bandeausoustitre.png")  !important;
background-repeat: no-repeat !important;
color: #00501f !important;
}

.tcl, .tc2, .tc3,.tcr, #announce .inbox {
background-color: rgba(144, 199, 150, 0.50) !important;
}


.fakeform  {
background-color: rgba(144, 199, 150, 0.5) !important;
}
.infldset DD {
background-color: rgba(144, 199, 150, 0) !important;
}


.inbox a {color:#00501f !important;}


.box, thead, th.tcl, th.tc2, th.tc3, th.tcr, td.tcl, td.tc2, td.tc3, td.tcr {
border-color: #00501f !important;
}

.blockpost .box .inbox{
background-color:rgba(150, 200, 150, 1) !important;
}

.blocktable .box .inbox{
background-color:rgba(150, 200, 150, 0.8) !important;
}

.postleft, .postfootleft {
background-color:rgba(255, 160, 150, 1) !important;
}

.postright, .postfootright {
background-color: rgba(150, 200, 150, 1) !important;
BORDER-LEFT: 18em solid rgba(255, 160, 150, 1) !important;
}

.blockform .box {
background-color: rgba(150, 200, 150, 1) !important;
}

.pun legend {
color:green !important;
}
.pun a:link, .pun a:visited {
color:green !important;
}
}
