css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(helgon.se), domain(helgon.net) {
    body

{
    background-image: url("http://th04.deviantart.net/fs71/PRE/f/2013/335/b/0/jotaro_render_by_franky4fingersx2-d6wg0dx.png") !important;
    background-repeat: no-repeat;
    background-color: rgb(53,53,53);
    background-position: left;
    background-attachment: fixed;
}
    #cbox
    {
        visibility: visible!important;
        background: rgb(51,0,0);
        border-radius:8px;
    }
    .wrapper_ext
    {
        max-width:1200px;
    }
    #col_banner
    {
        visibility:hidden!important;
display:none!important;
    }
    #wrapper_sidebar
    {
        margin:0 auto;
    }

    *
    {
         font-family: helvetica,sans-serif!important;
    }
    
table {
    background-image: none!important;
}

div#log_top {
    background-image: none !important;
    background-color: rgb(51, 0, 0);
}

div#log_top_logo {
    background-image: url("http://img3.wikia.nocookie.net/__cb20140405002201/jjba/images/1/18/Jojo%27s_bizarre_adventure_hi_res_japanese_logo.png")!important;
    background-size: 147px 71px;
}

div#top_logo img {
    opacity: 0;
}

div#top_logo {
    background-image: url("http://img3.wikia.nocookie.net/__cb20140405002201/jjba/images/1/18/Jojo%27s_bizarre_adventure_hi_res_japanese_logo.png")!important;
    background-size: 147px 71px;
}

div#log_top_logo img {
    opacity: 0;
}

div#log_bottom {
    background-image: none!important;
    background-color: rgb(51,0,0);
}

/*kroppen vid inloggning*/
div#log_body {
    background-image: url("http://img1.wikia.nocookie.net/__cb20130220223313/onepunchman/images/thumb/1/18/Slider_dos.png/670px-Slider_dos.png")!important;
    background-color: rgb(51,0,0);
    /*background-size: 670px 360px;*/
    height: 360px;
}

div#log_body_left {
    visibility: hidden;
}

div#log_body_right {
    visibility: hidden;
}

.b_b {
    background-color: black;
    font-family: helvetica,sans-serif!important;
}

.b_t {
    background-image: none!important;
    background: -moz-radial-gradient(center, ellipse cover, #f9c667 0%, hsl(37,76%,36%) 100%)!important; /* FF3.6+ */
    
}

.b_tr {
    background: none!important;
}
    
.b_tr a:link
        {
            color: rgb(102,178,255)!important; 
        }
       

.b_t a:hover {
      color: rgb(102,178,255)!important;
}
    
div#main {
    background-image: none!important;
    background-color: rgb(51,0,0);
    border: 2px solid;
    border-radius: 10px;
    border-color: rgb(51,0,0);
    margin-top:0;
}

div#top {
    background: url("http://img4.wikia.nocookie.net/__cb20140713190524/jjba/images/0/0f/WikiHeaderJojo.png")!important;
    background-color: black!important;
    height: 79px;
}

div#mmt {
    background-image: none!important;
    /* background-color:#FFCC00;*/
    background: -moz-radial-gradient(center, ellipse cover, #f9c667 0%, hsl(37,76%,36%) 100%)!important; /* FF3.6+ */
}

    div#mmt .center a, div#smt .center a, div#content .b_t a {
        color: hsl(57,33%,93%)!important;
        font-weight: bold!important;
    }

        div#mmt .center a:hover, div#smt .center a:hover, .ttt a:hover {
            transition: color 0.5s;
            color: rgb(102,178,255)!important;
            text-shadow: 2px 2px hsla(0,20%,0%,0.2);
        }

#smt {
    background: none!important;
    background: -moz-linear-gradient(top, rgba(121,191,51,1) 0%, rgba(76,151,35,1) 50%, rgba(45,139,1,1) 51%, rgba(85,166,38,1) 100%)!important; /* FF3.6+ */
    height:20px !important;
    min-width:1000px;
    overflow:HIDDEN;
}

    #smt .center
    {
        min-width:800px;
    }
    

div#mmt a {
    border-right: 1px solid hsla(0,20%,0%,0.1);
    /*border-radius: 8px;*/
    box-shadow: 1px 1px 2px hsla(0,20%,0%,0.2);
    background: radial-gradient(ellipse at center, rgba(209,158,73,0.5) 23%, rgba(229,158,73,0.5) 24%, rgba(225,148,53,0.2) 75%);


}

    div#mmt a:active {
        border: 1px solid hsla(0,20%,0%,0.2);
       /*border-radius: 8px;*/
        box-shadow: 2px 2px 2px hsla(0,20%,0%,0.2);
        background-color: hsla(0,10%,0%,0.1);
        text-shadow:1px 1px hsla(0,40%,0%,0.5)!important;
    }
    div#smt a {
    border-right: 0px solid hsla(0,20%,0%,0.2);
    /*border-radius: 5px;*/
    box-shadow: 1px 1px 2px hsla(0,20%,0%,0.2);
    padding-left:10px;
    padding-right:10px;
    background: radial-gradient(ellipse at center, rgba(121,191,51,0.5) 23%, rgba(121,191,51,0.5) 24%, rgba(145,232,66,0.2) 75%);

        
}

    div#smt a:active {
        border: 1px solid hsla(0,40%,20%,0.5);
        border-radius: 1px;
        box-shadow: 2px 2px 2px hsla(0,20%,0%,0.2);
        background-color: hsla(0,10%,0%,0.1);
        text-shadow:1px 1px hsla(0,40%,0%,0.5)!important;
    }

div#mmt .center {
    /*width: 730px!important;
    height:31px!important;*/
    width:auto!important;
    float:none!important;
 
}
    div#mmt .center ul {
    /*width: 730px!important;
    height:31px!important;*/
    margin:auto;
}
    div#smt .center {
}
