css:
@-moz-document domain("not.drupal.org"),
               regexp("^https?:///*[[domain]]*/\/admin"),
               regexp("^https?:///*[[domain]]*/\/admin\/.*$"),
               regexp("^https?:///*[[domain]]*/\/node\/add\/.*$"),
               regexp("^https?:///*[[domain]]*/\/.*\/edit.*$"),
               regexp("^https?:///*[[domain]]*/\/.*\/panelizer\/.*$") {

/* needed not.drupal.org up top to make userstyles.org validator happy */
body:not(.cke_editable) {
 background-color: #1D1D1D;
 color: #CACACA;
 background-image: url(data:image/png;base64,) !important;

}
#branding {
 background: none; 
 padding: 20px 40px 0 40px;
}
#branding h1.page-title {
 background-color: none; 
 color: #fff;
}
#page {
 background: #303030;
 color: #D3D3D3;
 padding: 20px 15px 40px 15px;
}
input.form-autocomplete, input.form-text, input.form-file, textarea.form-textarea, select.form-select {
  border: 1px solid #222;
  border-top-color: #222;
  background: #343434;
  color: #FFF;
  padding: 1px;
}
input.form-text:focus, input.form-file:focus, textarea.form-textarea:focus, select.form-select:focus {
  color: #FFFFFF;
  border-color: #303030;
}
.hierarchical-select-wrapper .hierarchical-select .selects .grippie, .resizable-textarea .grippie {
  background-color: #494949;
  border: none;
}

fieldset {
  border: 1px solid #222;
  padding: 2.5em 2.5em 0 0;
}
table {
  border: 1px solid #222;
}
table th.active {
  background: #2A2A2A; 
}
table th {
  background: #3F3F3F; 
  border: 1px solid #222; 
}
table td, table th {
  color: #A3A3A3; 
}
tr.odd, tr.even {
 border: 1px solid #222; 
}
tr.odd {
  background: #343434;
}
tr.even {
  background: #414141; 
}
tr td:last-child {
  border-right: none;
}
a {
  color: #0093ED;
}
.marker {
  color: #C75A5E;  
 }
input.form-submit, a.button {
  border: 1px solid #222;
  background: none;
  background-color: #303030;
  -webkit-border-radius: 0px;
  border-radius: 0px;
  color: #D3D3D3;
}
input.form-submit:active {
  background: #303030;
  border-color: #565656;
}

ul.admin-list li {
  border-top: 1px solid #222;
  background: none;
  padding-left: 5px;
}
div.admin-panel {
  background: #414141; 
  border: 1px solid #222;
}
ul.tips, div.description, .form-item div.description {
  color: #C5C5C5;
}
.views-displays {
   border: 1px solid #222; 
}
.views-display-top {
  background-color: #252525;
}
.views-ui-display-tab-bucket .views-display-setting {
  color: #EFEFEF;
}

.views-ui-display-tab-bucket .even {
  background-color: #383838;         
 }
.views-ui-display-tab-bucket {
  border: 1px solid #222;
}
#views-preview-wrapper {
  border: 1px solid #222;    
}
#views-live-preview .preview-section {
  border: 1px dashed #222;
}
.views-query-info table {
   border-color: #222;   
}
.views-display-column > .ctools-collapsible-handle {
  border-color: #222;        
}
.views-display-top {
  border-bottom: none;        
}
#panels-dnd-main div.panel-region {
  border: 1px solid #222;
  background: #3D3D3D;
  -webkit-border-radius: 0;
}
.panel-portlet {
  background: none;
  border: 1px solid #222;
}
.panel-pane.hidden-pane {
  background: #2c2c2c;
}
.panel-portlet.hidden-pane .grab-title {
  background-color: #323232;  
}
.panel-portlet .grab-title {
  background: none;
 }
     
#panels-dnd-main .panel-pane .pane-title:after {
 color: rgb(229, 106, 130); 
}
ul.secondary {
 background-color: none;
 background: none;            
}
#panels-dnd-main div.panel-region h2.label {
 color: #E4E4E4;
 text-shadow: none;
}
div.ctools-modal-content {
  background:rgb(69, 69, 69);
  color: #fff;
  border: 1px solid #222;
}
       
div.ctools-modal-content .modal-title{
  color: #fff;
  text-shadow: none;    
}
 div.ctools-modal-content .modal-header {
  background: #222;           
}
.node-unpublished {
  background-color: #181818;              
}
tr.dropbox-entry.odd{
 background-color: none;    
}

#module-filter-squeeze {
  background-color: #272727;
  border: 1px solid #222;  
}
#module-filter-left ul li a span.counts {
  color: #fff; 
}
#module-filter-left ul li.active a {
  background-color: #353535;
  color: #FFFFFF;
}
#module-filter-left ul li {
  background: #303030 none repeat scroll 0 0;
  border-bottom: 1px solid #222;
  
}
#module-filter-left {
  border: 1px solid #222;
  background: #222;
}
#module-filter-left ul li.active a,
#module-filter-left ul li.active a:hover {
  background-color: #222;
}
#module-filter-left ul li a:hover {
  background-color: #222;

}

div.warning {
  border-color: rgba(238, 221, 85, 0.44);
}
div.warning a, div.error a {
  color: #919191 !important;
}
div.warning, table tr.warning {
  color: #FFCE0C;
}
div.error, table tr.error, div.warning, table tr.warning, div.status, table tr.status {
  background-color: #222;
}
div.status {
  color: #A4E563;
}
div.error {
 border-color: rgba(237, 29, 29, 0.49);
 color: #E70606;
}
span.admin-disabled {
  color: #BA4A4A;
}
div.admin-requirements, div.admin-required {
  color: #7B7B7B;
}
ul.primary li a:link, ul.primary li a.active, ul.primary li a:active, ul.primary li a:visited, ul.primary li a:hover, ul.primary li.active a {
  -moz-border-radius: 0;
  -webkit-border-top-left-radius:0;
  -webkit-border-top-right-radius: 0; 
   border-radius: 0;
   background-color: #4A4A4A;
   color: #DDD;
   border-color: #222;
}
ul.primary li.active a, ul.primary li.active a.active, ul.primary li.active a:active, ul.primary li.active a:visited {
  background-color: #303030;
  border-color: #222; 
}
ul.primary li.active a:hover {
   color: #FFF;
}
ul.primary li a:hover {
   color: #FFF;
  background: #303030;
}
.horizontal-tabs ul.horizontal-tabs-list li:hover, .horizontal-tabs ul.horizontal-tabs-list li:focus {
  background-color: none;  
}
.horizontal-tabs ul.horizontal-tabs-list {
   background-color: #424242;  
   border: none;
}
.horizontal-tabs ul.horizontal-tabs-list li:hover, .horizontal-tabs ul.horizontal-tabs-list li:focus {
  background-color: #424242;  
}
div.horizontal-tabs {
 border: 1px solid #222;    
}
.horizontal-tabs ul.horizontal-tabs-list li a:hover {
  background-color: #303030;  
}
.horizontal-tabs ul.horizontal-tabs-list li.selected {
  background-color: #222;
  color:#fff;
}
.horizontal-tabs ul.horizontal-tabs-list .selected strong{
  color:#fff;
}
.horizontal-tabs ul.horizontal-tabs-list li {
 background: #4A4A4A;
 border-right: 1px solid #222;         
}
div.vertical-tabs {
 background: #3C3C3C;
 border: 1px solid #222;
}
div.vertical-tabs .vertical-tabs-list {
  border: none; 
}
div.vertical-tabs ul li.vertical-tab-button a {
  border-color: #222; 
}
div.vertical-tabs ul li.vertical-tab-button {
  border-right: 1px solid #222;
}
div.vertical-tabs ul li.selected a, div.vertical-tabs ul li.selected a:hover, div.vertical-tabs ul li.selected a:focus, div.vertical-tabs ul li.selected a:active {
  background: #3C3C3C;
  border-top: 1px solid #222;
}
div.vertical-tabs ul li.vertical-tab-button.selected {
  border-right: none; 
}
div.vertical-tabs ul li.vertical-tab-button a:hover {
  background: #303030;
}

#page-manager-edit .page-manager-ajax-pad {
  background: none; 
}
#page-manager-edit .page-manager-ajax-pad .content-title {
  background-color: #2C2C2C;
  color: #fff;
  border-bottom: 1px solid #222;
}
#page-manager-edit .page-manager-tabs {
    border: 1px solid #222;
}
#page-manager-edit table tr.even {
  background: none; 
}
.handler-summary .handler-title {
  color: #fff;
  background: #2C2C2C;
  border: 1px solid #222; 
}
#page-manager-edit .page-manager-edit-operations li.active {
  border: 1px solid #222; 
}
#page-manager-edit .page-manager-edit-operations li.active, #page-manager-edit .page-manager-edit-operations li.active-group .page-manager-group-title, #page-manager-edit .page-manager-edit-operations li.active a, #page-manager-edit .page-manager-edit-operations li.active a:hover  {
   background: #303030;
   border: none;
   color: #fff;
}
#page-manager-edit .page-manager-edit-operations li {
  background: #343434;
  border-top: 1px solid #222;
  border-left: 1px solid #222;
  border-right: 1px solid #222;
  color: #fff;
}
#page-manager-edit .page-manager-edit-operations li li.operation-first {
  border: none; 
}
#page-manager-edit .page-manager-edit-operations li a{ 
  color: #fff; 
}
#page-manager-edit .page-manager-edit-operations .inside {
  border: none; 
}
#page-manager-edit .page-manager-edit-operations li a:hover {
  background: none; 
}
.panels-add-content-modal {
   background: #4c4c4c; 
  }
.panels-section-column-categories .panels-categories-box{
    border-top: 1px solid #222;
}
 .panels-add-content-modal .panels-modal-add-category {
 border-bottom: 1px solid #222;
 }
.panels-modal-add-category {
   color: #FFFFFF !important;        
}
.panels-add-content-modal .panels-modal-add-category.active{
  background-color: rgb(119, 119, 119);
}
.panels-section-columns {
  background-color: rgb(119, 119, 119);
  border-top: 1px solid #222;
}
.content-type-button img {
   border: none; 
}
.panels-section-column .content-type-button {
  padding: 5px 0 5px 0; 
}
.panels-section-column .content-type-button a{
  color: #fff; 
}
li.krumo-child {
color: #4D90C0;
}
fieldset fieldset {
background-color: #616161;
}
fieldset fieldset fieldset {
background-color: #3F3F3F;
}
.page-user #block-system-main, .node-type-webform .block-system-main form {
  background: linear-gradient(to bottom,rgba(40, 40, 40, 0) 0%,#606060 100%) !important;
}
#edu_search_wrapper, #edu_results_wrapper {
 color: #000; 
}
}
