}

._5vb_.hasLeftCol #leftCol {
background: rgba(0, 0, 0, 0.57);
}
 
._bui ._5afe .linkWrap {
display: block;
max-width: 116px;
overflow: hidden;
padding-left: 0;
text-overflow: ellipsis;
white-space: nowrap;
color: rgb(29, 161, 255);
