css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* font */
*{ font-family: 'Helvetica Neue', HelveticaNeue, Helvetica, Arial, sans-serif !important; 
color: rgb(128,128,128) !important; }

/* remove backgrounds */

/* left col */
.identity-refresh .l-container.l-container--two-column .l-content, 
.identity-refresh .l-container.l-container--two-column-dashboard .l-content, 
CSS
.identity-refresh .l-container.l-container--two-column-dashboard .left_column,
/* right col */
.identity-refresh .l-container.l-container--two-column .right_column, 
.identity-refresh .l-container.l-container--two-column-dashboard .right_column,
.l-container.l-container--two-column .right_column, 
.l-container.l-container--two-column-dashboard .right_column,
.identity-refresh .l-container.l-container--two-column-dashboard .left_column,
.post.new_post,
.identity-refresh .post_avatar .post_avatar_link,
.identity-refresh .post_avatar,
.post.post_full,
.post_full.is_conversation .conversation_lines .chat_line:nth-child(even),
.post_full.is_conversation .conversation_lines .chat_line:nth-child(odd),
.drawer,
.keycommand-guide,
.identity-refresh .right_column:after { 
  background:none; 
}


/* re-apply backgrounds in neue style */
body,
.l-header-container, 
.l-header-container--refresh { 
  background:#333 url(https://i.imgur.com/d54UGPl.png) left top repeat !important; 
}

#dashboard_iframe {
  background:none !important;
}

/* compose button */
.compose-button {
  background:#ccc;
  border-color:#ccc;
}
.compose-button:hover {
  background:#fff;
  border-color:#fff;
}

/* prev-next links when endless scroll is off */
#dashboard_controls,
#dashboard_controls a, 
#dashboard_controls .control,
#pagination a {
  color:#999 !important; 
  text-shadow:none !important;
}
#dashboard_controls a:hover,
#pagination a:hover {
  color:#fff !important;
}

/* selection nipple */
.identity-refresh .post_full .post_permalink {
  border-color:#333 #333 transparent transparent;
}

/* right col hover *
.identity-refresh .controls_section li a:hover {
  background:#999;
}

/* suggested follows */
.follow_list_item_blog:before,
.radar_avatar:before { 
  background:transparent !important; 
  border-right:none !important; 
}

.identity-refresh .right_column .follow {
  color:#999;
}

.identity-refresh .controls_section li:not(.section_header):hover {
  color:#fff;
  background:rgba(255,255,255,0.1);
}

/* hide recommended tumblogs and radar */
.recommended_tumblelogs,
.controls_section_radar,
#tumblr_radar {
  display:none;
}

/* extra stuff */

.post_content.clearfix, .post_content.clearfix {
	background-color: transparent !important;
}

.glass.blue, #hello_glass.blue, #glass_overlay.blue {
    background: rgba(10,10,10,.90);
}

.identity-refresh .plexi.show.color {
    background-color: rgba(10,10,10,.90);
}

.post_full .post_body pre {
    background-color: transparent;
}

.post_full.is_note .post_body .note_item, .post-composer_note-post .note_item {
  	background-color: transparent;
}
        
.post_full.is_note .nipple, .post_full.is_note .nipple::after, .post-composer_note-post .nipple, .post-composer_note-post .nipple::after {
    right: -9px;
}

.identity-refresh .ui_peepr_glass {
	background-color: rgba(10,10,10,.90) !important;
}
    
.ui_peepr_glass {
    background: transparent;
}

.keycommand-guide .keycommand-table .shortcut-body .shortcut-row {
  	border-top: 0px !important;
}
      
/* icon colors fix */

.new_post_label i.icon_post_text {
    color: #444 !important;
}

.new_post_label i.icon_post_photo {
    color: #d95c40 !important;
}

.new_post_label i.icon_post_quote {
    color: #f2992e !important;
}

.new_post_label i.icon_post_link {
    color: #56bc8a !important;
}

.new_post_label i.icon_post_chat {
    color: #529ccc !important;
}

.new_post_label i.icon_post_audio {
    color: #a77dc2 !important;
}

.new_post_label i.icon_post_video {
    color: #748089 !important;
}
}
