css:
@-moz-document url-prefix("https://play.google.com/music/listen") {

	/* Layout */

	body {
		font-family: "Roboto",arial,sans-serif,"Meiryo UI","Microsoft Yahei UI","Microsoft JhengHei UI" !important;
	}

	.music-banner-title:before {
		content: "Google Play " !important;
	}

	#nav-container {
		width: 260px !important;
	}

	#content-container {
		margin-left: 260px !important;
	}

	.music-banner {
		width: 260px !important;
	}

	#gbqfb,
	#gbw,
	#gba,
	#gbwa,
	#gb > div:first-child > div:first-child,
	.button[data-id=upload-music] ,
	#gbq1 {
		display: none !important;
	}

	#gb > div:first-child > div:last-child {
		width: 1px !important;
		height: 1px !important;
	}

	#oneGoogleWrapper,
	.gb_f,
	.gb_cb,
	.gb_g.gb_Ta,
	.gb_j {
		height: 0px !important;
	}

	.gb_e,
	#gb,
	#gbq {
		height: 0px !important;
		top: 0px !important;
		width: 0px !important;
	}

	#gbq2 {
		position: fixed !important;
		top: 48px !important;
		left: 0 !important;
		padding: 20px 2px 10px 30px !important;
		margin-left: 0 !important;
		z-index: 9999 !important;
		width: 228px !important;
		height: 32px !important;
	}

	#nav {
		padding-top: 56px !important;
		width: 260px !important;
	}

	#gbqf {
		width: 188px !important;
	}

	#gbqfbw {
		display: inline-block !important;
	}

	.gbqfqw {
		border: 1px solid #cdcdcd !important;
	}

	a[title="Last.fm Settings"] {
		font-size: 0px;
	}

	#butter_bar {
		top: 32px !important;
	}

	/* Making it Blue! */

	.music-banner,
	.situations-background,
	.button.primary,
	.simple-dialog-buttons button.goog-buttonset-default,
	#volume_slider,
	#time_container_current,
	#slider .played-progress,
	#current-loading-progress
	{
		background-color: #007ee8 !important;
	}

	.button.primary:focus,
	.button.primary:hover,
	.simple-dialog-buttons button.goog-buttonset-default:hover,
	.simple-dialog-buttons button.goog-buttonset-default:focus
	{
		background-color: #004985 !important;
	}

	a.primary,
	#nav_collections .nav-item-container.selected,
	.nav-item-container.selected {
		color: #007ee8 !important;
	}

	.header-tab-title.selected {
		border-color: #007ee8 !important;
	}

	.cluster.has-next-page:hover .lane-button,
	#player .flat-button,
	#player .rating-container .selected,
	.goog-slider-track .goog-slider-thumb
	{
		background-image: url(http://www.ametatsu.com/misc/gmusic-sprites-blue.png) !important;
	}

}
