css:
@-moz-document domain("e-kg.pl"), domain("www.e-kg.pl") {
#pasekdolny,#reklamytop,.artbannersxtd_left,.artbannersxtd.center,.artbannersxtd,#ads_pod_menu,.artbannersxtd_top,#dropin,.artbannersxtd_right,.moduletable_news_region,
.artbannersxtd_banner_right_r,#reklamystopka,.moduletable_partnerzy,.moduletable_plakat,#modules-top {
  display:none;
}

#page_bg {
    background-color: white;
    background:url("http://untroubled.org/backgrounds/gray/bg3.jpg");
}
  
#container2 {
    background-color: white;
}
}

@-moz-document url("http://www.e-kg.pl/index.php") {
#right .moduletable, #left .moduletable {
  	display:none;
}

.moduletable_slideshow {
  	transform: translateY(10px);
  	border-bottom: none;
}

.moduletable_news_kolobrzeg {
	border-top: none;
}

#left,#right {
  	margin-top: -10px;
}

.moduletable_wlasne h3 {
  	margin: 0;
}
  
#left {
   transform: translateX(715px);
}
  
#right {
   transform: translateX(-300px);
}
}
