css:
@-moz-document url-prefix("https://play.google.com/music/listen") {

	/* Layout */

	body {
		font-family: "Roboto",arial,sans-serif,"Meiryo UI","Microsoft Yahei UI","Microsoft JhengHei UI" !important;
	}

	.music-banner-title:before {
		content: "Google Play " !important;
	}

	#nav-container {
		width: 260px !important;
	}

	#content-container {
		margin-left: 260px !important;
	}

	.music-banner {
		width: 260px !important;
	}

	#gbqfb,
	#gbw,
	#gba,
	#gbwa,
	#gb > div:first-child > div:first-child,
	.button[data-id=upload-music] ,
	#gbq1 {
		display: none !important;
	}

	#gb > div:first-child > div:last-child {
		width: 1px !important;
		height: 1px !important;
	}

	#oneGoogleWrapper,
	.gb_f,
	.gb_cb,
	.gb_g.gb_Ta,
	.gb_j {
		height: 0px !important;
	}

	.gb_e,
	#gb,
	#gbq {
		height: 0px !important;
		top: 0px !important;
		width: 0px !important;
	}

	#gbq2 {
		position: fixed !important;
		top: 48px !important;
		left: 0 !important;
		padding: 20px 2px 10px 30px !important;
		margin-left: 0 !important;
		z-index: 9999 !important;
		width: 228px !important;
		height: 32px !important;
	}

	#nav {
		padding-top: 56px !important;
		width: 260px !important;
	}

	#gbqf {
		width: 188px !important;
	}

	#gbqfbw {
		display: inline-block !important;
	}

	.gbqfqw {
		border: 1px solid #cdcdcd !important;
	}

	a[title="Last.fm Settings"] {
		font-size: 0px;
	}

	#butter_bar {
		top: 32px !important;
	}

	/* Making it Blue! */

	.music-banner,
	.situations-background,
	.button.primary,
	.simple-dialog-buttons button.goog-buttonset-default,
	#volume_slider,
	#time_container_current,
	#slider .played-progress,
	#current-loading-progress
	{
		background-color: #007ee8 !important;
	}

	.button.primary:focus,
	.button.primary:hover,
	.simple-dialog-buttons button.goog-buttonset-default:hover,
	.simple-dialog-buttons button.goog-buttonset-default:focus
	{
		background-color: #004985 !important;
	}

	a.primary,
	#nav_collections .nav-item-container.selected,
	.nav-item-container.selected {
		color: #007ee8 !important;
	}

	.header-tab-title.selected {
		border-color: #007ee8 !important;
	}

	.cluster.has-next-page:hover .lane-button,
	#player .flat-button,
	#player .rating-container .selected,
	.goog-slider-track .goog-slider-thumb
	{
		background-image: url(http://www.ametatsu.com/misc/gmusic-sprites-blue.png) !important;
	}

	/* Turn it BLACK! */

	body,
	#music-content,
	.simple-dialog-bg,
	.zoomable-image-dialog-bg,
	.song-row td,
	.upload-progress-row td,
	.gbqfqw,
	.read-more-button-wrapper.fade-out,
	.goog-menu,
	.now-playing-menu .goog-menuitem,
	.now-playing-menu .goog-submenu,
	.now-playing-menu .goog-submenu .goog-submenu-arrow,
	.goog-menuitem,
	#top_divider_line,
	#loading-progress,
	.transfer-status-indicator
	{
		background-color: #333333 !important;
	}

	.selected-song-row.song-row td {
		background-color: #282828 !important;
	}

	.song-row:hover td,
	.upload-progress-row:hover td
	{
		background-color: #202020 !important;
	}

	.song-table th:hover {
		background-color: #303030 !important;
	}

	.nav-bar,
	#breadcrumbs,
	#nav,
	.card,
	#player,
	.now-playing-menu-wrapper,
	#player [data-id="now-playing-menu"]:active,
	.player-middle
	{
		background-color: #191919 !important;
	}

	#nav_collections .nav-item-container:focus,
	.nav-item-container:focus,
	.nav-item-container:hover,
	.nav-item-container:hover .playlistDropDown,
	.nav-item-container:hover .playlistDropDown:hover,
	.nav-item-container:focus .playlistDropDown,
	.nav-item-container:focus .playlistDropDown:hover,
	.nav-item-container.menu-displayed .playlistDropDown,
	#main #player .flat-button:focus, #player .flat-button:hover,
	#volume:hover, #volume.simulated-hover,
	#main #player .rating-container.thumbs li.selected:hover,
	#main #player .rating-container.thumbs li:hover,
	#headerBar,
	#main #player .flat-button:focus,
	#main #player .flat-button:hover,
	.nav-section-divider,
	.gbqfqw
	{
		background-color: #222222 !important;
	}

	.situations-background,
	.playing-progress-background,
	.playlist-view .editable:hover,
	.header-tab-spacer,
	#time_container_duration
	{
		background-color: #444444 !important;
	}

	#loading-overlay[data-type="full-loading-overlay"],
	#loading-overlay[data-type="regular-loading-overlay"],
	.button,
	.simple-dialog-buttons button
	{
		background-color: #555555 !important;
	}

		.song-table th {
		border-color: #666666 !important;
	}

	.button:hover,
	.button:focus,
	.simple-dialog-buttons button:hover,
	.simple-dialog-buttons button:focus {
		background-color: #999999 !important;
	}

	#headerBar, .nav-section-divider,
	.gbqfqw,
	.transfer-status-indicator
	{
		border-color: #222222 !important;
	}

	.goog-menu,
	#player [data-id="now-playing-menu"]:focus,
	#player [data-id="now-playing-menu"]:hover,
	.now-playing-menu.goog-menu, .goog-menu,
	.song-row td,
	.upload-progress-row td,
	.song-row .hover-button[data-id="menu"]:hover,
	.card .reason
	{
		border-color: #444444 !important;
	}

	#player [data-id="now-playing-menu"] {
		border-color: #191919 !important;
	}

	body,
	#headerBar .tab-text,
	#nav_collections .nav-item-container,
	.nav-item-container,
	.recent-container .title,
	.recommended-header,
	.card .title,
	.card[data-is-listen-now="true"] .details *,
	.song-table th,
	.song-row.currently-playing td[data-col="title"],
	.song-row td, .upload-progress-row td,
	#gbqfq,
	.cluster .header .title,
	.settings-cluster .header .title,
	.now-playing-menu .goog-menuitem .goog-menuitem-content,
	.goog-menuitem-content,
	#playerSongTitle,
	.button,
	.simple-dialog-buttons button,
	a,
	.simple-dialog a,
	.transfer-status-indicator
	{
		color: #aaaaaa !important;
	}

	.goog-menuitem-highlight .goog-menuitem-content .goog-submenu-arrow,
	.goog-menuitem-highlight .goog-menuitem-content,
	.header-tab-title.selected {
		color: #eeeeee !important;
	}

	.situations-container .cluster .header .title {
		color: #ffffff !important;
	}

	.fade-out:after
	{
		background: linear-gradient(to right, rgba(25, 25, 25, 0), rgba(25, 25, 25, 1)) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
	}

	.fade-out.gray:after,
	.song-row .fade-out:after
	{
		background: linear-gradient(to right, rgba(51, 51, 51, 0), rgba(51, 51, 51, 1)) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
	}

	.selected-song-row.song-row .fade-out:after
	{
		background: linear-gradient(to right, rgba(40, 40, 40, 0), rgba(40, 40, 40, 1)) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
	}

	.selected-song-row.song-row:hover .fade-out:after,
	.song-row:hover .fade-out:after,
	.nav-item-container:focus .fade-out:after,
	.nav-item-container:hover .fade-out:after
	{
		background: linear-gradient(to right, rgba(34, 34, 34, 0), rgba(34, 34, 34, 1)) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
	}

	#butter_bar .fade-out:after
	{
		background: linear-gradient(to right, rgba(247, 239, 162, 0), rgba(247, 239, 162, 1)) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
	}

	.song-row .song-indicator[data-playback-status="playing"]  {
		background-image: url(http://www.ametatsu.com/misc/gmusic-equalizer-ani-white.gif) !important;
		background-size: 50% !important;
	}

	.song-row .song-indicator[data-playback-status="loading"]  {
		background-image: url(http://www.ametatsu.com/misc/gmusic-loading-ani-white.gif) !important;
		background-repeat: no-repeat !important;
		background-size: 60% !important;
		background-position: center center !important;
	}

	#loading-overlay[data-type="full-loading-overlay"], #loading-overlay[data-type="regular-loading-overlay"] {
		background-image: url(http://www.ametatsu.com/misc/gmusic-loading-ani-white.gif) !important;
	}

	.cluster.has-next-page:hover .lane-button,
	#player .flat-button,
	#player .rating-container .selected,
	.goog-slider-track .goog-slider-thumb,
	.song-row [data-col="index"] .hover-button[data-id="play"],
	.song-row [data-col="track"] .hover-button[data-id="play"],
	.song-row .song-indicator[data-playback-status="paused"],
	.header-row  .icon
	{
		background-image: url(http://www.ametatsu.com/misc/gmusic-sprites-blue-black.png) !important;
	}

	#embed-container {
		z-index: 0 !important;
	}

}
