css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goodgame.ru") {
/*Главная*/
	#streams, #streams .content {background-color: #cfcfcf!important;}
	#content {background-color: #cfcfcf!important;}
	.description  {background-color: #919191!important;}
	.block-content.padded {background-color: #2f3336!important;}
	.author-block {color:#2f3336!important}
	.tabs li a {color:#2f3336!important}
	.block-broadcasts tr.even {background-color: #919191!important;}
	.block-broadcasts tr.odd {background-color: #808080!important;}
	.block-broadcasts tr.date {background-color: #282828!important;}
	.row {background-color: #282828!important;}
	.news-block-list {background-color: #282828!important;}
	div.block div.header h2 {color:#000!important}
/*Видео*/
	#videos, #videos .content {background-color: #cfcfcf!important;}
	.content_head {background-color: #2f3336!important;}
	.video_list {background-color: #919191!important;}
	.video_autor {background-color: #2f3336!important;}
/*Личные сообщения*/
	.message-wrap, .message-block, .message-switcher, .switch {background-color: #ededed!important}
	.message-table tbody tr {background-color:#cfcfcf!important}
	.switch.active {background-color: #cfcfcf!important}
	.text a {color:#282828!important}
	.dialog-wrap {background-color: #ededed!important}
	.dialog-list, .dialog-block {background-color: #cfcfcf!important}
	.label {color:#000!important}
/*Форум*/
	#comments #bestc_wrapper .comment {background-color: #ededed!important;border: 1px solid #D9A135!important}
	#posts #bestc_wrapper .comment {border: 1px solid #D9A135!important}
	#comments h2 {color:#282828!important}
	#comments .comment, .inner {color:#282828!important;background-color: #ededed!important;}
	#posts .comment {color:#282828!important;background-color: #ededed!important;}
	#posts h2, .text {color:#282828!important}
	.cquote {background-color:#cfcfcf!important}
	.mods {background-color:#cfcfcf!important}
/*Верхнее меню сайта*/
	#menu, #top, .content {background-color:#2f3336!important;}
  #search {color:#ededed!important;background-color:#2f3336!important;border: 1px solid #000!important}
/*Страница трансляций*/
	#channels-tab {background-color: #ededed!important;color:#000!important}
	#favorite-games-wrap,.arrow.left,.arrow.right {background-color:#2f3336!important;}
	#footer-top {background-color:#2f3336!important;}
	.full {background-color: #cfcfcf!important;}
	.stream-name {color:#000!important}
/*Страница со стримом*/
	#channel, #channel .content {background-color:#cfcfcf!important;}
	.announce-wrap {background-color:#919191!important}
	.info-row {background:rgba(191,191,191,0.5)!important}
	.grad-block {display:none!important}
	.announce-text-wrap {background-color: #282828!important;}
/*Название стрима и игра*/
	#streams-header  h2, .content-name {color:#000!important;}
	.stream-title-separator {display:none !important}
/*Чат*/
	#chat,  #message-input, #messages {color:#000!important;background-color: #ededed!important}
	#chat .drag-handle {background-color:#2f3336!important}
	#chat .tab .tabinner {background-color: #c9c9c9!important;}
	#chat a.nick:hover, #chat a.nick:hover span {color:#2f3336!important}
	#smiles, #smiles-panel {background-color:#cfcfcf!important;}
	#chat #smiles:hover {background-color:#919191!important}
	#chat #content #messagesframe #messages .usermsg {color:#2f3336!important}
	#chat .hello, #chat .snipetext {color:#000!important;background-color:#f1bd13!important}
	#chat .streamer .nick {color:#7e7a00!important;}
	#messagesframe #messages div.msg.privat, div.msg.tome {color: #c79d34!important; background:rgba(255,210,28,0.3)!important;}
	.casual.donat3 .nick {color:#cdad00!important;}
	.casual.donat2 .nick {color:#8f8f8c!important;}
/*Профиль*/
	.block-left {background-color: #fcfcfc!important;color:#282828!important}
	span.icons {background-color:#2f3336!important;border: 1px solid #000!important}
	a.current {color:#000!important}
/*Разное*/
	#single, #help #results .block {background-color: #282828!important}
}
