css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tineye.com") {

/* ===== TinyEye Search - WideScreen Results v.5 (new5) - ===== */


/* (new3) - PUBS SUPP - === */
.shutterstock-label {
display: none !important;
}

/* (new5) WIDESCREEN */

footer {
    display: inline-block !important;
    float: none !important;
    width: 100% !important;
}
body {
    display: inline-block !important;
    float: none !important;
    width: 100% !important;
}
header {
    display: inline-block !important;
    float: none !important;
    height: 80px;
    width: 100% !important;
    background-color: #fff;
    float: none !important;
}
header .container {
    position: fixed !important;
    display: inline-block !important;
    float: none !important;
    clear: both;
    height: 70px !important;
    line-height: 5px !important;
    width: 100% !important;
    top: 0px !important;
    left: 0px !important;
    padding: 0 !important;
    z-index: 500 !important;
}
header  .container .row {
    display: inline-block !important;
    float: none !important;
    width: 100% !important;
    margin: 0 !important;
    background-color: #fff;
}

header .container .row .col-md-12  {
    display: inline-block !important;
    float: none !important;
    width: 100% !important;
    background: rgba(0, 0, 0, 0) linear-gradient(to left, #3c587a, #5177a6, #3c587a) repeat scroll 0 0 !important;
}
header .left-nav {
    position: absolute !important;
    display: inline-block !important;
    float: none !important;
    padding-top: 0px;
    width: 400px !important;
    height: 25px !important;
    line-height: 25px !important;
    top: 55px!important;
    padding: 0 !important;
    color: white !important;
    text-align: left !important;
    z-index: 200 !important;
}
header #products-nav, 
header #solutions, 
header #about, header #labs, 
header #search {
    display: inline-block !important;
    float: left;
    height: 25px !important;
    line-height: 25px !important;
    color: white !important;
    cursor: pointer;
    font-size: 15px;
    text-align: center;
    text-decoration: none;
}
header #login-menu {
    width: 225px;
    left: 123px !important;
    top: 16px !important;
}
header #account-nav {
    position: absolute !important;
    display: inline-block !important;
    float: left!important;
    width: 590px !important;
    height: 20px !important;
    line-height: 20px !important;
    top: 5px !important;
    left: 20px !important;
    text-align: left !important;
}
header #account-nav ul li a {
    color: white !important;
    color: #96a6b8 !important;
    font-size: 13px;
    text-decoration: none;
}
header #product-menu {
    left: 19px !important;
    top: 20px !important;
}

header #account-nav ul {
    position: absolute !important;
    float: right !important;
    height: 20px !important;
    margin-top: 0px !important;
    width: 590px !important;
    line-height: 20px !important;
    text-align: left !important;
}


/* (new5) SEARCH RESULTS - SEARCH FIELD */
body header + .search-container {
    position: fixed !important;
    display: inline-block !important;
    float: right !important;
    right: 0 !important;
    height: 55px !important;
    width: 50% !important;
    top: 4px !important;
    padding: 0 !important;
    border-radius: 5px 0 0 5px !important;
    z-index: 100;
    z-index: 500 !important;
background-color: #3c587a;
}
body header + .search-container .search {
    position: relative;
    float: left !important;
    height: 50px !important;
    width: 100% !important;
    border-radius: 5px 0 0 5px !important;
    color: #fff;
    z-index: 100;
background: rgba(0, 0, 0, 0) linear-gradient(to right, #3c587a, #5177a6, #3c587a) repeat scroll 0 0 !important;
}

body header + .search-container .row {
    float: left !important;
    height: 50px !important;
    width: 100% !important;
}
body header + .search-container .row >div:first-of-type {
    float: right !important;
    height: 55px !important;
    width: 100% !important;
}
body header + .search-container .search .forms {
    float: left !important;
    overflow: hidden;
    padding-top: 5px!important;
    position: relative;
    width: 100% !important;
    height: 45px !important;
}

.search-container .row >div:last-of-type {
    position: absolute;
    float: right !important;
    height: 40px !important;
    width: 40px !important;
    right: 0 !important;
    top: 4px !important;
}
.search-container .search .robot img {
    width: 30px !important;
    height: auto;
    left: 5px !important;
    top: 2px !important;
    z-index: 300;
}

/* SEARCH PAGE - SEARCH FIELD */
.bg  header + .search-container {
    position: relative !important;
    display: inline-block !important;
    float: none !important;
    width: 100% !important;
    height: auto !important;
    left: 0 !important;
    top: 0 !important;
    margin: auto !important;
    padding: 0;
    border-radius: 5px 0 0 5px;
    z-index: 50000 !important;

background-color: red !important;
}
.bg  header + .search-container .search {
    position: relative !important;
    display: inline-block !important;
    float: none !important;
    height: auto !important;

    width: 100%;
    z-index: 100;
}
.bg  header + .search-container .row:not(highlights) {
    position: relative;
    display: inline-block !important;
    float: none !important;
    height: auto !important;
    width: 100%;
}
.bg  header + .search-container .row.highlights {
    margin-top: 484px !important;
}
.bg  header + .search-container .row > div:first-of-type {
    float: none !important;
    height: auto !important;
    width: 100%;
    margin-top: -97px !important;
}
.bg  header + .search-container .search .forms {
    float: none !important;
    height: auto !important;
    overflow: hidden;
    padding-top: 5px;
    position: relative;
    width: 100%;
}

.products .preview-screenshot {
    top: 139px !important;
}

/* RESULT - LEFT PANEL */
.query-summary {
    position: fixed !important;
    display: inline-flex!important;
    float: left !important;
    height: 835px !important;
    width: 300px !important;
    padding-bottom: 30px;
    padding-top: 30px;
    border: 1px solid red !important;
z-index: 200 !important;
}
.query-summary .container {
    position: relative;
    width: 293px !important;
    padding: 5px !important;
}
.query-summary .container .row{
    margin-left: -20px !important;
color: white !important;
}
.query-summary .query-thumb {
    width: 293px !important;
    margin: 0 !important;
    text-align: center;
color: white !important;
}
.query-summary .search-details {
    width: 293px !important;
    padding: 5px !important;
    text-align: left !important;
color: white !important;
}
.query-summary p {
    color: #C4C4C4 !important;
    font-size: 14px;
    line-height: 1.4em;
    margin: 0;
    padding: 5px 0 0;
}
.query-summary .robot {
    position: absolute;
    height: 124px !important;
    width: 72px !important;
    right: 20px !important;
    bottom: -356px !important;
    padding: 65px !important;
    border-radius: 50% !important;
    background-position: center right !important;
    background-size: contain !important;
}

.without-query-src>a {
    color: #96a6b8 !important;
}
.query-summary .hidden-xs>a {
    color: tomato !important;
}
.search-source.hidden-xs>a  {
    display: inline-block !important;
    min-width: 269px !important;
    white-space: pre-wrap !important;
}
.search-details.col-md-10>h3>a {
    display: inline-block !important;
    width: 100% !important;
    min-width: 290px !important;
    max-width: 290px !important;
color: #39C9D0 !important;
}

/* (new5) RESULTS FILTER - === */
.domain_filter .tt-menu.tt-open {
    min-width: 100%;
    padding: 8px 0 !important;
    margin-top: -9px !important;
    border-radius: 2px 2px 5px 5px !important;
border: 2px solid red !important;
}
.domain_filter .tt-suggestion {
    display: inline-block !important;
    vertical-align: top !important;
    height: 35px !important;
    line-height: 10px !important;
    font-size: 12px !important;
}
.domain_filter .tt-suggestion .domain {
    display: inline-block !important;
    vertical-align: top !important;
    clear: none !important;
    float: none !important;
    height: 24px !important;
    line-height: 40px !important;
    font-size: 15px !important;
/* background: tan !important; */
}

.domain a ,
.domain_filter a {
    cursor: pointer;
    display: inline-block !important;
    width: 180px !important;
    height: 25px !important;
    line-height: 20px !important;
    margin-left: -3px !important;
    padding-left: 2px !important;
    font-size: 15px !important;
    overflow: hidden !important;
text-overflow: ellipsis !important;
/* border: 1px solid red !important; */
}

/* (new4) SIMMILLAR SIDEBAR - === */
#match-summary .sidebar.col-md-4 {
    position: absolute !important;
    position: fixed !important;
    height: 821px !important;
    width: 697px !important;
    right: -697px !important;
    padding-left: 0 !important;
    border-radius: 5px 0 0 5px !important;
    overflow: visible !important;
    transition: all ease 0.2s !important;
border: 1px solid red !important;
background: red !important;

}
#match-summary .sidebar.col-md-4:hover {
    right: 0px !important;
    z-index: 5000 !important;
    transition: all ease 0.7s !important;
background: black !important;
}

#match-summary .sidebar.col-md-4 .similar_matches {
    width: 690px !important;
    height: auto;
    max-height: 800px !important;
    padding-left: 30px !important;
    overflow: hidden !important;
    overflow-y: auto !important;
}
#match-summary .sidebar.col-md-4 h3 {
    position: absolute;
    min-width: 810px !important;
    display: inline-block;
    margin-top: 0;
    right: 303px !important;
    top: 395px !important;
    border-radius: 5px 5px 0 0 !important;
    text-align: center !important;
    transform: rotate(-90deg) !important;
color: gold !important;
border: 1px solid yellow !important;
background: black !important;
}
.sidebar a.promo {
color: gold !important;
}
.sidebar .stock-label {
    position: absolute;
    width: 100px !important;
    left: -65px !important;
    top: 48px !important;
    padding: 2px 5px !important;
    border-radius: 0 5px 0 0 !important;
    text-align: right !important;
    overflow: hidden;
    transform: rotate(-90deg) !important;
}

/* RESULTS - THUMBNAIL */
.results .col-md-push-8 {
    display: none !important;
}
.results:not(.show-all) {
    position: relative !important;
    display: inline-block !important;
    float: right !important;
    clear: none !important;
    width: 1580px !important;
    min-height: 800px !important;
    left: 750px !important;
}
.results:not(.show-all) .container {
    position: relative !important;
    display: inline-block !important;
    width: 100% !important;
    left: 0px !important;
}
.results:not(.show-all) .container > .row {
    width: 100% !important;
    width: 1580px !important;
}

.col-sm-12.col-md-8.col-md-pull-4 {
    position: relative !important;
    width: 1562px !important;
}

.row.hidden-xs {
    display: none !important;
}
.results .col-md-8 {
    right: 0 !important;
}

.results .match-row:first-of-type ,
.row.match-row {
    display: inline-block !important;
    float: left !important;
    clear: none !important;
    width: 300px !important;
    min-height: 300px !important;
    max-height: 300px !important;
    margin-right: 5px !important;
    margin-bottom: 5px !important;
    padding: 5px !important;
    border-radius: 3px !important;
border: 1px solid red !important;
}
.results .match-row:first-of-type {
    margin-top: 0px !important;
}

/* (new5) COLLECTION cf SEARCH JOCONDE */
.row.match-row {
    display: inline-block !important;
    float: left !important;
    clear: none !important;
    width: 300px !important;
    min-height: 300px !important;
    max-height: 300px !important;
    margin-right: 5px !important;
    margin-bottom: 5px !important;
    padding: 5px !important;
    border-radius: 3px !important;
    overflow: hidden !important;
background: rgba(0, 0, 0, 0) linear-gradient(to top, #3c587a, #5177a6, #3c587a) repeat scroll 0 0 !important;
/* background: red !important; */
}
.row.match-row .match-thumb  {
    height: 200px !important;
    width: 100% !important;
    min-width: 287px !important;
    max-width: 287px !important;
    margin-right: 0 !important;
    padding-left: 0;
    padding-right: 0px !important;
    text-align: center;
}
.results .match-thumb p span {
    white-space: nowrap;
color: #99D1D2 !important;
}
.row.match-row .match-details  {
    position: relative !important;
    display: inline-block !important;
    height: 120px !important;
    width: 100% !important;
    max-width: 287px !important;
    min-width: 287px !important;
    margin-top: -31px !important;
    padding-left: 0 !important;
    padding-top: 5px !important;
    padding-right: 0 !important;
    overflow: hidden !important;
    overflow-y: auto !important;
    transition: all ease 0.7s !important;
border: 1px solid gray !important;
background: rgba(0, 0, 0, 0) linear-gradient(to top, #3c587a, #294567, #3c587a) repeat scroll 0 0 !important;
}
.row.match-row:hover .match-details  {
    height: 242px !important;
    margin-top: -105px !important;
    border: 1px solid gray !important;
    padding-top: 30px !important;
    padding-bottom: 100px !important;
    overflow: hidden !important;
    overflow-y: auto !important;
    transition: all ease 0.7s !important;
background: rgba(0, 0, 0, 0) linear-gradient(to top, #3c587a, #294567, #3c587a) repeat scroll 0 0 !important;
}

.results:not(.show-all) .match {
    font-size: 13px;
    font-weight: 400;
    min-width: 285px !important;
    padding-bottom: 0px !important;
}
.results .match h4 {
    display: inline-block !important;
    min-width: 270px !important;
    max-width: 270px !important;
    height: 22px !important;
    line-height: 22px !important;
    margin-bottom: 3px !important;
    font-size: 16px;
    color: #39c9d0 !important;
    text-align: center !important;
}

.row.match-row .match-details .hidden-xs {
    position: relative !important;
    display: inline-block !important;
    width: 287px !important;
    margin-top: 5px !important;
    margin-bottom: 3px !important;
    transition: all ease 0.7s !important;
}

.results:not(.show-all) .match p {
    display: inline-block !important;
    width: 287px !important;
    line-height: 15px !important;
color: #EFFAFF !important;
}
.results .match > p:not(.short-image-link) > a  {
    display: inline-block !important;
    float: right !important;
    min-width: 287px !important;
    max-width: 287px !important;
    height: 22px !important;
    line-height: 22px !important;
    margin-right: 0px !important;
    padding: 0px 2px !important;
    font-size: 14px !important;
    text-align: left !important;
    color: #99d1d2  !important;
    white-space: nowrap !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
}

/* PAGES LINK */
.crawl-date + p span ,
.short-image-link.visible-xs + p span {
    color: #99d1d2 !important;
    pointer-events: none !important;
}
.crawl-date + p span + a ,
.short-image-link.visible-xs + p span + a {
    margin-top: -21px !important;
    color: #99d1d2 !important;
    z-index: 100 !important;
}
.crawl-date + p span + a:hover:before ,
.short-image-link.visible-xs + p span + a:hover:before  {
    position: absolute !important;
    content: attr(title) !important;
    min-width: 247px !important;
    max-width: 247px !important;
    right: 7px !important;
    margin-top: -15px !important;
    color:gold !important;
    white-space: nowrap !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
}

/* (new5) LINK VIEW IMAGE - === */
#match-summary .results.row .matches .row.match-row  .match .short-image-link.visible-xs {
    position: absolute !important;
    display: inline-block!important;
    height: 20px !important;
    width: 285px !important;
    top: -9px !important;
    left: 0 !important;
    padding: 0 3px !important;
    line-height: 15px !important;
    margin-bottom: 5px !important;
    text-align: center !important;
    visibility: hidden !important;
background: red !important;
}

#match-summary .results.row .matches .row.match-row:hover  .match .short-image-link.visible-xs {
    visibility: visible !important;
background: green !important;
}


/* (new5) COMPARE MATCH BUTTON - === */ 
.results .row.match-row .match-thumb >a>span {
    width: 100% !important;
    min-width: 285px !important;
    max-width: 285px !important;
    margin-bottom: 100px !important;
outline:  1px solid white !important;
}
.results .row.match-row .match-thumb p:last-of-type {
    margin-bottom: 20px !important;
/* outline:  1px solid white !important; */
}
.results .row.match-row .match-thumb p:last-of-type a {
    position: absolute !important;
    display: inline-block !important;
    min-width: 100px !important;
    height: 20px !important;
    line-height: 18px !important;
    top: 55px !important;
    right: -48px !important;
    padding: 0 5px !important;
    font-size: 14px !important;
    text-align: center;
    transform: rotate(90deg) !important;
background: #294567 !important;
}

/*(new5) COMPARE POPUP - === */
.compare-popup {
    position: fixed !important;
    width: 725px !important;
    min-height: 700px !important;
    line-height: 14px;
    top: 156px !important;
    left: 700px !important;
    padding: 10px;
    border: 10px solid #5d5e5c;
    border-radius: 18px;
    font-size: 12px;
    z-index: 100;
color: #777;
background-color: #333 ;
}
.compare-popup .overlay-detail {
    min-height: 600px !important;
    margin-bottom: 10px;
    border-radius: 8px;
border: 1px solid #e6ebf1;
background-color: #222 !important;
}


.row.match-row:nth-child(5) .slide-show .compare-popup {
    position: absolute !important;
    width: 325px !important;
    line-height: 14px;
    left: -20px !important;
    padding: 10px;
    border-radius: 18px;
    font-size: 12px;
    z-index: 100;
color: #777;
border: 10px solid #5d5e5c;
background-color: #f4f4f4;

}
.compare-popup .toggle .yourimage, 
.compare-popup .toggle .imagematch {
    float: left;
    width: 135px !important;
    height: 50px;
    line-height: 50px;
    padding: 0px !important;
    text-align: center !important;
border: 1px solid red !important;
}

.compare-popup .toggle h4 {
display: inline-block !important;
    width: 124px !important;
    height: 38px !important;
    line-height: 36px !important;
/* background: yellow !important; */
}


.compare-popup .toggle .imagematch.active-right {
    padding: 0px !important;
    text-align: center !important;
    background-color: gold !important;
}

.compare-popup .toggle .switch-active-right {
    margin-left: 1px !important;
    width: 108px !important;
    padding: 0px !important;
background-color: gold !important;
}

.compare-popup .toggle .switch-active-left {
    margin-right: 1px !important;
    width: 108px !important;
}
.compare-popup .toggle .active-left {
    padding: 0px !important;
background-color: green !important;
}

.compare-popup .toggle .switch {
    height: 40px;
    left: 43%;
    top: 5px;
    width: 120px;
    z-index: 120;
}

/* TEST - OVERLAY */
.overlay:hover {
/*     background-color: red !important; */
/* background-position: -211px center !important; */
/* transition: all ease 0.1s !important; */
}



/* (new5) COLOR */

/* TEXT  GRAY */
p ,
h1, 
h2, 
h3, 
h4, 
h5, 
h6 {
    color: gray !important;
}


/* TEXT WHITE */
.feature-content h1, .feature-content h2, .feature-content p {
    color: white !important;
}

/* TEXT + BACKGROUND DIVERS */
.pagination .current {
    background: red !important;
    color: gold !important;;
}

/* LINK */
.domain_filter .tt-suggestion ,
.domain_filter a ,
.results .match > p:not(.short-image-link) > a ,
a  {
color: peru !important;
}

.results .match > p:not(.short-image-link) > a:visited ,
a:visited  {
color: tomato !important;
}

/* BACKGROUND */

/* TOTAL */
body,
html {
    background: #1f2536 !important;
}

/* DARK A */
.tt-suggestion.tt-selectable:nth-child(odd) ,
.results .match-row:first-of-type ,
.row.match-row ,
.query-summary {
    background: #0f1126 !important;
}

/* DARK B */
.tt-suggestion.tt-selectable:nth-child(even) ,
.domain_filter .tt-menu,
.query-summary .robot ,
.results .match h4 {
    background-color: #1f2536 !important;
}

/* WHITE */
.col-md-8.col-md-offset-1.col-md-pull-2  .row.cool-search-result.clearfix {
    background-color: white !important;
}
/* IMAGE ARROW  */
.arrow img {
/*     filter: invert(1); */
    padding-left: 20px;
}

/* === END  ==== */


}
