css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(tweakers.net){
a:hover, a.highlightlink,a[rb-highlight] 
{
	box-shadow: none !important;
}


body,
.ScorecardView {
	background-color: rgba(12, 12, 12, .9) !important;
}
#imageOverlay,
#listing table.listing td {
	background: rgba(50, 50, 50, 1)!important;
}

#imageViewer,
html.js {
	background: rgb(35, 35, 35)!important;
}
.messagecontent .message-quote-div {
	Background: rgba(150, 150, 150, .5)
}



/*BG None*/
#searchbar,
.darkFpBlock,
.article table,
.autoPagerS,
#categoryBrowser,
.bar,
.searchAanbod #nearbyAdForm,
#jobs,
#filter,
.pageTabsContainer,
#entity,
.relatedBlock,
div.message,
#tracker .fakeTop,
#layout {
	background: none !important;
}



/*BG Transparent*/
#portalPlayerWrapper,
.ScoreCard,
.reactieBody.informative,
html.js {
	background-color: transparent !important;
}



/*Border none*/
#top,
.darkFpBlock,
.pageTabsContainer,
table.spec-detail td.spec-column,
#entity > div,
#ticker {
	border: 0 !important;
}


.thumb {
	border-radius: 5px;
}
.pageTabs li {
	border: 0;
	border-radius: 5px;
}
.userReviewDescription,
#poll,
#jobs,
#sponsored,
#loi,
#textlinks,
#newsletter,
h2.detailHeader,
h2.bar,
#reactieForm textarea,
a.fancyButton.grey:hover,
#adv,
.reactieBody,
.filterGroup,
.toggleable,
.toggleBtn {
	Background: rgb(48, 48, 48)!important;
	box-shadow: inset 0px 0px 2px 0px #000 !important;
	border: 1px!important;
}
#contentArea,
a.fancyButton,
span.fancyButton,
.fancyButton.clickOut,
.fancyButton.grey,
table.listing,
div.message {
	Background: rgba(30, 28, 30, .2)!important;
	box-shadow: 0px 0px 4px 0px #000 !important;
	border-top-color: rgba(255, 255, 255, .12) !important;
	border: 3px!important;
}
#forumheading .info,
#folders {
	Background: rgba(150, 150, 150, .1)!important;
	border-top-color: rgba(255, 255, 255, .12) !important;
	box-shadow: inset 0px 0px 4px 0.5px #000 !important;
	border: 3px!important;
}
table.listing.dm td.ind,
table.listing td.topic,
table.listing .poster,
table.listing .replies,
table.listing.dm .time,
table.listing .ind,
table.listing .time {
	Background: rgba(30, 28, 30, .2)!important;
	color: white!important;
	border: none;
	padding-left: 5px;
}

.filterOption.active,
div.filter.closed,
#keywordFilter,
#filterForm,
#specificationFilter_163,
#brandFilter,
#specificationFilter_783,
#specificationFilter_22,
#specificationFilter_5169,
#specificationFilter_5171,
#specificationFilter_1293,
#specificationFilter_1294,
#specificationFilter_1290 {
	Background: #2A2A2A!important;
	border: 0!important;
}
#search input.text,
#newsletter input.text {
	Background: rgba(51, 51, 51, 0.9);
	box-shadow: inset 0px 0px 1px 0.5px #000 !important;
	border-top-color: rgba(255, 255, 255, .12) !important;
	border: 3px!important;
	width: 480px;
	color: white !important;
}
.reactieBody.spotlight,
#filtercontainer,
#pmessages form dl dd textarea,
input#pmtitle {
	Background: rgba(130, 128, 130, .7)!important;
	box-shadow: inset 0px 0px 3px 1px black !important;
	border: none;
}
.reactieBody.informative,
#pmessages form,
#filterForm {
	Background: rgba(130, 128, 130, .2)!important;
	box-shadow: inset 0px 0px 3px 0.5px black !important;
}

#menubottombar,
.ScorecardView,
.textadContainer,
.userReview,
#b_ad,
.frontpage #community .userReview .userReviewDescription,
a img {
	background: rgba(49, 50, 50, .7);
}

#top {
	background: linear-gradient(transparent 44px, black 44px, black 67px, rgba(0, 0, 0, .5) 67px, rgba(50, 50, 50, .2) 50px, transparent 122px)!important;
}

#bottom {
	height: 0px
}


Hover kadera.fancyButton.grey.clickOut,
#poll label:hover {
	background: rgba(49, 50, 50, .5) !important;
	text-shadow: none;
}

#layout {
	padding-bottom: 50px !important;
}

#listingContainer {
	min-height: 100%;
}



/*Sidebar*/
.dropdown ul,
.dropdown li a,
.dropdown ul li,
.sortable li,
.sortable.specification .specName,
.sortable .specName,
div.sortable:nth-child(2) > div:nth-child(1) > span:nth-child(1) {
	background: rgba(50, 50, 50, 0.9);
	text-shadow: none!important;
	color: darkgrey !important;
}
#categoryBrowser .large li {
	background-color: rgba(50, 50, 50, 0.9);
	font-size: 14px;
	color: white;
	text-shadow: 0px 0px 1px black;
}
#tracker .fakeTop {
	border-bottom: 1px solid transparent;
}

/*forum*/
table.listing {
	background: rgba(50, 50, 50, 0.9);
	color: white!important;
	border: none!important;
}
.forumList table.listing *,
table.listing .tags,
layout *,
table.listing td.title a,
table.forumlisting p.title,
table.forumlisting > tbody > tr > td,
table.forumlisting .preview .content {
	background: rgba(50, 50, 50, 0.9);
	color: white!important;
	border: none!important;
}



div.forumList,
table.forumlisting {
	background: rgba(50, 50, 50, 1);
	color: white!important;
	text-shadow: 0px 0px 1px black!important;
}


/*Sidebar*/
[class="fpaImageContainer"]:not(:hover) {
	opacity: .6;
}
#categoryBrowser div,
#categoryBrowser .sublist,
[class="filterOption"],
.pageTabs li {
	background: rgba(255, 255, 255, .8) !important;
	color: white;
}



/*Text*/
#layout *,#categoryBar ul li a,
.editionOverview .specs *,
.entityHeader .subInfo,
.entityHeader .breadcrumb > li,
.entityHeader .breadcrumb > li a,
.pwlink,
.responsiveFilter,
table.listing td.topic a,
.toggleable,
#categoryBar li a {
	color: #e2e2e2;
	text-shadow: 0px 0px 0.5px black, 0px 0px 0.5px black, 0px 0px 0.5px black;
	font-size-adjust: 0.5;
}

a:visited,
#bestBuyGuides .description,
#adv a,
table.listing td.topic a,
table.listing td.title a,
a.pwlink {
	color: rgba(153, 153, 153, 0.8) !important;
	text-shadow: 0px 0px 0.5px #555!important;
}

#pricewatch li .fancyButton,#categoryBrowser .images li a,
#poll label:hover span,#header h1 .subtitle,
.commentCount,#categoryBar ul li,
.scoreButton,
a.fancyButton,
span.fancyButton,
.pageDistribution b,
.fancyButton,
.sortable.sortorder li a,
[id="overviewCtaButton"] *,
.sortable > ul > li > a {
	color: white!important;
	text-shadow: none !important;
}

#categoryBrowser li a,
.portalBlock #categoryBrowser *,
a.fancyButton.grey,
.filterOption.fancy {
	color: rgb(0, 0, 0);
	text-shadow: 0px 0px 0.5px #555!important;
}

.fpaItem a,
input.text,
.pageTabs > ul a,
.forumList,
.table.listing,
#select_days,
#select_days *,
.messagecontent .message-quote-div *,
.messagecontent .message-quote-div,
.sortable.select .specName,
.sortable.sortorder small,
.jouwRechten *,
#reacties #reactieContainer > .reactie > .reactieBody.spotlight *,
.pageDistribution .next,
div.filteroption * {
	color: #000 !important;
	text-shadow: none !important;
}
}
