css:
@-moz-document domain("tweetdeck.twitter.com") {
body, #container {
    font-family:'Arial', 'microsoft yahei', sans-serif;
  background: url('http://static.tumblr.com/ea568d43430e0f881680e5cdb622a1b3/v8gip9c/xXPn3e1r4/tumblr_static_ge.png') top right fixed repeat;
}

.app-columns {
    background:none;
    padding:0;
}

*{  border-radius: 0; }

h1, h1 *, .attribution {
  color:#fff;
}

.column-type-icon
{
  color:#fff;
}

.js-column
{background:rgba(0, 0, 0, 0.7);}

.is-wide-columns .column {
      margin-right: 0px;}


.column-header, .column-header-temp
{background:rgba(0, 0, 0, 0.5);}

.js-detail-container
{background:none;}

.tweet-detail-wrapper
{background:none;}

::selection {
  background:rgba(118, 226, 194,0.8); 
  color: #fff
}

.med-fullpanel {background:rgba(0, 0, 0, 0.75);}


.js-docked-compose {background:rgba(0, 0, 0, .5);} 
.js-drawer {background:#1a0f1e}


}
