css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	body  { background-color: #f0f0f0; ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #f0f0f0; ;}
	pre  { color: #bbbbbb ;}

	.hs-chr  { color: #4070a0; font-weight: bold ;}
.hs-comment  { color: #60a0b0; font-style: italic ;}
.hs-comment a  { color: #60a0b0; font-style: italic ;}
.hs-conid  { color: #902000 ;}
.hs-conop  { color: #666666 ;}
.hs-cpp  { color: #007020; font-weight: bold ;}
.hs-definition  { color: #06287e ;}
.hs-keyglyph  { color: #007020; font-weight: bold ;}
.hs-keyword  { color: #007020; font-weight: bold ;}
.hs-layout { color: #000000;}
.hs-num  { color: #40a070 ;}
.hs-str  { color: #4070a0 ;}
.hs-varid { color: #000000;}
.hs-varop  { color: #666666 ;}
background  { background-color: #f0f0f0; ;}
foreground  { color: #bbbbbb ;}

}
