css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	body  { background-color: #202020; color: #d0d0d0 ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #202020; color: #d0d0d0 ;}
	pre  { color: #666666 ;}

	.hs-chr  { color: #ed9d13 ;}
.hs-comment  { color: #999999; font-style: italic ;}
.hs-comment a  { color: #999999; font-style: italic ;}
.hs-conid  { color: #6ab825; font-weight: bold ;}
.hs-conop  { color: #d0d0d0 ;}
.hs-cpp  { color: #6ab825; font-weight: bold ;}
.hs-definition  { color: #447fcf ;}
.hs-keyglyph  { color: #6ab825; font-weight: bold ;}
.hs-keyword  { color: #6ab825; font-weight: bold ;}
.hs-layout  { color: #d0d0d0 ;}
.hs-num  { color: #3677a9 ;}
.hs-str  { color: #ed9d13 ;}
.hs-varid  { color: #d0d0d0 ;}
.hs-varop  { color: #d0d0d0 ;}
background  { background-color: #202020; color: #d0d0d0 ;}
foreground  { color: #666666 ;}

}
