css:
@-moz-document regexp('(https?://hackage\\.haskell\\.org/.*/src/.*)|(https?://haddocks\\.fpcomplete\\.com/.*/src/.*)') {

	body  { background-color: #ffffff; ;}

	pre {
		font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
		font-size: 12px;
		overflow: auto;
	}

	pre  { background-color: #ffffff; ;}
	pre  { color: #bbbbbb ;}

	.hs-chr  { color: #bb8844 ;}
.hs-comment  { color: #999988; font-style: italic ;}
.hs-comment a  { color: #999988; font-style: italic ;}
.hs-conid  { color: #445588; font-weight: bold ;}
.hs-conop  { font-weight: bold ;}
.hs-cpp  { font-weight: bold ;}
.hs-definition  { color: #990000; font-weight: bold ;}
.hs-keyglyph  { font-weight: bold ;}
.hs-keyword  { font-weight: bold ;}
.hs-layout { color: #000000;}
.hs-num  { color: #009999 ;}
.hs-str  { color: #bb8844 ;}
.hs-varid { color: #000000;}
.hs-varop  { font-weight: bold ;}
background  { background-color: #ffffff; ;}
foreground  { color: #bbbbbb ;}
}
