css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tieba.baidu.com") {
/*-- 字体雅黑 --*/
  *{font-family:  "Microsoft Yahei" !important;}
/*-- 页面精简 --*/  
  /*userbar*/
  .userbar .u_wallet, .u_tbmall, .u_app, .split, .u_baiduPrivilege, .u_tshow, .u_joinvip,
  /*列表页推广*/
  #contet_wrap .per_list.j_per_list,.BAIDU_CLB_AD,#forum_recommend,.ecitic_activity_head/*贴吧银行*/,
  /*回复框*/
  .poster_head,
  /*侧边栏*/
  #encourage_entry,.game_tab,.game_content,
  /*边边角角*/
  .firework_sender_wrap,
  /*内容页*/
  #search_button_wrapper,#pb_adbanner,.reward_btn,.share_btn_wrapper,.thread_recommend,.dasense ,.p_share_ding
  {display:none !important;}
  
/*-- 导航栏 --*/
  /*navbar*/
  .search_nav{
    left:0;top:0;
      background-color: #2D3C4E !important;;
      background-image: -moz-linear-gradient(center top , #3F546D 0%, #2D3C4E 100%) !important;
    position:absolute;
    width:100% ;
    padding-top:0 !important;
    height:40px !important;
    line-height:40px !important;
    font-weight:700;
  }
  .search_nav a:link, .search_nav a:hover, .search_nav a:visited {
      font-family: Arial,SimSun;
      color: #CFCFCF;
      text-decoration: none;
  }
  .search_nav a, .search_nav b {
    padding:10px 15px !important;
    border-right:1px solid #3F546D ;
    margin:0;
  }
  .search_nav b{color:#FFF !important;}
  #head{padding-top:50px;}
  .search_logo{
    width:58px !important;
    height:40px !important;
    z-index:10;
  background:none transparent !important;
    top:0px;
    left:118px;
    margin:0 !important;
    position:absolute;
  }
  /*userbar*/
  .userbar{padding-right:7px ;top: 4px;}
  .u_username_avatar{border-radius:10px !important;}
  .u_username_title{color:#D9D9D9 !important;}
  .u_menu_hover ,.u_username .u_menu_hover .u_username_title {
      color:#537cc4 !important;
      border-color:  #5a8bd7;
  }
  .u_ddl_con {
      border-color:  #5a8bd7;
  }
  .u_setting_wrap {
      background: url('data:image/png;base64,') no-repeat scroll 0px 0px transparent;
  }
  .u_news_wrap {
      background: url('data:image/png;base64,') no-repeat scroll 0px 0px transparent;
      margin-top: 0px;
  }
  .u_menu_hover >.u_setting_wrap{
      background: url('data:image/png;base64,') no-repeat scroll 0px 0px transparent;
  }
  .u_menu_hover >.u_news_wrap{
      background: url('data:image/png;base64,') no-repeat scroll 0px 0px transparent;
  }
  /*searchbar*/
  .search_main_fixed{display:none !important;}
  .search_bright .search_ipt, .search_bright .search_btn_wrap, .search_bright .search_btn, .search_bright .search_btn_enter_ba {
      text-decoration: none;
      background: none !important;
  }
  .search_bright .search_btn {
      height: 30px;
      line-height: 30px;
      border:1px solid #999;
      background-image: -moz-linear-gradient(center top , #F1F2F3 0%, #DCDCDC 100%) !important;
  }
  .search_bright .search_btn_enter_ba {
      border-color:#2D3C4E !important;
      background-image: -moz-linear-gradient(center top , #3F546D 0%, #2D3C4E 100%) !important;
      color: white;
  }
  .search_bright .search_inp_border {
    border-color:#999 !important;
  }
  .senior-search-link {
      margin-top: 7px;
  }
}

@-moz-document url-prefix("http://tieba.baidu.com/f?kw="),
url-prefix("http://tieba.baidu.com/f?ie="),
url-prefix("http://tieba.baidu.com/f?fp="),
url-prefix("http://tieba.baidu.com/f?tp="),
url-prefix("http://tieba.baidu.com/f?wd="),
url-prefix("http://tieba.baidu.com/f?fr="),
url-prefix("http://tieba.baidu.com/f/good?kw="),
url-prefix("http://tieba.baidu.com/f/vote?kw="),
url-prefix("http://tieba.baidu.com/photo"),
url-prefix("http://tieba.baidu.com/p/"),
url-prefix("http://tieba.baidu.com/f?z="),
url-prefix("http://tieba.baidu.com/f?ct="),
url-prefix("http://tieba.baidu.com/f?kz="){
    html{	   
    background:none #222E3E !important;   
    }  
    body,.wrap1,.wrap2{
    background:none transparent !important;
    } 
/*内容页回复框*/
  .forum_foot,.pb_footer{background:white !important;}
  #tb_rich_poster_container {
    padding-top: 20px;
    margin-top: 0px;
}
/*帖子加宽*/
  .right_section{display:none !important;}
  /*标题加宽*/
  .left_section{width:978px !important;}
  #j_core_title_wrap{width:978px !important;}
  .core_title_txt{width:656px !important;}
  /*正文加宽*/
  .l_post_bright {
    width: 978px !important;;
    background:#ECEFF1 !important;
  }
  .d_post_content_main {width: 798px;padding:0 25px !important;}
  .l_post_bright .core_reply_wrapper {margin-left:100px !important;width: 678px;}
  .edui-container{width:644px !important;}
  .lzl_editor_container .lzl_panel_wrapper {width: 644px;}
/*放大图片尺寸*/
.BDE_Image:not([width="1"]),.imgwrap,.d_content_img {width:auto!important;height:auto!important;max-width:100%!important;}
.p_content .BDE_Flash {width:800px!important;max-width:100%!important;height:485px!important;max-height:100%!important;}
/*修复明星贴吧顶部信息条错位*/
.star_head{width:978px !important;}

/*-- 贴吧信息,特殊贴吧暂未修改 --*/
.card_banner {
    display: none;
}
/*tieba 信息*/
.card_top_theme1 .card_head, .card_top_theme1 .card_head_img {
    width: 60px;
    height: 60px;
}
.card_top_theme1 .card_head {
    margin: 15px;
}
.card_top_theme1 .card_top {
    padding-left: 100px;
}
.card_top_theme1 {
    border-top: 0px solid #FFF;
    background:#F1F2F3;
}

  /*深度精简*/
  /*帖子内容页分类导航*/
  .nav_wrap_add_border,.star_nav_wrap_margin,.star_collect_body,
  /*内容页顶部页码*/
  .thread_theme_5 {
    display:none !important;
}
}
