css:
/* i really want this to be global */

.codebits-full .ace-tomorrow-night .ace_scroller, .lesson-middle .ace-tomorrow-night .ace_scroller {
background-color: #FFF;
}

.ace-tomorrow-night .ace_gutter, .codebits-full .ace-tomorrow-night .ace_gutter .ace_gutter_cell, .lesson-middle .ace-tomorrow-night .ace_gutter .ace_gutter_cell {
background: #EDEDED;
color: #969696;
}

.lesson-ace-editor, .lesson-middle .lesson-ace-editor {
position: absolute;
right: 0;
left: 0;
bottom: 0;
top: 40px;
background-color: #FFF;
margin-top: 15px;
font-size: 14px;
line-height: 19px;
direction: ltr;
}

.lesson-file-tabs {
height: 40px;
background-color: #EDEDED;
border-bottom: none !important;
font-size: 14px;
color: #FFF;
}


.lesson-middle {
background-color: #EDEDED;
}

.lesson-file-tabs__tab.active, .lesson-file-tabs__tab:hover {
color: #6E6C6C;
}


.lesson-file-tabs__tab.active {
background-color: #fff !important;
border: none !important;
}

.lesson-middle .ace-tomorrow-night .ace_gutter, .codebits-full .ace-tomorrow-night .ace_gutter .ace_gutter_cell, .lesson-middle .ace-tomorrow-night .ace_gutter .ace_gutter_cell {
background: #fff!important;
color: #272727 !important;
}

.ace-tomorrow-night .ace_gutter-active-line {
background-color: #DBDBDB !important;
}


.ace-tomorrow-night .ace_storage, .ace-tomorrow-night .ace_storage.ace_type, .ace-tomorrow-night .ace_support.ace_type {
color: #9F00CF !important;
}

.ace-tomorrow-night .ace_text-layer {
color: #000 !important;
}

.ace-tomorrow-night .ace_entity.ace_name.ace_function, .ace-tomorrow-night .ace_support.ace_function, .ace-tomorrow-night .ace_variable {
color: #0F5896 !important;
}

.ace-tomorrow-night .ace_variable.ace_parameter {
color: #BB5005 !important;
}

tomorrow-night .ace_variable {
color: #BD1E1E !important;
}

.ace-tomorrow-night .ace_string {
color: #438902 !important;
}

body.composer * {
-webkit-tap-highlight-color: #EDEDED !important;
}
  
  .lesson-middle .ace-tomorrow-night .ace_marker-layer .ace_selection {
background-color: rgba(253, 255, 171, 0.57) !important;
}

.lesson-file-tabs, .lesson-file-tabs__tab:hover:not(.active) {
background-image: none !important;
}

.lesson-middle {
background-color: #FFF !important;
}

.lesson-checkpoint__wrapper {
border-right: 1px solid #EDEDED;
}

.ace-tomorrow-night .ace_support.ace_constant, .ace-tomorrow-night .ace_variable.ace_parameter {
color: #E26006 !important;
}

.ace-tomorrow-night .ace_keyword, .ace-tomorrow-night .ace_meta, .ace-tomorrow-night .ace_storage, .ace-tomorrow-night .ace_storage.ace_type, .ace-tomorrow-night .ace_support.ace_type {
color: #9305BD !important;
}

body.composer header {
background: #FFD7A2 !important;
}

.lesson__back-nav {

color: #3A3A3A !important;
}

.composer-bottom-bar {
background-color: #EDEDED;
background-image: none !important;
border-top: none;
}


.ui-button--blue--on-dark {
background-color: #08C;
color: #FFF;
border-color: #EDEDED;
}
  
  .ui-button--transparent--on-dark {
-webkit-box-shadow: none !important;
-moz-box-shadow: inset 1px 1px rgba(255,255,255,0.1),1px 1px rgba(0,0,0,0.1);
box-shadow: none !important;
color: #FFF;
background-color: rgba(255, 116, 116, 0.93);
border-color: #EDEDED;
}
  
  .composer .lesson-sidebar .terminal .console .jqconsole * {
color: #3472A6 !important;
line-height: 19px;
font-size: 14px;
}
  
  .composer .lesson-sidebar .terminal .console {
z-index: 101;
background: #FFF;
}
  
  .composer .lesson-sidebar .result-chrome {
position: relative;
z-index: 1;
border: 15px solid rgba(0, 0, 0, 0);
padding: 7px;
background-color: #D3D3D3;
border: 1px solid rgba(240, 237, 237, 0);
border-radius: 3px;
margin-top: 17px;
}

body.composer header {
background: #D3D3D3 !important;
}
