css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digirooster.hanze.nl") {
html,#nav-cells,#form > table:nth-child(3) > tbody:nth-child(1) {
    background:  rgba(50, 50, 50, 1);
    height: auto;
}
      
body,#nav-container, #app-container,#navCurrentDateTxt,.date-top {
    background-color: rgba(50, 50, 50, 1) !important;
    
}
.btn-pos,.btn-checked,.btn,#b_ac{
    background-color: rgba(48, 48, 50, 0.1);
 border-color: #E60;
   border-radius: 5px;
  }
.btn-checked{
   background-color: rgba(20,20,20,1);
 }
  
tbody, div.grid-time-text{
    background-color: rgba(20,20,20,.7);
  }
.act-block{
  background-color:rgba(48, 48, 50, 1);
} 
.act-change{
   background-color:rgba(75,0,0,1)!important;
  }
  
 #b_tb, #b_btb, #b_re, #b_br, #b_ad, #b_ac {
    display: none;
}
  
.menu-list-button,.date-picker-off,#nav-table-container,#menu-container {
  background-color: rgba(48, 48, 50, 1);
}

  
div.grid-time-text,#grid-day-0,#grid-day-1,#grid-day-2,#grid-day-3,#grid-day-4{
  color: black;
}  
  .date-top,.menu-title,.act-block,.btn-content,#yearSelected,#groupSelected,#schoolSelected{
  color: lightgrey;
}
  
}
