css:
@-moz-document domain("mail.yahoo.com") {

/* make message list/preview boundary stand out more */
#msg-list         {border: 2px solid black}
#rail-resize-hori {background-color: black}

/* alternate row colors */
.list-view-item-container:nth-child(odd)  {background-color: aliceblue}
.list-view-item-container:nth-child(even) {background-color: snow}

/* change folder list color to stand out better from background */
div.listnav-outter {background-color: /*[[folder-list-bg]]*/}

/* disable the link to ymail in mail logo to avoid accidental clicks */
#uhWrapper a.YLogoMY {pointer-events: none}

/* buttons superimposed on message list */
div.inf-scroll-btn {display:none}

div.list-view-item-date-label {zoom: 0.5}     /* timechunk labels, useful but too large */
div.anchored-header {display:none !important} /* zooming timechunks breaks the anchored one   */

}
