css:
@-moz-document domain("baka-updates.com") {
body {
 
	background-color: #333;
	color: #BDBDBD;
  
}

a {
	/* color: #DB7500; */
  color: #A8A8A8;
}

a:visited {
	text-decoration: none;
	color: #A100E0;
}

#container { 
	background: #4A4A4A;
	border-left: 2px solid #595959; 
  
}
#container2 {
	background: none; 
}
#banner #login_panel .login_form {
    background: #858585 none repeat scroll 0%;
    border-color: #8F8F8F;
    border-style: solid;
    color: #A8A8A8;
  	font-size:9pt;

}
#search #searchbox {
	background: #858585 none repeat scroll 0%;
    border-color: #8F8F8F;
    border-style: solid;
	color: #A8A8A8;
}
#right_panel {
	background: none top left repeat-y;
}
#left_panel {
	border-right: 2px solid #595959;
}
#left_panel #sleeping {
	border-top: 2px solid #595959; 
}
#left_panel #poll {
	background-image: none;
	background-color: #616161;
}
#left_panel #poll_bottom {
	border-left: 2px solid #595959;
    background-image: none;
}
#left_panel #bottom {
	background-image: none; 
}
#left_panel #poll #poll_title {
 	background-image: url('http://i.imgur.com/pqLgVIQ.png') !important;
  	border:1px solid #545454;
  	margin-bottom:5px;  	
}
#content_panel {
	border-top: 2px solid #595959; 
}
#banner #login_panel {
	background-image: none;
}
#banner_top_left {
	background-image: none; 
}
#banner #bottom_mid {
	background-image: none;
}
#banner_top_right {
    border:none;
    color: #4996db;
  	background-image: none;
}
#banner_border {
 	background-image: none; 
}
#banner #bottom_logo #bot_logo {
	background-image: none; 
  	border: none;
}
#banner #bottom_right {
	background-image: none; 
}
#footer {
    border-top: 2px solid #595959; 
}
#content_panel #content_header {
	background-image: none; 
  	padding-top: 10px;
}
#content_panel #content_header_tri {
	background-image: none;
}
#horizontal_border {
	background-image: none; 
	border-top:1px solid #858585;
}
#menu #links li span:hover {
	background: none no-repeat left;	
}
#menu #links li span a {
	/*color: #CF8300; */
  	color: #A8A8A8;
}
#menu #links li span a:visited {
	color: #A100E0;
}
#menu #links li span a:hover {
	color: #C4C4C4;
}
#menu #links li .curr_page {
	background: none no-repeat left;
    font-weight:900;
  	font-style: italic;
	color: #C9C9C9;
}

.rlsheader {
	background-color: #414141 !important;		
}
.release table {
	background-color: #313232 !important;
}
.release table .rlselement:hover {
	background-color: #3D3D3D !important;
}
.groups table {
	background-color: #313232 !important; 
}    
.box {
	border: 1px solid #5E5E5E;    
}
.series .seriesheader {
	background-color: #414141 !important;     
}
.series table {
	background-color: #313232 !important;
}
tr, tr:hover {
	background-color: #313232 !important;
}
.btlink img {
  display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url('http://i.imgur.com/y2b7HMH.png') no-repeat !important;
  width: 14px;
  height: 14px;
  padding-left: 14px;
  
  
}
}
