css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pcmonitors.info") {
  body {
    font-family: "Fira Sans";
  }
  
  #nv-tabs {
    top: 33px;
  }
  
  #nv-tabs ul li {    
    font-weight: bold;
    font-size: 13px;
  }
  
  #sub-tabs ul li {
    line-height: 22px;
  }
  
  #sub-tabs ul li, #sub-tabs ul li a {
    color: black;
        font-size: 13px !important;
  }
    
 #content .entry p {
    margin-bottom: 10px;
  }
  
  #content .entry p span {        
    color: black;
    font-family: "Quercus";
    font-size: 21px !important;
    line-height: 1.5em !important;
  }

  
}
