css:
@-moz-document domain("tvtropes.org") {
ins, img[src="http://creativecommons.org/licenses/by-sa/3.0/"] {
    display: none !important
    }


 #wikileftpage h1 {
    background: url(data:image/png;base64,) 5px no-repeat !important;
    }


 div.pagetitle span {
    color: yellow !important;
    }

 
 div.alt_title {
    color: #FFAE00 !important;
    }


 body, h1{
    background-color: #000 !important;
    color: #bbb !important;
    }
 #wikitext h1 {
    border: 1px solid #555 !important;
    }




 tr td div.buttoninterior {
    color: yellow !important;
    }
 tr td div.buttonframe div.buttoninterior a {
    color: #fdc423 !important;
    display: block !important;
    }
 div.buttonframe, div.buttoninterior, div.folderlabel, div.templatebuttoninterior, div.templatebuttonframe {
    background-color: #222 !important;
    }
 tr td div.buttoninterior:hover, tr td div.buttonframe:hover {
    border-color: yellow !important;
    }


 div.folderlabelopen {
    background-color: #222 !important;
    color: #E600FF !important;
    border-color: #444 !important;
    }
 div.folderlabelopen:hover {
    border-color: #777 !important;
    }


 div.folderlabel, div.folderlabel span {
    color: #ccc !important;
    border: #444 solid 1px !important;
    }
 div.folderlabel:hover {
    border-color: #777 !important;
    }


 div.cse-branding-bottom {
    background-color: #222 !important;
    color: #fff !important;
    -moz-border-radius: 10px !important;
    padding-right: 9px !important;
    margin-left: 9px !important;
    border: 1px solid #555 !important;
    }


 table.pathholder {
    background-color: #222 !important;
    border-color: #555 !important;
    height: 100% !important;
    }
 table.pathholder td {
    background-color: #222 !important;
    }


 div.pathholder {
    background-color: #222 !important;
    border: 1px solid #555 !important;
    }

 td strong {
    background-color: #202020 !important;
    color: #bbb !important;
    }


 div.acaption, div.acaptionright {
    background-color: #252525 !important;
    border: 1px solid #555 !important;
    color: #00cc33 !important;
    position:relative !important;
    z-index: 3 !important;
    }
 div.quoteright {
    background-color: transparent !important;
    border-color: #555 !important;
    }
 div.acaption span, div.acaptionright span, div.acaption a:link, div.acaptionright a:link, div.quoteright a:link {
    color: #00cc33 !important;
    }
 div.acaption a:visited, div.acaptionright a:visited, div.quoteright a:visited {
    color: #008020 !important;
    }
 div.acaption a:hover, div.acaptionright a:hover, div.quoteright a:hover {
    color: #d3b050 !important;
    }


 div.indent {
    color: #00BFFF !important;
    }
 div.indent a:link {
    color: #00BFFF !important;
    }
 div.indent a:visited {
    color: #00759C !important;
    }
 div.indent a:hover {
    color: #d3b050 !important;
    }


 div.noticeheader, div.repairbug {
    background-color: #333 !important;
    color: #FF00F2 !important; 
    font-size: 16px !important;
    border: 1px solid #555 !important;
    }


 h2, h3, div.contributorlead, div.justbugscaption, div.wmglead {
    background-color: #222 !important;
    color: #bbb !important;
    border: 1px solid #555 !important;
    }


 a:link {
    color: #ccc !important;
    text-decoration: underline !important;
    }
 a:visited {
    color: #fdc425 !important;
    text-decoration: underline !important;
    }
 a:hover {
    color: #d3b050 !important;
    }


 a.createlink:link, a.createlink:visited {
    color: #ac7069 !important
    }
 a.createlink:hover {
    color: #d26f42 !important;
    }


 li, a {
    border: none !important;
    z-index: 1 !important;
    
    }


 li.plus, div.floatboxright, div.floatboxright ul {
    background-color: #111 !important;
    z-index: 2 !important;
    position: relative !important;
    margin-top: 0px !important;
    }


 li.plus {
    list-style-image:url(data:image/gif;base64,R0lGODlhCwALAIABALu7u////yH5BAEAAAEALAAAAAALAAsAAAIXhI8Wy6ze3gKRQkeTksFO31WR2FQaUgAAOw==) !important;
    }


 p {
    color: #ff0000 !important;
    }


 div.difftime, div.score, div.voteactivity, div.scoreboard {
    background-color: #333 !important;
    z-index: 2 !important;
    position: relative !important;
    }


 div.review_text {
    background-color: #222 !important;
    color: #bbb !important;
    }


 div.pagetitle, th { 
    color: #bbb !important;
    z-index: 2 !important;
    position: relative !important;
    }


 a.forumbutton {
    background-color: #333 !important;
    }


 a.tropername {
    background-color: #000 !important;
    }


 div#smileyPanel {
    background-color: #222 !important;
    }


 a.fauxbutton, div.fauxbuttonholder {
    z-index: 3 !important;
    position: relative !important;
    background-color: #333 !important;
    }


 li.newminor {
    background-color: #222 !important;
    }


 div#Toolcats div, div#Toolcats, div#indexen {
    background-color: #222 !important;
    }

 div#Goodiescats li, div#Toolcats li, div#Toyscats li {
    background-color: transparent !important;
    }


 a.comixlink:link {
    color:#666600 !important;
    }
 a.DarthWikilink {
    color:white !important;
    }
 a.SoYouWantTo, a.newgrouplink {
    color:#339900 !important;
    }
 a.discussionlink, a.DarthWikiDiscussionlink {
    color:#D2691E !important;
    }
 a.yklink, a.fauxww, a.rename {
    color:purple !important;
    }
 a.AATAFOVSlink {
    color:#543C1C !important;
    }
 a.justbugsme, a.wmg {
    color:#009999 !important;
    }


div#Happening li {
    background-color: #222 !important;
    }


 span.spoiler,  .spoiler a.createlink,  .spoiler a, span.spoiler a.twikilink, span.spoiler span, span.supersecret {
    color: transparent !important;
    background-color: #111 !important;
    }


 span.tiptag sup {
    position: relative !important;
    top: 6px !important;
    color: yellow !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/topics.php"), url-prefix("http://tvtropes.org/pmwiki/conversations.php"), url-prefix("http://tvtropes.org/pmwiki/factivity.php"), url-prefix("http://tvtropes.org/pmwiki/factivity2.php"), url-prefix("http://tvtropes.org/pmwiki/profile.php"), url-prefix("http://tvtropes.org/pmwiki/voiceactivity.php"), url-prefix("http://tvtropes.org/pmwiki/crown_activity.php"), url-prefix("http://tvtropes.org/pmwiki/crowner.php"), url-prefix("http://tvtropes.org/pmwiki/pm.php") {
span {
    color: yellow !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/conversations.php"), url-prefix("http://tvtropes.org/pmwiki/posts.php"), url-prefix("http://tvtropes.org/pmwiki/topics.php") {
div.container div div.pagetitle {
    display: none !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/conversations.php") {
tr:nth-child(2n+3) td {
    background-color: #303030 !important;
    }


 tr td {
    border-color: #a2a2a2 !important;
    }


 th {
    background-color: #404040 !important;
    text-transform: capitalize !important;
    }


 a:link {
    text-decoration: none !important;
    }
 a:visited {
    text-decoration: none !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/showcuts.php") {
div > div:nth-child(even) {
    background-color: transparent !important;
    }
 div > div:nth-child(odd) {
    background-color: #222 !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/CutList.php") {
div#wikiright {
    background-color: transparent !important;
    }


 tr:nth-child(4n+3) td {
    background-color: #222 !important;
    }
 tr:nth-child(4n+4) td {
    background-color: #222 !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/posts.php") {
div.forumpagebuttons {
    border-style:solid !important;
    border-color: #555 !important;
    border-width: 2px !important;
    background-color: #333 !important;
    }

 a.forumpagebutton {
    text-decoration: none !important;
    background-image: none !important;
    }


 div.forumreplyheader  {
    background-color: #000 !important;
    color: #bbb !important;
    z-index: 2 !important;
    position: relative !important;
    }


 div.forumreplybody, div.forumimageholder {
    background-color: transparent !important;
    border-color: #444 !important;
    z-index: 2 !important;
    position: relative !important;
    }


 div.forumtitle {
    background-color: #222 !important;
    border-color: #444 !important;
    border-style: solid !important;
    border-width: 1px !important;
    color: #ddd !important;
    font-weight: bold !important;
    border-left: none !important;
    }


 div.forumsigline {
    border-color: #444 !important;
    color: #ddd !important;
    z-index: 2 !important;
    position: relative !important;
    }


 div.forumreplyheader sup {
    color: yellow !important;
    }


 div.forumreplyheader a:link span {
    color: yellow !important;
    }


 div.forumsigline {
    background: none !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/ykttw.php"), url-prefix("http://tvtropes.org/pmwiki/discussion.php") {
div.header, div.wtitleholder {
    background-color: #333 !important;
    color: #fff !important;
    }

 div#fauxtitle, div.newykttw, div.ykttwtext, div.levelOneCommands, div.levelThreeCommands {
    background-color: #111 !important;
    color: #fff !important;
    }

 .aYKTTW {
    background-color: #222 !important;
    border-color: #bbb !important;
    }

 div.replytext {
    background-color: #111 !important;
    border-color: #777 !important;
    color: #bbb !important;
    }


 div.levelOneCommands span, div.timestamp div {
    color: #F480FE !important;
    }
 div.timestamp span {
    color: #80C8FE !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/review_activity.php") {
tr:nth-child(odd) td {
    background-color: #222 !important;
    }
 div.alphalist a {
    background-color: #333 !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/launches.php") {
tr:nth-child(even) td {
    background-color: #222 !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/factivity.php"), url-prefix("http://tvtropes.org/pmwiki/factivity2.php") {
tr:nth-child(even) td {
    background-color: #303030 !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/changes.php") {
div#wikimiddle div {
    background-color: transparent !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/pmwiki.php") {
div.footnav {
    display: none !important;
    }
}

@-moz-document url-prefix("http://tvtropes.org/pmwiki/pictureindex.php") {
div td center table tr td>a {
    background-image: none !important;
    }
}

@-moz-document domain("tvtropes.org") {
a.subpagelink, a.subpagelink:visited {
		color: #cccccc !important;
		text-decoration: none !important;
		background-color: #333333 !important;
		border: 1px solid #111111 !important;
	}
		
	div#watchmsg div.buttonframe, div#watchmsg div.buttoninterior,
	div.templatebuttoninterior,
	div.templatebuttonframe  {
		color: #cccccc !important;
		background-color: #333333 !important;
		border: 1px solid #111111 !important;
		-moz-border-radius: 4px !important;
		-webkit-boarder-radius: 4px !important;
	}
	div.buttoninterior a, div.buttoninterior a:visited {
		text-decoration: none !important;
		color: #cccccc !important;
	}
	div#watchmsg div.buttoninterior:hover, a.subpagelink:hover{
		border-color: limegreen !important;
	}
}

@-moz-document domain("tvtropes.org") {
div.main, td, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }
}

@-moz-document domain("tvtropes.org") {
div.header, td, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }
 div.logo, td,  div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }

div.footer-top, td, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }
div.page-content, td, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }

div.main-left>.inner, td, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }
div.mobile-menu-section, td, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }

}

@-moz-document domain("tvtropes.org") {
div#search-box, td, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }

div.gsc-control-wrapper-cse, td, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }


div.scroller-inner, td, div.example, #wikitext {
    background-color: #111 !important;
    color: #bbb !important;
    }
a.textcolor {
  background-color: #282828 !important;
  color: #bbb !important;
  border-color: #e7e7e7 !important;
  }
}

@-moz-document domain("tvtropes.org") {
span.spoiler { color: black !important; background-color: black !important ; border-color: black !important; }
             span.spoiler a { color: black !important; }
             span.spoiler:hover { color: #ffffff !important; }
             span.spoiler:hover a { color: cyan !important; }
}
