css:
@-moz-document domain("youtube.com") {

body, .comment-settings-header {background: url(http://i.imgur.com/NqrFuWF.jpg) !important;}
#guide-container, #appbar-settings-menu, #yt-picker-language-footer, #yt-picker-country-footer, #yt-picker-safetymode-footer, 
#content, #masthead-appbar, .yt-uix-button-menu, #masthead-expanded-container, .yt-uix-shelfslider-prev, .yt-uix-shelfslider-next,
#yt-masthead-container, #watch7-container, #watch7-sidebar, #footer-container, .GKY1I5JBMEB
{background: url(http://imgur.com/1tIJnBh)!important;}
#yt-masthead #logo, #footer-logo {background: url(https://lh6.googleusercontent.com/-SbzEbHi8gxw/U9e-jUmZ0vI/AAAAAAAAAMk/wfq98m0_BaY/w821-h462-no/YOUTUBE+PEQUE+3.fw.png)!important;}

/* Settings */
#guide-container {box-shadow: 0 4px 4px 2px rgba(0,0,0,0.1);}
#appbar-settings-menu, #yt-picker-language-footer, #yt-picker-country-footer, #yt-picker-safetymode-footer, #content, 
#masthead-appbar, .yt-uix-button-menu, #masthead-expanded-container {box-shadow: 0 0 4px 2px rgba(0,0,0,0.1);}
.yt-uix-shelfslider-prev, .yt-uix-shelfslider-next {border-color: #AC8D5B !important;}
#masthead-appbar-container {box-shadow: 0 3px 4px  rgba(50,50,50,0.2);}
#help, .yt-card, .ytg-fl, #non-appbar-vm-video-actions-bar, #vm-pagination, #analyticsCard, .vm-video-item {background: none !important;}
.branded-page-v2-detached-top .branded-page-v2-container {margin-top:0px !important;}
.branded-page-v2-secondary-col, #footer-logo img, .ab-midpane {display :none !important;}
#yt-masthead-container {box-shadow: 0 2px 2px rgba(50,50,50,0.2);}
#footer-logo {
  width: 72px;
  height: 30px;
  vertical-align: middle; }
#guide {border-right: 1px solid #E8E8E8 !important;}
#watch7-container {box-shadow: 0 0 6px rgba(0,0,0,0.1);}
#watch7-sidebar {
  padding-bottom: 16px;
  box-shadow: 0 0 3px 3px rgba(0,0,0,0.1); }
#watch7-content, #watch7-headline, #watch7-user-header, #guide, #content .primary-col .branded-page-v2-body, #feed, 
#content .branded-page-v2-primary-col, #branded-page-body .tab-content-body, .secondary-header-contents {background: transparent !important;}
#footer-container {box-shadow: 0 0 4px rgba(0,0,0,0.2);}
#watch-description {box-shadow: 0 24px 20px -14px rgba(160,160,160,0.3);}
#watch7-action-panel-footer {
  background: url(http://imgur.com/1tIJnBh) !important;
  height: 1px; }
#watch7-headline, #watch7-user-header {border: none;}
.yt-uix-scroller, .yt-ui-ellipsis-2 {background: url(http://i.imgur.com/1tIJnBh.png) !important; border: none !important; }

/* Dark Light Large Player */
.watch-medium::after,.watch-large::after {width:2px;height:100%;cursor:none;content:"\a0";position:absolute;top:0;left:0;}
.watch-medium::before,.watch-large::before
{transition:.3s 0s opacity;width:100%;height:100%;pointer-events:none;content:"\a0";background:#1B1B1B;position:fixed;top:0;left:0;opacity:0;}
.watch-medium:hover::before,.watch-large:hover::before {transition:.8s .8s opacity;opacity:.9;}/**/
#appbar-primary-container::after {display:inline-block;width:600px;opacity:.5;margin-left:3.5rem;content:"\a0";background:none no-repeat 0 50% / auto 50%;background-image:-moz-element(#watch-headline-title);background-image: element(#watch-headline-title);}
.watch-medium + *,.watch-large + *, .watch-medium, .watch-large {position:relative!important;z-index:0;top:-15px;}
#masthead-positioner ~ * .watch-medium,#masthead-positioner ~ * .watch-large {top:0;}
.watch-medium, .watch-large {z-index:1999999999;background:#1B1B1B!important;min-width: 0!important;left:0;right:0;width:99%!important;width:calc(100% - 2px)!important;padding:0 1px!important;margin-top:0!important;}
.watch-medium .player-width, .watch-large .player-width {width:100%!important;}
.watch-medium .player-height, .watch-large .player-height {height:87vh!important;height:calc(100vh - 92px)!important;max-height:78vw!important;}
#masthead-positioner ~ * .watch-medium .player-height, #masthead-positioner ~ * .watch-large .player-height {height:85vh!important;height:calc(100vh - 90px)!important;}
.watch-medium .html5-main-video, .watch-large .html5-main-video,
.watch-medium .html5-video-content, .watch-large .html5-video-content {min-height: 100%!important;height:auto!important;margin:0 auto!important;left:0!important;right:0!important;}
.watch-medium .html5-main-video, .watch-large .html5-main-video {width:auto!important;}
.watch-medium #watch7-playlist-tray-container:not(:hover), .watch-large #watch7-playlist-tray-container:not(:hover) {height:38vh!important;}
.watch-medium .watch-sidebar, .watch-large .watch-sidebar {left:inherit!important;right:0!important;float:right!important;}
.watch-playlist-collapsed #playlist-tray {pointer-events:none!important;}
.watch-playlist-collapsed #watch7-playlist-tray-mask,
.watch-medium #player-branded-banner, .watch-large #player-branded-banner {height:0!important;}

.html5-play-progress, .ytp-play-progress {
   background: #AC8D5B !important; 
}
.html5-scrubber-button:hover, .ytp-scrubber-button:hover {
   background: #AC8D5B !important;
}
.ytp-volume-slider-foreground:before {
   background: #AC8D5B !important;
}
.ytp-volume-slider-foreground:after {
   background: #C4AD88 !important;
}
.html5-load-progress, .ytp-load-progress {
   background: url(http://i.imgur.com/NqrFuWF.jpg) !important;
}
#UCs5XULb_2tok6gN7_JjNWZg-guide-item .display-name
    {
    font-family: arial !important;
    color: #FF0000 !important;
    font-weight: bold !important;
    }

#watch-like-dislike-buttons #watch-dislike:after{content: " No me gusta" !important;}
.video-extras-sparkbar-likes
{
   background:#64E600;
}
.video-extras-sparkbars
{
   height:3px;
}
.html5-info-panel .video-extras-sparkbar-dislikes{background:#990b00}
.html5-info-panel .video-extras-sparkbar-likes{background:#64E600}
.html5-info-panel .video-extras-sparkbars{height:3px;}
#watch7-views-info .video-extras-sparkbars {height:3px;}

.html5-play-progress, .ytp-play-progress {
   background:  repeat-x !important; 
}

.html5-load-progress, .ytp-load-progress {
   background: url("https://lh6.googleusercontent.com/-dbEXnZg_jec/U9e4HHm_6DI/AAAAAAAAAKE/fhDL3tVLzJo/w821-h462-no/Fondo+REPRODUCTOR+5.fw.png") !important;
}

.html5-scrubber-button, .ytp-scrubber-button {
	background: url("https://lh6.googleusercontent.com/-EuUuI2ghzf0/U9e3QWTX9-I/AAAAAAAAAI4/u5KSfb2kEXs/w821-h462-no/Fondo+REPRODUCTOR+3.fw.png") repeat-x !important; 
}
.video-extras-sparkbar-likes {
 background-color: #64E600 !important;
}
}
