css:
@-moz-document regexp("https://pacificridge.haikulearning.com/u/[a-zA-z0-9]+/portal") {
/* Calendar Edits */

.cldr.lil_cldr {
      display: none;
}
#frame {
 	position: relative; 
}
#col_1 {
    margin-top: 60px;
    float: none !important;
    margin-left: 5% !important;
 	width: 60% !important;
}
#col_2 {
    width: 22% !important;
    top: 0;
}
#portlet_box_calendar {
 	border-radius: 0;
    margin-left: 10%;
}
#col_1::before {
    content: 'Homework';
    position: absolute;
    left: 120px;
    top: -27px;
    color: rgba(0,0,0,.75);
    font-family: 'Open Sans', 'Helvetica', 'Arial', sans-serif !important;
    font-weight: 700;
 	font-size: 1.4em; 
}
#portlet_box_content_calendar_week {
    padding: 5px 15px !important;
}
#portlet_box_content_calendar_week > div:nth-child(3) {
      border: 1px solid rgba(0,0,0,.1);
      border-radius: 4px;
      background-color: rgba(0,0,0,.01);
      margin-left: -8px;
      padding: 5px 8px;
}
#portlet_box_content_calendar_week > div:nth-child(3) > h3, 
.box_title {
      color: rgba(0,0,0,.75);
      font-family: 'Open Sans', 'Helvetica', 'Arial', sans-serif !important;
      font-weight: 700;
      font-size: 1.3em;
}
#portlet_box_content_calendar_week > div > h3 {
      color: rgba(0,0,0,.7);
}
#portlet_box_content_calendar_week > div:nth-child(2) > h3::after {
      content: "  (Today)";
}
#portlet_box_content_calendar_week > div:nth-child(3) > h3::after {
      content: "  (Next Class)";
}
.calendar_day {
	border-top: 1px solid rgba(0,0,0,.15);  
}
.calendar_day:nth-child(2) {
	border-top: 1px solid rgba(0,0,0,0);  
}
.head {
	display:none;  
}
.center.pad_ten {
	border: 1px solid rgba(0,0,0,.0);
    border-radius: 8px;
    height: 15px !important;
}

.center.pad_ten:hover {
	border: 1px solid rgba(0,0,0,.1); 
    background: rgba(0,0,0,.01);
}

/* Header Edits */

a {
    color: #1A72B0;            
}
#header-logo {
 	display: none;
}
#global_top_links {
    max-width: 100% !important; 
}
h1 {
    float: left;
    padding: 5px 25px !important;
 	margin-top: -45px !important;
    color: rgba(0,0,0,.75) !important;
}
.sub_navigation_tier_1 {
 	display: none; 
}
.subnav-tier2-empty {
	color: rgba(0,0,0,.5);      
}

#header .toplinks a.icon_link,
div .loading,
#help_lnk {
  	display: none !important; 
}
#global_top_links {
    background-color: #1C6DAF !important; 
}
#header .toplinks a.home {
    background-color: rgba(255,255,255,.2);
    transition: 0.3s;
}
#header .toplinks a.home:hover {
 	background-color: rgba(255,255,255,.4); 
}
.eclass_filter {
	width: 200px !important;    
}
.eclass_filter a {
	overflow: hidden;    
}
li.eclass_border_color {
    padding-top: 10px !important;
    padding-bottom: 13px !important;
	border-left: 0;      
}
#global_top_links,
#frame,
#navbar,
#navbarowner {
 	box-shadow: none !important; 
}
#navbarowner {
 	border-width: 0; 
}
.eclass_border_color {
    border-left-width: 0 !important;
    border-left-style: none !important;
}
#header-body {
 	display: none; 
}

/* Right Column */

#portlet_box_domain_html {
 	display: none; 
}

/* Etc. */

p,
a,
em,
strong,
div,
h1,
h2,
h3,
h4,
h5 {
      font-family: 'Open Sans', 'Helvetica', 'Arial', sans-serif !important;
}
body {
    margin: 0;
 	padding: 0; 
}
#frame {
    width: 100% !important;
    margin-right: 0 !important;
    margin-left: 0 !important;
    max-width: 100% !important;
}
.subnav-tier2-empty {
 	border-color: rgba(0,0,0,.7); 
}
#navbar {
	display: none;    
}
#navbar_spacer {
 	height: 20px !important; 
}
.box_title {
    position: relative;
    left: -4px;
    bottom: -10px !important;
	font-weight: bold;
    font-size: 1.35em;
    color: rgba(0,0,0,.7) !important;
}
.filter_tgl {
    float: none !important;
	display: inline-block !important;  
}
#portlet_box_content_annc > table > tbody > tr > td:first-child {
    display: none;
}
#portlet_box_content_annc > table > tbody > tr {
    border-bottom: 1px solid #e0e2e3 !important;
}
#portlet_box_content_annc > table > tbody > tr:last-child {
    border-bottom: none;
}
.eclass_border_color div:nth-child(3) {
	width: 23px !important;
    margin: 0 !important;
    display: inline-block;
}
.eclass_filter {
    float: none !important;
    margin: 0 !important;
    display: inline;
    top: 1px !important;
}
.item_description > a > img {
	  display: none;
}
strong span {
      color: rgba(0,0,0,.65);
      font-family: 'Open Sans', 'Helvetica', 'Arial', sans-serif !important;
      font-weight: 800;
      font-size: 1.15em !important; 
}
#col_2::before {
       content:'Announcements';
 	   font-weight: bold;
       font-size: 18px !important;
       color: rgba(0,0,0,.7) !important;
}
.subnav-tier2-empty {
       background-color: #F6F6F6;
       border-color: #F6F6F6;
}

/* Cool button */
#portlet_calendar_load_next a {
    padding: 10px 38%;
 	border: 1px solid rgba(28, 109, 175, .4);
    cursor: pointer;
  	border-radius: 8px;
    transition: .35s;
}

#portlet_calendar_load_next a:hover {
 	background-color: rgba(28, 109, 175, .7);
 	border: 1px solid rgba(28, 109, 175, .7);
    transition: .35s;
    color: white;
}
#portlet_calendar_load_next, #portlet_calendar_load_next:hover {
  	width: 99%;
  	margin: 20px 0 !important;
  	padding: 0;
	background: none;
  	border: none;
}

#header .toplinks a:hover, #header .toplinks a.selected, #header .toplinks a:active, #header .toplin   ks a.active,
#header .toplinks a:hover, #header .toplinks a.selected, #header .toplinks a:active, #header .toplinks a.active {
    background-color: rgba(255, 255, 255, .15);
}
.dmenu.nav {
    background-color: #8AAAC5;
}
    
/* Layout */

#content {
 	position: relative; 
}
#col_2 {
 	position: absolute;
    right: 0;
    width: 17%;
    margin-top: 59px;
}
#portlet_box_annc {
    border-radius: 0 !important;
    border-top-left-radius: 6px !important;
    border-bottom-left-radius: 6px !important;
}
#col1 {
  position: absolute;
  left: 0;
  margin-top: 50px !important;
  width: 17% !important;
  border: 0 !important;
  background: #F6F6F6;
}
#portal_body .portlet_box .box {
 	border-top: 0; 
}
#portlet_box_calendar {
    margin-top: 20px !important;
}
#portlet_box_calendar #portlet_box_content_calendar_week {
    max-height: 80vh
}
#user_menu {
 	background: #F6F6F6; 
    border-top: 0;
}
.eclass_list ul li {
    border: 1px solid #e0e2e3 !important;
    border-top: 0 !important;
}
.eclass_list ul {
    border-top: none;
}
.eclass_list ul li:first-child {
    border-top-right-radius: 6px;
    border-top: 1px solid #e0e2e3 !important;
}
.eclass_list ul li:last-child {
    border-bottom-right-radius: 6px;
}
#portlet_box_content_annc > table > tbody > tr > td > div {
    font-family: 'Open Sans', 'Helvetica', 'Arial', sans-serif !important;
 	font-size: 0.9em; 
    padding: 8px;
    padding-left: 15px !important;
}
.filter-class-hidden {
	display: block !important;  
}
.dmoption {
	padding-top: 10px !important;
    padding-bottom: 10px !important;
}
#dmenu {
    max-height: 80vh;
	overflow: auto;
  	box-shadow: 0 2px 10px rgba(0, 0, 0, .25);
}
.dmoption.more-classes {
	display: none !important;  
}
.dmenu.nav .hover, .dmenu.nav .dmoption:hover, .dmenu.nav a:hover {
	background-color: #2b81c4 !important;  
}
}
