css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lparchive.org") {

html,
body
{
  background-color: #111!important;
}

.logo,
.header,
#nav_top,
#nav_bottom,
#details,
.returnblock
{
  background-color:#222!important;
}
.logo
{
  border-color:#111!important;
}
.logo:hover
{
  background-color:#AA2625!important;
}
#details #statistics,
#details #tags
{
  border-color:#333!important;
}
.featured
{
  border-color:#222!important;
}
.cblock
{
  background-color:#222!important;
  color:#CCC!important;
}
.cblock h1,
.cblock h2,
.cblock h3
{
  background-color:#2F2F2F!important;
  color:#EEE!important;
}
#content .toc li a
{
  color:#B02625!important;
}
div.cblock table:not(.plain) tr:nth-child(2n), div.cblock ul.toc:not(.plain) li:nth-child(2n)
{
  background-color:#333!important;
}
div.cblock table tr td, div.cblock ul.toc li
{
  border-color:#333!important;
}
.lpbox
{
  color:#CCC!important;
}
#letter,
#kazuhisa,
#latest,
#latest *
{
  background-image:none!important;
}
#latest h3 strong,
#latest.cblock h4
{
  text-shadow:0 -4px 6px #111, 0 -4px 12px #111, 0 -4px 0 #111, 0 4px 8px #111, 0 4px 12px #111, 0 4px 3px #111!important;
}
div.toc div.row:nth-child(2n):not(.plain)
{
  background-color:#333!important;
}
.toc .row
{
  border-color:#333!important;
}
.toc .row .tag
{
  background-color:#444!important;
}
.toc #tocui.pinned
{
  box-shadow:1px -2px 6px #111, 0 -12px 0 #111, 0 2px 0 -1px #111 inset, 0 -50px 30px -15px #111 inset, 1px 2px 6px #111!important;
}

}
