@namespace url(http://www.w3.org/1999/xhtml);

/* ドラクエ10　まとめブログ - 20151007 */
/* MEMO: ※ID末尾 O */
@-moz-document domain("www.dql0.com") {
  .article {/* 記事 はじまり */
    margin: 0;
  }
  .article-body {
    margin: 0;
  }
  .article-body-inner > span > div[style="text-align: right;"] {
    margin-bottom: 2em;
  }
  .t_b {
    line-height: 1.3em !important;
  }
  div[style^="font-size:18px"] {
    font-size: 1.2em !important;
  }
  .article-sub-category h3 {
    font-size: 100%;
  }
  .article-sub-category ul {
    font-size: 95%;
  }
  .comment-body {
    color: #222;
    font-size: 1.1em;
  }
  
  iframe,
  #blog-header01,
  .rss-blogroll,
  .plugin-tag_cloud,
  /* .article-body-inner > span img, */
  /* .article-body-inner > span br, */
  form[id$="_form"],
  .brp,
  .amazon,
  .main-column-inner > b,
  a[href^="http://blog.with2.net"],
  a[href^="http://game.blogmura.com"],
  a[href^="http://game-blog-ranking.com"],
  a[href^="http://www.amazon.co.jp"],
  .article-social-btn,
  a+br,
  a+br+br,
  iframe+br,
  iframe+br+br,
  img+br,
  img+br+br,
  img+br+br+br,
  img+br+br+br+br,
  img+br+br+br+br+br,
  div[id^="plugin-categorize"]+.plugin-memo,
  .plugin-tag_cloud+.plugin-memo,
  .ad01,
  .left-column-inner > :nth-of-type(1),/* ブログ内検索 */
  .left-column-inner > :nth-of-type(2),/* オススメ商品 */
  .left-column-inner > :nth-of-type(3),/* スポンサードリンク */
  .pop_cnt_list,
  .right-column-inner > :nth-of-type(1),/* スポンサードリンク */
  .right-column-inner > :nth-of-type(2),/* Twitter */
  .right-column-inner > :nth-of-type(4),/* Amazonライブリンク */
  .right-column-inner > :nth-of-type(5),
  .right-column-inner > :nth-of-type(6) {
    display: none;
  }
}

/* 超DQ10速報 - 20151013 */
@-moz-document domain("chodq10.blog.jp") { 
  /* .net ソース */
  .article-body {
    line-height: 1.55;
    margin-bottom: 0;
  }
  .article-body a {
    color: #008000;
  }
  dt[style="font-family: Times;"] {
    font-family: "ヒラギノ角ゴ Pro W3","Hiragino Kaku Gothic Pro",メイリオ,meiryo,Arial !important;
    font-size: 75%;
  }
  .article-body-inner dd {
    font-size: 90%;
    margin: 0 0 1.37em 0;
  }
  .article-sub-category h3 {
    font-size: 90%;
  }
  ul[id^="ldblog_related_articles_"] {
    font-size: 80%;
  }
  .comment-body {
    font-size: 14px;
  }
  .comment-author {
    color: #008000;
  }
  .article-sub-category .related-article-image {/* カテゴリの最新記事 */
    display: none !important;
  }
  
  /* sc ソース */
  .t_h {
    font-size: 80%;
  }
  .t_b {
    font-size: 93% !important;
    line-height: 1.3em !important;
  }
  
  /* right-container */
  .right-container {
    position: static !important;
    left: 1037px !important;
    margin-left: -210px !important;
    bottom: 271px !important;
    top: 1300px !important;
  }
  
  
  iframe,
  #blog-header01,
  .plugin-twitter_profile,
  .left-column-inner > :nth-of-type(1),/* 応援お願い致します */
  .left-column-inner > :nth-of-type(4),/* スポンサードリンク */
  .left-column-inner > :nth-of-type(6),/* ブログランキング */
  .left-column-inner > :nth-of-type(7),/* アクセスランキング */
  .left-column-inner > :nth-of-type(10),/* 著作権 */
  .right-column-inner > :nth-of-type(1),
  div[id^="popularArticlesWithImageTag"],
  form[id$="_form"],
  .rss-blogroll,
  .plugin-tag_cloud,
  .plugin-link,
  .ad-center,
  .ninja-recommend,
  dd > br,
  .t_h+br,
  .brp,
  .amazon,
  a[href^="http://blog.with2.net"],
  a[href^="http://game.blogmura.com"],
  a[href^="http://game-blog-ranking.com"],
  a[href^="http://www.amazon.co.jp"],
  .article-social-btn,
  [style="overflow: hidden;"] {
    display: none;
  }
}

/* DQ10まとめくす！ - 201410xx */
@-moz-document domain("dq10matomex.com") {
  .text_body[style="font-size:18px; font-weight: bold;"] {
    font-size: 95% !important; 
    font-weight: normal !important;
  }
  div[style="padding:5px; margin-bottom:50px;"] {
    margin-bottom: 1.3em !important;
  }
  div font[style="font-weight: bold; font-size:13px; padding-left: 5px; color: #5f9ea0;"]:nth-of-type(3),
  div font[style="font-weight: bold; font-size:13px; padding-left: 5px; color: #5f9ea0;"]:nth-of-type(4) {
    color: #808080 !important;
    font-weight: normal !important;
  }
  font[style="font-weight: bold; font-size:13px; padding-left: 5px; color: #5f9ea0;"] {
    color: #008000 !important;
  }
  font[style="font-weight: bold; font-size:13px; color:#ffa500;"] {
    color: #000 !important;
    font-weight: normal !important;
  }

  #header2,
  #blog-header01,
  .plugin-twitter_profile,
  .rtcom,
  .hdpr,
  /* br+a, *//* 画像も非表示になってしまう */
  /* a+br, */
  div[align="right"]+script+script+b,
  script+a,
  b > a,
  b > u,
  b > span,
  .menuTabLight,
  a[href^="http://blog.with2.net"],
  img[src="http://dq10matomex.com/oshirase.jpg"],
  img[src="http://dq10matomex.com/icatch.jpg"],
  img[src="http://dq10matomex.com/banner.png"],
  .right-container > :nth-of-type(1),
  .right-container > :nth-of-type(2),
  .rss-blogroll3,
  .brp {
    display: none !important;
  }
}

/* ドラクエ10攻略 - 201410xx */
@-moz-document domain("dq10x.net") {
  #blog-header01,
  #blog-header02,
  .message-board-outer,
  a[href^="http://blog.with2.net"],
  a[href^="http://game-blog-ranking.com"],
  .left-container > :nth-of-type(1),
  .article-sub-popular,
  form {
    display: none !important;
  }
}



