/* DQX相場情報 - 20141228 */
@-moz-document domain("grooowl.com") {
  h1,
  .updated,
  .fix_form,
  .warn {
    display: none !important;
  }
}
/* ドラクエ10 攻略の虎 - 20150215 */
@-moz-document domain("dragon-quest.jp") {
  body {
    font-family: Verdana,"ヒラギノ角ゴ ProN W3","Hiragino Kaku Gothic ProN","メイリオ",Meiryo,sans-serif;
  }
  #main {
    top: 0px !important;
  }
  h1,
  #header-logo,
  #menu,
  #navi {
    display: none;
  }
}
/* ドラクエ10 最新攻略情報まとめ(ゲーム攻略完全図鑑) - 20150204 */
@-moz-document domain("dopr.net") {
  #bg {
    margin: 20px auto 0;
  }

  /* iframe, */
  #gsc+div[style="margin-bottom: 5px;"],
  header,
  #pankuzu,
  .share-box,
  #review,
  #news,
  #side-vc-banner,
  #fav-review,
  #site-ranking,
  #footer {
    display: none !important;
  }
}
/* ドラクエ10・モンスター完全図鑑 - 20150128 */
@-moz-document domain("www.d-quest-10.com") {
  td[style="padding-bottom:8px; text-align:center;"] {
    display: none;
  }
}
/* モーモンバザーサーチ - 201411xx */
@-moz-document domain("rizria.com") {
  body {
    padding-top: 2em;
  } 
  .row.banner,
  .nav,
  .adbyamazon {
    display: none !important;
  }
}
