@font-face {
  font-family: 'Droid Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Droid Sans'), local('DroidSans'), url(http://fonts.gstatic.com/s/droidsans/v6/s-BiyweUPV0v-yRb-cjciBsxEYwM7FgeyaSgU71cLG0.woff) format('woff');
}


body { font-family: 'Droid Sans'; }

.rightBar{
	position:absolute;
	top: 300px;
}

.container{
	width:auto;
}   

.stream_container{
	width: 200%;
	position: relative;
	left:0px;
}

.span-18 > .stream_container{
	width: 150%;
	position: relative;
	left:0px;
}
.span-15 > .stream_container{
	width: 180%;
	position: relative;
	left:0px;
}
	 
	 
.reshare{
	background-color: #fafafa;
}

.controls + div > .author{
	background-color: cyan; 
}
.controls + div > .author:hover{
	background-color: lightblue; 
}

.reshare .author{
	background-color: lightgreen;
}

.reshare .author:hover{
	background-color: green;
}


ul#stream_selection>li{
  margin:1px;
  padding:1px;
  background-color:wheat;
}
	
ul#stream_selection>li a{
  margin:1px;
  padding:1px;
  background-color:wheat;
}  
	 
.stream_element{
	border: 2px dotted cyan;
	margin-bottom: 10px;
	border-radius: 10px;
}
}
