css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('twitter.com') {
/* Write your CSS here. */
  
  .dashboard-right, .dashboard-left, .promoted-tweet {display: none}
  #timeline {width:100% !important}
  .tweet { 
    padding: 12px 20px;border:0 !important;}
  
 .tweet .js-tweet-text {
font-size: 20px;
line-height: 1.4;
margin-top: 20px;
  margin-bottom: 20px;}
  
  .tweet-text {margin:20px !important;}
  
  .stream-item-header .avatar{
    float: left;
   
    margin-left: -87px;
    
    box-shadow: 3px 3px 5px rgba(191, 188, 188, 1);
}
  
 .tweet > .context {
   margin-left: -5px !important;}
  
  .tweet-context {
    font-size: 16px;
    margin-bottom: 18px;
    background: none repeat scroll 0% 0% #F6F4F4;
    padding: 10px;
    display: inline-block;
    border: 1px solid #CCC;
    border-radius: 5px;}
  
  .conversation-module .conversation-tweet-item .stream-item-header .avatar {
    margin-left: -61px;
    width: 50px;
    height: 50px;
    border-radius: 5px;}
  
  .stream-item, .unfocusable-stream-item {
    background: none repeat scroll 0% 0% padding-box #FFF;
  
    margin: 15px 0px;
  }
  
  .expand-stream-item {background: #008000;
color: #FFF;
  padding: 5px 7px;}
}
