css:
@-moz-document domain("godoc.org") {
body {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
    color: #fff !important;
}

/* navigation */
body nav {
    background-color: #111 !important;
    border: none !important;
}
body nav ul li.active a {
    background-color: transparent !important;
    color: #375EAB !important;
}
body nav ul li a:hover {
    color: #ccc !important;
}
body nav input {
    background-color: #333 !important;
    border-color: #375EAB !important;
    color: #eee !important;
}

/* footer */
body div#x-footer {
    background-color: #111 !important;
    border: none !important;
}
body div#x-footer a:hover {
    color: #ccc !important;
}

/* home page banner */
body div.container div.jumbotron {
    background-color: rgba(51, 51, 51, 0.75) !important;
}
body div.container div.jumbotron input {
    background-color: #333 !important;
    border-left-color: #375EAB !important;
    border-top-color: #375EAB !important;
    border-right-color: transparent !important;
    border-bottom-color: #375EAB !important;
    color: #eee !important;
}
body div.container div.jumbotron button {
    background-color: #111 !important;
    border-left-color: transparent !important;
    border-top-color: #375EAB !important;
    border-right-color: #375EAB !important;
    border-bottom-color: #375EAB !important;
    color: #375EAB !important;
}
body div.container div.jumbotron button:hover {
    background-color: #222 !important;
    border-left-color: transparent !important;
    border-top-color: #375EAB !important;
    border-right-color: #375EAB !important;
    border-bottom-color: #375EAB !important;
    color: #375EAB !important;
}

/* doc page */
body div.container div#x-projnav {
    background-color: #111 !important;
    border-radius: 6px !important;
}
body div.container h3,
body div.container h4 {
    padding: 9.5px !important;
    background-color: #111 !important;
    border-radius: 4px !important;
    border-bottom: 1px solid #ccc !important;
}
body div.container h3.highlighted,
body div.container h4.highlighted {
    background-color: rgba(210, 115, 45, 0.9) !important;
    border-radius: 5px !important;
    padding: 5px !important;
}
body div.container pre {
    background-color: #222 !important;
    color: #eee !important;
    border: 1px solid #444 !important;
}
body div.container div.panel-group div.panel {
    background-color: transparent !important;
    border: 1px solid #444 !important;
}
body div.container div.panel-group div.panel div.panel-heading {
    font-weight: bold !important;
    background-color: #111 !important;
    border: 1px solid #444 !important;
}
body div.container div.panel-group div.panel div.panel-collapse {
    background-color: #222 !important;
    border: 1px solid #444 !important;
}
}
