css:
@-moz-document domain("imgur.com") {
/*Removes conflicting fixed header in new Imgur style*/
.post-header.fixed {
    position: static;
}

/*Lightens the background slightly*/
body {
  background: #212121;
  color: #ddddd1;
  font-family: "Roboto", "Open Sans", sans-serif;
}

/* Hides annoying top bars */
#cta-container {
  display: none;
}

::selection {
  background: #673AB7;
}

/* REMOVED DUE TO NATIVE IMPLEMENTATION BY IMGUR
Green OP indicator, created by @riking 
.topic-post.topic-owner .username a:after {
    content: "OP";
    color: #85BF25;
    margin-left: 10px;
}

#post-cloak-1 .topic-post.topic-owner .username a:after {
    content: "";
}
*/

/*Some Community Elements*/
#topic-title h1 a {
  color: #ddddd1;
}

.ember-view .reply {
  color: #212121;
}

.quote {
  color: #212121;
}

.d-header {
  background: #311B92;
}

.nav-pills li a {
  color: #ddddd1;
}
    
.nav-pills li a:hover {
  background: #311B92;
  color: #ddddd1;
}

blockquote {
  color: #212121;
}

/*Main Imgur Stuff*/
#secondary-nav .notification-container {
  background: #85BF25;
  border-color: #334A0E;
  transition: background 0.25s;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#content, #inside {
  margin-top: 56px;
  font-family: "Roboto", "Open Sans", sans-serif;
}

a:link, a:active, a:visited, a strong {
  color: #673AB7;
  text-decoration: none;
  font-family: "Roboto", "Open Sans", sans-serif;
}

a:link:hover, a:active:hover, a:visited:hover, a strong:hover {
  color: #85BF25;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#topbar {
  background: #311B92;
  position: fixed;
  box-shadow: 0px 5px 8px rgba(0,0,0,0.50);
  font-family: "Roboto", "Open Sans", sans-serif;
}

#topbar .upload-button.blue-cta a, #topbar .upload-button a:hover {
  background: linear-gradient(#673AB7, #311B92);
  box-shadow: 0px 5px 10px rgba(0,0,0,0.50);
  transition: background 0.25s, box-shadow 0.25s;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#topbar .link-menu a {
  background: #311B92;
  color: #E0E0E0;
  transition: background 0.25s;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#topbar .link-menu a:hover {
  background: #673AB7;
  color: #FFFFFF;
  box-shadow: 0px 5px 10px rgba(0,0,0,0.50);
  font-family: "Roboto", "Open Sans", sans-serif;
}

#header-logo a.logo:hover {
  background: #673AB7;
  box-shadow: 0px 5px 10px rgba(0,0,0,0.50);
  transition: background 0.25s, box-shadow 0.25s;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#main-nav .menu-btn:hover, #main-nav .menu-btn.opened {
  background: #673AB7;
  box-shadow: 0px 5px 10px rgba(0,0,0,0.50);
  transition: background 0.25s, box-shadow 0.25s;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#topbar .meme-icon a:hover, #topbar .random-button a:hover {
  background: #673AB7;
  box-shadow: 0px 5px 10px rgba(0,0,0,0.50);
  transition: background 0.25s, box-shadow 0.25s;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#secondary-nav .user-dropdown {
  background: #311B92;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#secondary-nav .user-dropdown a.active {
  background: #85BF25;
  color: #FFFFFF;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#secondary-nav .user-dropdown a.active:hover {
  background: #673AB7;
  color: #FFFFFF;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#secondary-nav .user-nav a:hover, #secondary-nav .user-nav.selected a {
  background: #673AB7;
  color: #FFFFFF;
  box-shadow: 0px 5px 10px rgba(0,0,0,0.50);
  transition: background 0.25s, box-shadow 0.25s, color 0.25s;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#global-search-container .search-container:hover {
  background: #673AB7;
  box-shadow: 0px 5px 10px rgba(0,0,0,0.50);
  transition: background 0.25s, box-shadow 0.25s;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#image-title {
  color: #7E57C2;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#fullbleed-bg {
  display: none;
}

/*Changes the color of new replies*/
#captions .caption .usertext.contexted {
  background: #311B92;
  border-color: #673AB7;
}

#captions .caption .usertext.contexted a:link {
  color: #85BF25;
}

/*Changes the color of the messages*/
#conversations-list .conversation:hover {
  background: #311B92;
  border-color: #673AB7;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#conversations-list .conversation.viewing {
  background: #85BF25;
  border-color: #4E6F16;
  font-family: "Roboto", "Open Sans", sans-serif;
}

/*Changes the color of the Nav Next button*/
#right-content .navNext {
  background: linear-gradient(#673AB7, #311B92);
  font-family: "Roboto", "Open Sans", sans-serif;
}

/*Changes the color of the downvotes to purple to match the overall color scheme*/
#under-image .negative {
  background: #673AB7;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#info-analytics .part-one {
  background: #673AB7;
  font-family: "Roboto", "Open Sans", sans-serif;
}

#info-analytics .red {
  color: #7E57C2!important;
  font-family: "Roboto", "Open Sans", sans-serif;
}

/*Changes the color and transparency of the images in gallery view*/
#imagelist .post {
  opacity: 0.90;
  transition: border-color 0.25s;
}

#imagelist .post:hover {
  opacity: 1.0;
  border-color: #311B92;
  box-shadow: 0px 5px 10px rgba(0,0,0,0.50);
}

#imagelist .gradient-transparent-black {
  background: linear-gradient(rgba(0,0,0,0),#311B92);
  opacity: 0;
  transition: opacity 0.25s;
}

#imagelist .post:hover .transition {
  opacity: 1;
}

/*Hides the social panel*/
.social {
  display: none;
}
}
