css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com") {
/* Pseudo-anti-aliasing Removal */

/* Hidden Items */
#upgrade-now-button, #navigation .rbx-text-notes, .rbx-nav-sponsor, .ad-annotations, .other, .rbx-nav-notification, #Footer {
	display: none !important;
}

/* Borders */
#tab-outfits, #tab-wardrobe, #FriendsTab, #BestFriendsTab, #FriendRequestsTab, #GroupPlacesContainer, .tab-content-container {
	border-left: 1px solid rgb(200, 200, 200) !important;
	border-bottom: 1px solid rgb(200, 200, 200) !important;
	border-right: 1px solid rgb(200, 200, 200) !important;
}

/* Navigation and Header */
#header {
	box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.3) !important;
}

#navigation {
	z-index: 1031 !important;
}

/* Pseudo-elements */
::selection {
	background: rgba(145, 180, 255, 0.4);
}

::-moz-selection {
	background: rgba(145, 180, 255, 0.4);
}

/* Ratings */
.votes, .percent {
	border-radius: 3px !important;
}

/* Body Wrap Positioning and Recolor */
#ResponsiveWrapper, .nav-content-inner, body, html, .Avatar, .Post, .StandardPanelWhite , #Body, .UserBlurb, .accord-section-open, #UserPlaces {
	background-color: rgb(250, 250, 250) !important;
	background-image: none !important;
}

.nav-content-inner {
	position: absolute !important;
	left: 20%;
}

#ResponsiveWrapper {
	position: relative !important;
	left: 0px;
}

/* Forums */
#ctl00_cphRoblox_ThreadView1_ctl00_ThreadList .normalTextSmaller, .forum-post .normalTextSmaller, #ctl00_cphRoblox_MyForums1_ctl00_ParticipatedThreads .normalTextSmaller {
	color: rgb(100, 100, 100) !important;
}

.thread-link-container {
	color: rgb(80, 80, 80) !important;
}

.forum-post .normalTextSmall {
	color: rgb(30, 30, 30) !important;
}

.forum-table-row {
	transition: all 0.2s ease !important;
	-ms-transition: all 0.2s ease !important;
	-moz-transition: all 0.2s ease !important;
	-webkit-transition: all 0.2s ease !important;
}

.forum-table-row:hover {
	background-color: rgb(230, 230, 230) !important;
	border-radius: 5px !important;
}

#forum-nav .menuTextLink {
	border-left: 1px solid rgb(200, 200, 200) !important;
	color: #2E8BE8 !important;
	text-decoration: none !important;
}

#forum-nav .menuTextLink:hover {
	color: #4A9DF0 !important;
	text-decoration: none !important;
}

#forum-nav .menuTextLink.first {
	border: none !important;
}

.linkMenuSink {
	color: #2E8BE8 !important;
	text-decoration: none !important;
}

.linkMenuSink:hover {
	color: #4A9DF0 !important;
	text-decoration: none !important;
}

.forum-table-header, .forum-table-footer {
	border: none !important;
	background-color: #5C88D1 !important;
}

.forum-table-header th {
	background-color: #5C88D1 !important;
}

/*Buttons */
.see-all-button, .btn-control, .btn-small, .btn-medium {
	color: white !important;
	padding-left: 10px !important;
	padding-right: 10px !important;
	border-radius: 0px !important;
	background-image: none !important;
	background-color: rgb(0, 162, 255) !important;
	border:none !important;
	transition: all 0.2s ease !important;
	-ms-transition: all 0.2s ease !important;
	-moz-transition: all 0.2s ease !important;
	-webkit-transition: all 0.2s ease !important;
}

.see-all-button:hover, .btn-control:hover, .btn-small:hover, .btn-medium:hover {
	background-color: rgb(50, 181, 255) !important;
	box-shadow: 0px 2px 2px rgb(230, 230, 230) !important;
}

.btn-primary {
	color: white !important;
	padding-left: 10px !important;
	padding-right: 10px !important;
	border-radius: 0px !important;
	background-image: none !important;
	background-color: #00BA19 !important;
	border: none !important;
	transition: all 0.2s ease !important;
	-ms-transition: all 0.2s ease !important;
	-moz-transition: all 0.2s ease !important;
	-webkit-transition: all 0.2s ease !important;
}

.btn-primary:hover {
	background-image: none !important;
	background-color: #00D11C !important;
	box-shadow: 0px 2px 2px rgb(230, 230, 230) !important;
}

/*** SPECIAL CASE: MERELY'S GROUP ENHANCER ***/
#blacklist-group {
	font-size: 12px;
	height: 21px;
	line-height: 21px;
	color: white !important;
	padding-left: 10px !important;
	padding-right: 10px !important;
	border-radius: 0px !important;
	background-color: rgb(0, 162, 255) !important;
	background-image:none !important;
	border:none !important;
	transition: background-color 0.2s ease !important;
	-ms-transition: background-color 0.2s ease !important;
	-moz-transition: background-color 0.2s ease !important;
	-webkit-transition: background-color 0.2s ease !important;
}

#blacklist-group:hover {
	background-color: rgb(50, 181, 255) !important;
	box-shadow: 0px 2px 2px rgb(230, 230, 230) !important;
}

/* Group Page Tweaks */
.GroupListItemContainer  {
	transition: background-color 0.2s ease !important;
	-ms-transition: background-color 0.2s ease !important;
	-moz-transition: background-color 0.2s ease !important;
	-webkit-transition: background-color 0.2s ease !important;
}

.GroupListItemContainer:hover {
	background-color: rgb(230, 230, 230) !important;
}

/* Place List */
.accord-header {
	background-image: none !important;
	background-color: rgb(245, 245, 245) !important;
	border-radius: 0px !important;
	transition: all 0.2s ease !important;
	-ms-transition: all 0.2s ease !important;
	-moz-transition: all 0.2s ease !important;
	-webkit-transition: all 0.2s ease !important;
}

.accord-header:hover {
	background-color: rgb(230, 230, 230) !important;
}

/* Game Page */
.details span, .allowed-gear {
	color: rgb(100, 100, 100) !important;
}

.place-description-content {
	color: rgb(30, 30, 30) !important;
}

#SocialMediaBarTarget {
	display: none !important;
}
}
