@namespace url(http://www.w3.org/1999/xhtml);

/*
 * La Multi Ani TMD!
 * By user SYNTHEMA
*/

@-moz-document regexp("https?://(www\\.)?torrentsm((d\\.(com|eu|me))|(oldova\\.(com|org|net))).*") {

	/* visited links */
	.tableTorrents td[align="left"] a:visited {
		color: #10a68c !important;
	}

	/* resize big images */
	.imgforumcontainer img {
		max-width: 690px;
	}

	body {
		background: #f5f4ea url("http://clip2net.com/clip/m269243/f3350-body-17kb.jpg") repeat-x 50% 0 !important;
	}

	#no_td_border > #top_menu {
		background: #fff url("http://clip2net.com/clip/m269243/04eb9-header-17kb.jpg") no-repeat 44% 0;
		min-height: 85px;
	}

	#top_menu > table > tbody > tr > td:first-child a {
		background-image: url("http://clip2net.com/clip/m269243/091fb-logo-8kb.png");
		width: 176px;
		height: 66px;
		text-indent: -999px;
		overflow: hidden;
		display: block;
		margin-top: 7px;
	}
	
	#menu.anon .torrents {
		background: transparent;
	}

	#main_menu {
		padding: 10px 0;
		line-height: 27px;
	}
	  #menu img,
		#menutable img {
			display: none;
		}

	  #menu a,
		#menutable a {
			display: block;
			float: left;
			border-right: 1px solid #a7b8cc;
			padding-right: 8px;
			margin-right: 8px;
		}
		#menu a:last-child
		#menutable a:last-child {
			border-right: none;
			padding-right: 0;
			margin-right: 0;
		}

	.mCenter.categories {
		padding: 20px 0 40px;
	}

	#newslisttd,
	.mCenter.categories {
		background: url("http://clip2net.com/clip/m269243/3c165-mos_and_co-23kb.png") no-repeat 100% 100%;
	}

	#newslisttd,
	.mCenter.categories,
	.pageContainer > table[width="880"]:not(.main),
	#forumPosts_first {
		position: relative !important;
	}

	#newslisttd:after,
	#newslisttd:before,
	.mCenter.categories:after,
	.mCenter.categories:before,
	.pageContainer > table[width="880"]:not(.main):after,
	.pageContainer > table[width="880"]:not(.main):before,
	#forumPosts_first:after,
	#forumPosts_first:before {
		content: "";
		display: block;
		position: absolute;
	}

	#newslisttd:after,
	.mCenter.categories:after {
		background-image: url("http://clip2net.com/clip/m269243/bbc65-cerb_1-4kb.png");
		width: 48px;
		height: 55px;
		left: -19px;
		bottom: -12px;
	}

	#newslisttd:before,
	.mCenter.categories:before {
		background-image: url("http://clip2net.com/clip/m269243/67f38-cerb_2-3kb.png");
		width: 33px;
		height: 38px;
		top: 7px;
		right: -13px;
	}

	.pageContainer > table[width="880"]:not(.main):after,
	#forumPosts_first:after {
		background-image: url("http://clip2net.com/clip/m269243/be93d-jucarie_1-14kb.png");
		width: 65px;
		height: 294px;
		top: 100px;
		left: -44px;
	}
	.pageContainer > table[width="880"]:not(.main):before,
	#forumPosts_first:before {
		background-image: url("http://clip2net.com/clip/m269243/7fe60-jucarie_2-7kb.png");
		width: 51px;
		height: 207px;
		top: 79px;
		right: -36px;
	}
}
