css:
@-moz-document url-prefix('https://huboard.com') {

  /*************************************************************
    Colum reStyle
  *************************************************************/

  .main-content.sidebar-open > .sidebar { flex: 0 0 230px; }

  .main-content > .sidebar {
    transition: all 0.4s ease-out 0s;
    flex: none;
  }

  .column {
    background-color: #F3F3F3; /*F3F3F3*/
    padding: 0px 15px;
     border: 1px solid #E5E5E5;
    margin: 0 7px
  }

  .column.no-milestone  {
    padding: 0px 15px;
  }

  .column > .collapsed{ border: none; }

  .column h3 {
    border: none;
    border-bottom:  2px dashed #E5E5E5;
    margin-right: -15px;
    margin-left: -15px;
  }

  .column h3:after,
  .column h3:before{ display: none; }

  .column .ui-sortable-placeholder{
    background: rgba(204, 204, 204, 0.3);
    border: 2px dashed rgba(204, 204, 204, 1);
  }

  .column li {
    border-radius: 3px;
  }

  .column > h3 span,
  .column > h3 {
    transition: all 0.4s ease-out;
  }

  /*************************************************************
    Colum Collapsed
  *************************************************************/

  .column.hb-state-collapsed > h3 span,
  .column.hb-state-collapsed > h3 {
    display: block;
    text-align: center;
  }

  .column.hb-state-collapsed .sortable li > div {
    display: block;
  }

  .column.hb-state-collapsed .sortable li > div  {
    margin-top: 45px !important;
  }
  .column.hb-state-collapsed .sortable li:first-child > div  {
    margin-top: 20px !important;
  }

  .column.hb-state-collapsed .sortable li > div > * { display: none !important; }

  .column.hb-state-collapsed .sortable li > div > .card-header { display: block !important; border: none; }

  .column.hb-state-collapsed .sortable li > div > .card-header .title {
    display: none !important;
    position: relative;
  }

  .column.hb-state-collapsed .sortable li > div > .card-header .number > * {
    position: absolute;
  }

  .column.hb-state-collapsed .sortable li > div > .card-header .number > a {
    margin-left: 25px !important;
  }
  .column.hb-state-collapsed .sortable li > div > .card-header .number > small {
    padding-left: 10px;
  }

}
