css:
/* AGENT_SHEET */ 

@-moz-document url-prefix(chrome:) 
    
{#tabscope-container{background: none!important;}
    
#tabscope-title {font:.95em/1.2em "Trans.Text"!important;}
    
#tabscope-popup {
padding-top:20px!important;
   /*padding-right:170px!important; padding-left:170px!important;*/
padding-right:28px!important;
opacity: 0.95 !important;

}
#tabscope-preview {
     
 border: dotted 3px !important;  
 border-color: moccasin !important;    
 margin-bottom:-14px !important; 
 background:black!important;
 opacity: 0.95 !important;
box-shadow:0 1px 4px black!important;      
border-radius: 4px;  }    
}

#tabscope-toolbar toolbarbutton { 
background-color: rgba(4, 87, 164, .45)!important;
border-radius: 4px 4px 2px 2px!important;
border: solid 0.1em lightblue  !important;
border-top-color: lightblue !important;
border-right-color: lightblue   !important;
border-bottom-color: transparent  !important;
box-shadow:  
0 0 2px moccasin inset, -2px -1px 3px lightblue inset,
0px 3px 2px rgba(255,220,255,.4) !important;  
min-height: 24px !important;
max-height: 24px !important;
min-width: 24px !important;
max-width: 24px !important;
-moz-transform: scale(1.0) !important;
image-rendering: auto !important;
vertical-align:middle !important;
padding-bottom: 4px !important;
margin-right: 10px !important;
margin-left: 10px !important;
}

#tabscope-toolbar toolbarbutton:hover { 
border-radius: 4px 4px 2px 2px!important;
border: solid 0.1em whitesmoke  !important;
border-top-color: moccasin  !important;
border-right-color: moccasin    !important;
border-bottom-color: transparent  !important;
-moz-transform: scale(1.40) !important;
image-rendering: auto !important;
vertical-align:middle !important;
padding-bottom: 6px !important;
margin-right: 10px !important;
margin-left: 10px !important;
}

#tabscope-toolbar {padding-right:250px!important; padding-left:300px!important;
height: 24px !important;
border-width: 0 2px 0 0px !important; 
border-left:solid 0.25em rgba(108,146,174, 0.8)!important;
border-right:solid 0.25em rgba(108,146,174, 0.8)!important;
border-bottom: solid 0.2em rgba(108,146,174, 0.9) !important; 
box-shadow: lightblue 0px -2px 2px 1px!important;  
background: rgba(207,219,236,0.73) !important;
}
