css:
@-moz-document domain("wikipedia.org") {
	#firstHeading {
        font-family: 'Helvetica Neue','Lucida Grande',Verdana,Arial,Helvetica,sans-serif !important;
        width: 900px !important;
        margin-left: auto !important;
        margin-right: auto !important;
        padding-top: 0.25em !important;
        padding-bottom:0.25em !important;
        border: none !important;
        font-weight: bold !important;
    }

    #bodyContent {
        font-family: 'Helvetica Neue','Lucida Grande',Verdana,Arial,Helvetica,sans-serif important;
        font-size: 1.09em !important;
        line-height: 1.55em !important;
        border: 1px solid #ccc !important;
        width: 900px !important;
        padding: 50px !important;
        margin-left: auto !important;
        margin-right: auto !important;
        margin-top: 1em !important;
        background: #fbfbfb !important;
    }
    
    #mw-articlefeedback {
        font-family: sans-serif !important;
        font-size: 0.994em !important;
    }

    /* Hover to reveal */
    #mw-head, #mw-panel {
        opacity: 0;
        -moz-transition: all 400ms ease-in-out !important;
        -webkit-transition: all 400ms ease-in-out !important;
    }

    #mw-head:hover, #mw-panel:hover {
        opacity: 1;
    }

    .infobox,
    .tright, .tleft, div.tright, div.tleft,
    .taxobox, table.taxobox,
    .wikitable {
        margin-top: 0;
        margin-bottom: 0.5em;
    }

    .ambox, table.ambox {
        margin: 1.5em 10%;
    }

    pre {
        overflow: auto;
    }
	/* titoli sezioni */
	.mw-body h1, .mw-body h2, .mw-body 
  	#firstHeading {
	font-family: 'Helvetica Neue','Lucida Grande',Verdana,Arial,Helvetica,sans-serif;
  	font-weight: bold !important;
	line-height: 1.3;
	margin-bottom: 0.25em;
	padding: 0;
	}
	/* gestione grassetto */
	strong, b {
	font-weight: bold !important;
  	font-family: 'Helvetica Neue','Lucida Grande',Verdana,Arial,Helvetica,sans-serif;
	}
	/* contents menu */
	 .mw-body #toc h2, .mw-body .toc h2 {
	font-size: 100%;
	font-family: 'Helvetica Neue','Lucida Grande',Verdana,Arial,Helvetica,sans-serif !important;
	}


	/* altro */


	.mw-body h3, .mw-body h4 {
	font-weight: bold;
  	font-family: 'Helvetica Neue','Lucida Grande',Verdana,Arial,Helvetica,sans-serif !important;
	}
  
  	table.wikitable {
	margin: 1em 0;
	background-color: #f9f9f9;
	border: 1px #aaa solid;
	border-collapse: collapse;
    font-family: 'Helvetica Neue','Lucida Grande',Verdana,Arial,Helvetica,sans-serif !important;
	color: black;
	}
  
  	th.fn{
    font-family: 'Helvetica Neue','Lucida Grande',Verdana,Arial,Helvetica,sans-serif !important;
  	}
  	th{
    font-family: 'Helvetica Neue','Lucida Grande',Verdana,Arial,Helvetica,sans-serif !important;
  	}
}
