css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Written by Zachary Gosse*/

@-moz-document domain("slate.sheridancollege.ca") {
  .td{
    color:aliceblue;
  }
  .d2l-body, body{
      background: url(http://s18.postimg.org/bokznk22v/wpid_Circuit_Wallpaper_13.jpg); 
   background: url(http://s30.postimg.org/91pjb8qhr/slatebg_Filtered.png);
    background-position: 00px 100px;
    background-size: 1920px 1080px;
  }
  
  
  .d2l-background-global{
    visibility:hidden;
  }
  
  /*Background Left and Right*/
  .d2l-background-left,.d2l-background-right{
     visibility:hidden;
  }
  
  .d2l-background-right{
    background-position: top right;
     background-position: -225px 30px;
  }
  
  /*Top-most nav-bar*/
  .d2l_1_14_513,.d2l_1_29_583,.d2l-minibar {
    background: #202020;
  }
  
  /*Main nav-bar*/
  #d2l_navbar div.d_nb_cFull{
    background: #222222;
  }
  #d2l_navbar div.d_nb_cFull {
    border-bottom:4px;
  }
  /*Main window*/
  .d2l-page-main, .d2l-page-bg > div{
 
    background: rgba(40,40,40, 0.7);
    color: blue;
  }
  .d2l-msg-container{
    background-color: transparent;
    border:0;
    color:darkgrey;
  }
  
  .d2l-msg-container-inner {
    background-color: #713C3C;
    color:darkgrey;
    border:0;
  }
  span{
    color:rgb(94, 187, 239);
  }
  .MsoNormalTable{
    color: rgb(94, 187, 239);
  }
  
  /*Title font*/
  .d2l-homepage-title .d2l-page-title {
    color: rgba(75, 148, 231, 1);
}
  
  /* unvisited link */
  a:link {
    color: rgba(0, 136, 255, 1);
  }

/* visited link */
  a:visited {
    color: rgba(0, 106, 225, 1);
  }

/* mouse over link */
  a:hover {
    color: #FF00FF;
  }

/* selected link */
  a:active {
    color: #0000FF;
  }
  
  /*Navbar Link Colours*/
  #d2l_navbar div.d_nb_pMainLinks a.d2l-navbar-link span, #d2l_navbar     div.d_nb_pMainLinks a.d2l-navbar-link:visited span, #d2l_navbar div.d_nb_pMainLinks span.d2l-navbar-text span {
    font-size: 1.2em;
    color: rgba(0, 136, 255, 1);
    font-weight: normal;
    text-transform: none;
    font-style: normal;
    text-decoration: none;
  }
  
  /*Page Title Colour*/
  .d2l-page-title {
    color: rgba(197, 119, 196, 1);
  }
  
  /*Content Panel*/
  .d2l-twopanelselector-wrapper {
    background-color: rgba(88, 80, 80, 0.4);
    background: -moz-linear-gradient(center top , 
          rgba(101, 57, 45, 0.54) 0%, rgba(207, 91, 91, 0) 100%) 
         repeat scroll 0% 0% transparent;
  }
  
  /*Contel Panel Items*/
  .d2l-le-TreeAccordionItem-wrapper {
    background: linear-gradient(to bottom, rgba(45, 45, 45, 0.97) 0%,     rgba(59, 57, 57, 1) 100%) repeat scroll 0% 0% transparent;
    border-left: 0px solid rgba(57, 15, 57, 1);
    border-right: 0px solid rgba(57, 15, 57, 1);
    border-top:0;
    border-bottom:0;
    
  
    color: #905050;
  }
/*Table of Contents*/
  
  .d2l-le-TreeAccordion {
    border-top: 0px solid rgba(78, 75, 75, 1);
    
  }
   #D2L_LE_Content_TreeBrowser_TOC{
   margin-top: 25px;
  border-top: 1px solid #6C5C5C;
     
   }
/*Content Item hover*/
  #D2L_LE_Content_TreeBrowser :hover{  
	   background: -moz-linear-gradient(center top , 
         rgba(55, 45, 45, 0.54) 0%, rgba(20, 20, 20, 0) 100%) 
         repeat scroll 0% 0% transparent;
	   color: #905050;
  }
  #D2L_LE_Content_TreeBrowser :active{  
	  background:rgba(158, 95, 95, 1);
  }
/*PROGRESS BAR*/
  .d2l-progresschart{
    fill:#454545 !important;
    height:0;
    visibility:hidden;
  }
  .d2l-le-TreeAccordionItem-Selected > a > div {
    background-color:black;
    background: -moz-linear-gradient(center top , 
         rgba(101, 57, 45, 0.54) 0%, rgba(150, 91, 91, 1) 100%) 
         repeat scroll 0% 0% transparent;
    border-bottom:0;
}
  
  .body{
    background-color: rgb(140, 241, 251);
  }
  
  #d2l_navbar img.d2l-navbar-image-right{
    /*visibility: hidden;
    /background-color: rgb(140, 241, 251);*/

   content:url("http://m.c.lnkd.licdn.com/media/p/1/005/029/2ae/097e821.png");
  }
  div.d2l-box.d2l-box-h.d2l_1_49_396.d2l_1_48_747{
    background-image:url("http://m.c.lnkd.licdn.com/media/p/1/005/029/2ae/097e821.png");
    background-color: rgb(140, 241, 251);
    background-color: #8CF1FB;
  }
  
/*Widget headers*/
  .d2l-widget-header{
    background: -moz-linear-gradient(center top , 
         rgba(101, 57, 45, 0.54) 0%, rgba(207, 91, 91, 0) 100%) 
         repeat scroll 0% 0% transparent;
     border: 0px solid rgba(0, 0, 0, 0);
  }
    #d2l_1_117_706, #d2l_1_135_797{
       background: -moz-linear-gradient(center top , 
         rgba(101, 57, 45, 0.64) 0%, rgba(207, 91, 91, 0) 100%) 
         repeat scroll 0% 0% transparent;
    }

 /* table tr:first-child td{
  background-color: rgb(140, 241, 251);
  }*/
  
  /*News Window*/
  .d2l-widget-content{
    color: rgba(140, 140, 180, 1) !important;
    background: -moz-linear-gradient(center top , 
         rgba(101, 57, 45, 0.34) 10%, rgba(207, 91, 91, 0)100%) 
         repeat scroll 0% 0% transparent;border-width: 0px 1px 1px;
    border-style: solid dotted dotted;
    border-color: transparent rgba(0, 0, 0, 0) rgba(218, 219, 211, 0);
  } 
  p span, tbody td{
    color:rgba(150, 140, 170, 1) !important;
  }
  tbody td{
    background: -moz-linear-gradient(center top , 
         rgba(101, 57, 45, 0.34) 10%, rgba(207, 91, 91, 0)100%) 
         repeat scroll 0% 0% transparent !important;
  }
  tbody tr:nth-child(1) td{
    background: -moz-linear-gradient(center top , 
         rgba(50, 40, 45, 0.80) 10%, rgba(207, 91, 91, 0)100%) 
         repeat scroll 0% 0% transparent !important;
      color:rgba(180, 140, 150, 1) !important;
  }
  /*Turnitin window*/
  .d2l-widget-content.d2l_1_82_48{
    background: -moz-linear-gradient(center top , 
          rgba(101, 57, 45, 0.54) 0%, rgba(207, 91, 91, 0) 100%) 
         repeat scroll 0% 0% transparent;
  }
/*Pane Headers*/
  .d2l-collapsepane-header{
    background-color:#252525;
  }
  .d2l-heading,.d2l-heading.vui-heading-3{
    color:#FFFAFF;
  }
  
  /*Teaching Online banner*/
 div[id*='d2l_1_6_']{
   visibility:hidden;
   height:0;
 }
  
/*DROPBOX*/
  /*TABLE*/
  #z_b td{
    border: 0px inset red;
  }
  #z_b th{
    border:1px solid black;
  }
  /*label row*/
  table.d_gd tr th.d_hch {
    border: 0px;
    background-color: #292929;
    font-weight: bold;
    color: #707070;
  }
  /*Header table row*/
  table.d_gd tr.d_ggl2 {
    background-color: #303030;
  }
  table.d_gd tr {
    background-color: rgba(45, 41, 41, 5);
  }
  #d_content_inner{
     background: -moz-linear-gradient(center top , 
          rgba(80, 57, 45, 0.34) 0%, rgba(188, 91, 91, 0) 85%) 
         repeat scroll 0% 0% transparent !important;
  }

}
