css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("exilebro.com") {
  body{
    color: rgba(234, 255, 254, 1);
    
  }
  
  .panel
  {
    background-color: transparent;
  }
  
  .panel-footer{
    background-color: transparent;
  }
  
  .page-head{
    background: none repeat scroll 0% 0% rgba(0, 38, 35, 1);
  }

  .page-header{
    background-color: #002623;
  }
  .page-content{
    background: none repeat scroll 0% 0% #333B44;
  }
  .portlet.light{
    background-color: rgba(5, 44, 44, 1);
  }
  .alert-info{
    background-color: #051A24;
  }
  .grey-steel.btn{
    background-color: #051A24;
  }
  .grey-cascade.btn{
    background-color: rgba(35, 170, 170, 1);
  }
  #extable td{
    background-color: #002623 !important
  }
  div.popover-content
  {
    background-color: #002623 !important
  }
  .table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th{
    background-color: rgba(65, 85, 92, 1);
  }
}
